INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Alfatral 5mg s.r. 14 f.c.tab.', 13724, 'الفاترال 5مجم اس ار 14 قرص', '20', NULL, 'Antihypertensive.selective alpha 1 receptor blockers', 'Antihypertensive.selective alpha 1 receptor blockers', NULL, 'Alfuzosin', 'Amriya', 'Tab', 'أقراص', '5mg', '2', 'علاج لمرض ارتفاع ضغط الدم.', NULL, 'description alfuzosin (inn provided as the hydrochloride salt) is an alpha-adrenergic blocker used to treat benign prostatic hyperplasia indication : for the reduction of urinary obstruction and relief of associated manifestations (eg. sensation of incomp', NULL, 1012, '2022-05-19', NULL, 0, 'Alfuzosin is used to treat the signs and symptoms of benign prostatic hyperplasia (BPH).[L9251] ', 'Alpha(1)-adrenoreceptors  are found in the prostate, bladder base, bladder neck, prostatic capsule, and prostatic urethra; their activation may lead to contraction of smooth muscle and urinary symptoms in patients with BPH.[A180688,L9251] Alfuzosin selectively binds to and inhibits alpha(1)-adrenergic receptors in the lower urinary tract.[A518] This leads to the relaxation of smooth muscle in both the prostate and bladder neck, resulting in the improvement in urine flow and a reduction of urinary symptoms.[L9251]', 'By selectively inhibiting alpha adrenergic receptors in the lower urinary tract, alfuzosin causes smooth muscle relaxation in the bladder neck and prostate, improving urine flow, thereby reducing BPH symptoms.[L9251] Additionally, alfuzosin reduces the vasoconstrictor effect of catecholamines (epinephrine and norepinephrine), leading to peripheral vasodilation.[L31618] This leads to a risk of postural hypotension/syncope, and prescribing information warns that caution should be exercised in patients who take nitrates, antihypertensives, or have experienced decreased blood pressure after using other medications.[L9251]', 'DrugBank');
