INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (9055, 'Novirus 200mg 8 caps.', 'نوفيرس 200مجم 8 كبسولات', '13', NULL, 'Antiviral.nucleoside', 'Acyclovir', 'Glaxo smithkline', 'Cap', 'كبسولة', '200mg', '1', NULL, NULL, '6221045000433', NULL, 1120, '2023-04-11', 0, 'An acyclovir topical cream is indicated to treat recurrent herpes labialis in immunocompetent patients 12 years and older.[L7303] Acyclovir oral tablets, capsules, and suspensions are indicated to treat herpes zoster, genital herpes, and chickenpox.[L7315] An acyclovir topical ointment is indicated to treat initial genital herpes and limited non-life-threatening mucocutaneous herpes simplex in immunocompromised patients.[L7318] An acyclovir cream with hydrocortisone is indicated to treat recurrent herpes labialis, and shortening lesion healing time in patients 6 years and older.[L7321] An acyclovir buccal tablet is indicated for the treatment of recurrent herpes labialis.[L7324] An acyclovir ophthalmic ointment is indicated to treat acute herpetic keratitis.[L7327]', 'Acyclovir is becomes acyclovir monophosphate due to the action of viral thymidine kinase.[A180757] Acyclovir monophosphate is converted to the diphosphate form by guanylate kinase.[A903] Acyclovir diphosphate is converted to acyclovir triphosphate by nucleoside diphosphate kinase, pyruvate kinase, creatine kinase, phosphoglycerate kinase, succinyl-CoA synthetase, phosphoenolpyruvate carboxykinase and adenylosuccinate synthetase.[A903,A180781] Acyclovir triphosphate has higher affinity for viral DNA polymerase than cellular DNA polymerase and incorporates into the DNA where the missing 2'' and 3'' carbons causes DNA chain termination.[A180757] In other cases acyclovir triphosphate competes so strongly for viral DNA polymerase that other bases cannot associate with the enzyme, inactivating it.[A180757]', 'Acyclovir is a deoxynucleoside analog that inhibits the action of viral DNA polymerase and DNA replication of different herpesvirus.[L7303,L7315,L7318,L7321,L7324,L7327] Acyclovir has a wide therapeutic window as overdose is rare in otherwise healthy patients.[L7315]', 'DrugBank', 0, 0, 0);
