INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (9177, 'Oframax 1 gm i.v. vial', 'اوفراماكس 1جم فيال للحقن الوريدي', '70', '47.5', 'Antibiotic.cephalosporin.third-generation', 'Ceftriaxone', 'Cid > ranbaxy', 'Vial', 'فيال', '1 gm', '1', NULL, 'about ceftriaxone third generation cephalosporin antibiotic(parenteral). mechanism of action of ceftriaxone ceftriaxone is a 3rd generation cephalosporin which exerts its bactericidal action against both gram positive & gram negative organisms by inhibiti', NULL, NULL, 1266, '2024-07-29', 0, 'Ceftriaxone is used for the treatment of the infections (respiratory, skin, soft tissue, UTI, ENT) caused by susceptible organisms.[L15082] Organisms that are generally susceptible to ceftriaxone include S. pneumoniae, S. pyogenes (group A beta-hemolytic streptococci), coagulase-negative staphylococci, Some Enterobacter spp, H. influenzae, N. gonorrhoeae, P. mirabilis, E. coli, Klebsiella spp, M. catarrhalis, B. burgdorferi, and some oral anaerobes.[A215582] ', 'Ceftriaxone works by inhibiting the mucopeptide synthesis in the bacterial cell wall.[T324,L15082] The beta-lactam moiety of ceftriaxone binds to carboxypeptidases, endopeptidases, and transpeptidases in the bacterial cytoplasmic membrane. These enzymes are involved in cell-wall synthesis and cell division. Binding of ceftriaxone to these enzymes causes the enzyme to lose activity; therefore, the bacteria produce defective cell walls, causing cell death.', 'Ceftriaxone is a cephalosporin/cephamycin beta-lactam antibiotic used in the treatment of bacterial infections caused by susceptible, usually gram-positive, organisms.[L15082] Ceftriaxone has <i>in vitro</i> activity against gram-positive aerobic, gram-negative aerobic, and anaerobic bacteria.[L15087] The bactericidal activity of ceftriaxone results from the inhibition of cell wall synthesis and is mediated through ceftriaxone binding to penicillin-binding proteins (PBPs).[T324] Ceftriaxone is stable against hydrolysis by a variety of beta-lactamases, including penicillinases, and cephalosporinases and extended-spectrum beta-lactamases.[L15082] However, resistance to ceftriaxone usually occurs through beta-lactamase hydrolysis, altered PBPs, or reduced bacterial cell permeability.[T324] Ceftriaxone should not be mixed with or giving in the same IV line as diluents/products containing calcium as they may cause ceftriaxone to precipitate.[L15082] Ceftriaxone use may also cause biliary sludge or gallbladder pseudolithiasis.[L15082,A215602] ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (9180, 'Ofusidic viscous eye drops 5 ml', 'اوفيوسيديك قطرة عين 5 مل', '28', '18', 'Antibiotic', 'Fusidic acid', 'Orchidia pharmaceutical industries', 'Drops', 'نقط', '5 ml', '1', 'مضاد حيوي', 'about fusidic acid a bacteriostatic steroid antibiotic systemic antibacterial anti-infective. mechanism of action of fusidic acid fusidic acid is a bactericidal or bacteriostatic drug depending upon the concentration. it inhibits the transfer of aminoacid', '6224000219437', NULL, 3915, '2024-11-03', 0, 'For the treatment of bacterial infections.', 'Fusidic acid works by interfering with bacterial protein synthesis, specifically by preventing the translocation of the elongation factor G (EF-G) from the ribosome. It also can inhibit chloramphenicol acetyltransferase enzymes.', 'Fusidic acid is a bacteriostatic antibiotic and helps prevent bacterial growth while the immune system clears the infection.', 'DrugBank', 0, 0, 0);
