INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25841, 'Relestat 0.05% eye drops 5 ml', 'ريليستات 0.05% قطرة للعين 5مل', '40', NULL, 'H1- and h2-receptors blocker', 'Epinastine', 'Allergan', 'Drops', 'نقط', '0.05%', '1', NULL, 'description epinastine is used for the prevention of itching associated with allergic conjunctivitis. it has a multi-action effect that inhibits the allergic response in 3 ways: 1. stabilizes mast cells by preventing mast cell degranulation to control the', '5016007203578', NULL, 1074, '2022-12-08', 'For the prevention of itching associated with allergic conjunctivitis.', 'Epinastine has a multiaction effect that inhibits the allergic response in 3 ways: 1. stabilizes mast cells by preventing mast cell degranulation to control the allergic response, 2. prevents histamine binding to both the H1- and H<sub>2</sub>-receptors to stop itching and provide lasting protection, and 3. prevents the release of proinflammatory chemical mediators from the blood vessel to halt progression of the allergic response.', 'Epinastine is an antihistamine and an inhibitor of histamine release from the mast cell for topical administration to the eyes. Epinastine is indicated for the prevention of itching associated with allergic conjunctivitis. Epinastine is a topically active, direct H<sub>1</sub>-receptor antagonist and an inhibitor of the release of histamine from the mast cell. Epinastine is selective for the histamine H<sub>1</sub>-receptor and has affinity for the histamine H2 receptor. Epinastine also possesses affinity for the a1-, a2-, and 5-HT<sub>2</sub> -receptors. Epinastine does not penetrate the blood/brain barrier and, therefore, is not expected to induce side effects of the central nervous system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21367, 'Ketofan 2.5% gel 40 gm', 'كيتوفان 2.5% جل 40 جم', '11', NULL, 'Nsaid.propionic acid derivatives', 'Ketoprofen', 'Amriya', 'Gel', 'جل', '2.5%', '1', NULL, 'about ketoprofen nsaid propionic acid derivative analgesic antipyretic antiinflammatory. mechanism of action of ketoprofen it inhibits cyclo oxygenase enzymes both cox1 and cox2.these cyclooxygenase enzyme catalyses the synthesis of prostaglandins from ar', '6221075230336', NULL, 828, '2022-12-06', 'For symptomatic treatment of acute and chronic rheumatoid arthritis, osteoarthritis, ankylosing spondylitis, primary dysmenorrhea and mild to moderate pain associated with musculotendinous trauma (sprains and strains), postoperative (including dental surgery) or postpartum pain.', 'The anti-inflammatory effects of ketoprofen are believed to be due to inhibition cylooxygenase-2 (COX-2), an enzyme involved in prostaglandin synthesis via the arachidonic acid pathway. This results in decreased levels of prostaglandins that mediate pain, fever and inflammation. Ketoprofen is a non-specific cyclooxygenase inhibitor and inhibition of COX-1 is thought to confer some of its side effects, such as GI upset and ulceration. Ketoprofen is thought to have anti-bradykinin activity, as well as lysosomal membrane-stabilizing action. Antipyretic effects may be due to action on the hypothalamus, resulting in an increased peripheral blood flow, vasodilation, and subsequent heat dissipation. ', 'Ketoprofen is a nonsteroidal anti-inflammatory agent (NSAIA) with analgesic and antipyretic properties. Ketoprofen has pharmacologic actions similar to those of other prototypical NSAIDs, which inhibit prostaglandin synthesis. Ketoprofen is used to treat rheumatoid arthritis, osteoarthritis, dysmenorrhea, and alleviate moderate pain.', 'DrugBank', 0, 0, 0);
