INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26071, 'Rifabiotic 300 mg 8 caps.', 'ريفابيوتك 300مجم 8 كبسول', '8', NULL, 'Antibiotic.antituberculosis', 'Rifampicin', 'Cid', 'Cap', 'كبسولة', '300 mg', '1', NULL, 'about rifampicin rifamycin derivative antituberculosis antibiotic. mechanism of action of rifampicin rifampicin is primarily bactericidal and has some bacteriostatic properties also. it acts by inhibiting dna dependent rna polymerase and thus stopping the', NULL, NULL, 695, '2022-08-01', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29008, 'Varolex 10 tab.', 'فارولكس 10 اقراص', '0', NULL, 'Vitamin b', 'Calcium pantothenate+nicotinamide+vitamin b1+vitamin b2+vitamin b6', 'Memphis', 'Tab', 'أقراص', NULL, '1', NULL, NULL, NULL, NULL, 828, '2022-07-24', 'Calcium acetate is one of a number of calcium salts used to treat hyperphosphatemia (too much phosphate in the blood) in patients with kidney disease.', 'Calcium acetate and other calcium salts are phosphate binders. They work by binding with the phosphate in the food you eat, so that it is eliminated from the body without being absorbed.', 'Patients with advanced renal insufficiency (creatinine clearance less than 30 ml/min) exhibit phosphate retention and some degree of hyperphosphatemia. The retention of phosphate plays a pivotal role in causing secondary hyperparathyroidism associated with osteodystrophy, and soft-tissue calcification. The mechanism by which phosphate retention leads to hyperparathyroidism is not clearly delineated. Therapeutic efforts directed toward the control of hyperphosphatemia include reduction in the dietary intake of phosphate, inhibition of absorption of phosphate in the intestine with phosphate binders, and removal of phosphate from the body by more efficient methods of dialysis. The rate of removal of phosphate by dietary manipulation or by dialysis is insufficient. Dialysis patients absorb 40% to 80% of dietary phosphorus. Therefore, the fraction of dietary phosphate absorbed from the diet needs to be reduced by using phosphate binders in most renal failure patients on maintenance dialysis. Calcium acetate when taken with meals combines with dietary phosphate to form insoluble calcium phosphate which is excreted in the feces. Maintenance of serum phosphorus below 6.0 mg/dl is generally considered as a clinically acceptable outcome of treatment with phosphate binders. Calcium acetate is highly soluble at neutral pH, making the calcium readily available for binding to phosphate in the proximal small intestine.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28695, 'Sylialand 5 gm 10 sachets', 'سيليالاند 5جم 10 اكياس', '200', NULL, 'Food supplement', 'Psyllium powder + chromium', 'Pharma land', 'Sachet', 'أكياس', '5 gm', '1', 'لعلاج البهاق', NULL, NULL, NULL, 881, '2023-04-25', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (3269, 'Cytotec 200mcg 28 tab', 'سيتوتيك 200مكجم 28 قرص', '250', NULL, 'Labour inducer.narcotic', 'Misoprostol', 'Pfizer >', 'Tab', 'أقراص', '200mcg', '2', 'انهاء الحمل حيث تساعد هذه المادة على تنشيط عملية انقباض الرحم وإنهاء الحمل بطريقة طبيعية.', NULL, '2801928551100', NULL, 6123, '2023-02-14', 'Misoprostol is indicated as a tablet to reduce the risk of NSAID induced gastric ulcers but not duodenal ulcers in high risk patients.[L7616] Misoprostol is also formulated in combination with diclofenac to treat symptoms of osteoarthritis or rheumatoid arthritis in patients with a high risk of developing gastric ulcers.[L7619] Misoprostol is used off label for the management of miscarriages, prevention of post partum hemorrhage, and is also used alone or in combination with mifepristone in other countries for first trimester abortions.[A181589,A181583,A181697]', 'Misoprostol is a synthetic prostaglandin E1 analog that stimulates prostaglandin E1 receptors on parietal cells in the stomach to reduce gastric acid secretion.[A181586] Mucus and bicarbonate secretion are also increased along with thickening of the mucosal bilayer so the mucosa can generate new cells.[A181586] 



Misoprostol binds to smooth muscle cells in the uterine lining to increase the strength and frequency of contractions as well as degrade collagen and reduce cervical tone.[A181586]', 'Misoprostol is a prostaglandin E1 analog used to reduce the risk of NSAID induced gastric ulcers by reducing secretion of gastric acid from parietal cells.[A181586,L7616,L7619] Misoprostol is also used to manage miscarriages and used alone or in combination with mifepristone for first trimester abortions.[A181589,A181583,A181598] An oral dose of misoprostol has an 8 minute onset of action and a duration of action of approximately 2 hours, a sublingual dose has an 11 minute onset of action and a duration of action of approximately 3 hours, a vaginal dose has a 20 minute onset of action and a duration of action of approximately 4 hours, and a rectal dose has a 100 minute onset of action and a duration of action of approximately 4 hours.[A181607]', 'DrugBank', 0, 0, 0);
