INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (11393, 'Thrombostat solution for i.v.infusion', 'ثرومبوستات محلول للحقن', '900', NULL, 'Antiplatelet', 'Tirofiban', 'Memphis > genesis pharmaceuticals', 'Solution', 'محلول', NULL, '1', NULL, NULL, NULL, NULL, 826, '2022-08-03', 0, 'For treatment, in combination with heparin, of acute coronary syndrome, including patients who are to be managed medically and those undergoing PTCA or atherectomy.', 'Tirofiban is a reversible antagonist of fibrinogen binding to the GP IIb/IIIa receptor, the major platelet surface receptor involved in platelet aggregation. Platelet aggregation inhibition is reversible following cessation of the infusion of tirofiban.', 'Tirofiban prevents the blood from clotting during episodes of chest pain or a heart attack, or while the patient is undergoing a procedure to treat a blocked coronary artery. It is a non-peptide antagonist of the platelet glycoprotein (GP) IIb/IIIa receptor, and inhibits platelet aggregation. When administered intravenously, tirofiban inhibits ex vivo platelet aggregation in a dose- and concentration-dependent manner. When given according to the recommended regimen, >90% inhibition is attained by the end of the 30-minute infusion. Tirofiban has been recently shown in patients with unstable angina to reduce ischemic events at 48 hours following infusion when compared to standard heparin therapy.', 'DrugBank', 0, 0, 0);
