INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Tenoxicam 20mg 20 caps', 11273, 'تينوكسيكام 20مجم 20 كبسولة', '60', '39', 'Nsaid.oxicam', 'Nsaid.oxicam', NULL, 'Tenoxicam', 'El-obour', 'Cap', 'كبسولة', '20mg', '2', 'مسكن الام', NULL, NULL, '6223004600074', 2152, '2024-12-06', NULL, 0, 'For the treatment of rheumatoid arthritis, osteoarthritis, backache, and pain.', 'The antiinflammatory effects of tenoxicam may result from the inhibition of the enzyme cycooxygenase and the subsequent peripheral inhibition of prostaglandin synthesis. As prostaglandins sensitize pain receptors, their inhibition accounts for the peripheral analgesic effects of tenoxicam. Antipyresis may occur by central action on the hypothalamus, resulting in peripheral dilation, increased cutaneous blood flow, and subsequent heat loss.', 'Tenoxicam, an antiinflammatory agent with analgesic and antipyretic properties, is used to treat osteoarthritis and control acute pain.', 'DrugBank');
