INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Efexor xr 150mg 14 caps.', 4006, 'ايفكسور اكس ار 150مجم 14 كبسولة', '230', '175', 'Psychiatric.antidepressants', 'Psychiatric.antidepressants', NULL, 'Venlafaxine', 'Wyeth ayerst lederle-canada > wyeth egypt company-egypt', 'Cap', 'كبسولة', '150mg', '1', 'مضاد للإكتئاب -- حالات النفسية والعصبية', NULL, 'about venlafaxine an arylalkanolamine derivative serotonin/norepinephrine reuptakeinhibitor antidepressant anxiolytic. mechanism of action of venlafaxine venlafaxine and its metabolite block the re-uptake of neurotransmitter serotonin and noradrenaline fr', '6251611000178', 5291, '2024-10-17', NULL, 0, 'Venlafaxine is indicated for the management of major depressive disorder (MDD), generalized anxiety disorder (GAD), social anxiety disorder (SAD), and panic disorder.[L43030]', 'The exact mechanism of action of venlafaxine in the treatment of various psychiatric conditions has not been fully elucidated; however, it is understood that venlafaxine and its active metabolite O-desmethylvenlafaxine (ODV) potently and selectively inhibits the reuptake of both serotonin and norepinephrine at the presynaptic terminal.[A27600,A252065,L43030] This results in increased levels of neurotransmitters available at the synapse that can stimulate postsynaptic receptors.[A252075] It is suggested that venlafaxine has a 30-fold selectivity for serotonin compared to norepinephrine: venlafaxine initially inhibits serotonin reuptake at low doses, and with higher doses, it inhibits norepinephrine reuptake in addition to serotonin.[A252065,A252070] Venlafaxine and ODV are also weak inhibitors of dopamine reuptake.[L43030]', 'Venlafaxine is an antidepressant agent that works to ameliorate the symptoms of various psychiatric disorders by increasing the level of neurotransmitters in the synapse. Venlafaxine does not mediate muscarinic, histaminergic, or adrenergic effects.[L43030]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Rehair 2% topical sol. 60ml', 25811, 'ريهاير 2% 60مل', '30', NULL, 'Antihypertensives.hair regrowth.vasodilator agents', 'Antihypertensives.hair regrowth.vasodilator agents', NULL, 'Minoxidil', 'El nasr', 'Sol', 'محلول', '2%', '1', NULL, NULL, 'about minoxidil a potassium channel agonist direct-acting vasodilator hair- growth stimulant in androgenic alopecia. mechanism of action of minoxidil minoxidil works by activating pghs 1 (prostaglandin endoperoxide synthase-1) which helps promote hair gro', '6221067003139', 933, '2022-12-08', NULL, 0, 'For the treatment of severe hypertension and in the topical treatment (regrowth) of androgenic alopecia in males and females and stabilisation of hair loss in patients with androgenic alopecia.', 'Minoxidil is thought to promote the survival of human dermal papillary cells (DPCs) or hair cells by activating both extracellular signal-regulated kinase (ERK) and Akt and by preventing cell death by increasing the ratio of BCl<sup>-</sup>2/Bax. Minoxidil may stimulate the growth of human hairs by prolonging anagen through these proliferative and anti-apoptotic effects on DPCs. Minoxidil, when used as a vasodilator, acts by opening adenosine triphosphate-sensitive potassium channels in vascular smooth muscle cells. This vasodilation may also improve the viability of hair cells or hair follicles.', 'Minoxidil is an orally effective direct acting peripheral vasodilator that reduces elevated systolic and diastolic blood pressure by decreasing peripheral vascular resistance. Minoxidil is also used topically to treat androgenetic alopecia. Microcirculatory blood flow in animals is enhanced or maintained in all systemic vascular beds. In man, forearm and renal vascular resistance decline; forearm blood flow increases while renal blood flow and glomerular filtration rate are preserved. The predominant site of minoxidil action is arterial. Venodilation does not occur with minoxidil; thus, postural hypotension is unusual with its administration. The antihypertensive activity of minoxidil is due to its sulphate metabolite, minoxidil sulfate.', 'DrugBank');
