INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Frequegard 15 mg 7 ext. rel. f.c. tab.', 19402, 'فريكيوجارد 15 مجم 7 اقراص', '22', NULL, 'Urge incontinence', 'Urge incontinence', NULL, 'Darifenacin', 'Medizen pharmaceutical industries', 'Tab', 'أقراص', '15 mg', '1', NULL, NULL, 'symptomatic treatment of urge incontinence and/or increased urinary frequency and urgency as may occur in adult patients with overactive bladder syndrome.', NULL, 674, '2022-07-29', NULL, 0, 'For the treatment of overactive bladder with symptoms of urge urinary incontinence, urgency and frequency.', 'Darifenacin selectively antagonizes the muscarinic M3 receptor. M3 receptors are involved in contraction of human bladder and gastrointestinal smooth muscle, saliva production, and iris sphincter function.', 'Darifenacin is a competitive muscarinic receptor antagonist. <i>In vitro</i> studies using human recombinant muscarinic receptor subtypes show that darifenacin has greater affinity for the M3 receptor than for the other known muscarinic receptors (9 and 12-fold greater affinity for M3 compared to M1 and M5, respectively, and 59-fold greater affinity for M3 compared to both M2 and M4). Muscarinic receptors play an important role in several major cholinergically mediated functions, including contractions of the urinary bladder smooth muscle and stimulation of salivary secretion. Adverse drug effects such as dry mouth, constipation and abnormal vision may be mediated through effects on M3 receptors in these organs.', 'DrugBank');
