INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Mefentan 250mg 10caps.', 22457, 'ميفينتان 250مجم 10 كبسول', '3', NULL, 'Nsaid.fenamates', 'Nsaid.fenamates', NULL, 'Mefenamic acid', 'Global napi pharmaceuticals', 'Cap', 'كبسولة', '250mg', '1', NULL, NULL, 'about mefenamic acid nsaid analgesic antiinflammatory. mechanism of action of mefenamic acid mefenamic acid has analgesic anti-inflammatory and antipyretic action. it acts by inhibiting prostaglandin (pgs) synthesis and their release at the site of injury', NULL, 821, '2022-07-15', NULL, 0, 'For the treatment of rheumatoid arthritis, osteoarthritis, dysmenorrhea, and mild to moderate pain, inflammation, and fever.', 'Mefenamic acid binds the prostaglandin synthetase receptors COX-1 and COX-2, inhibiting the action of prostaglandin synthetase. As these receptors have a role as a major mediator of inflammation and/or a role for prostanoid signaling in activity-dependent plasticity, the symptoms of pain are temporarily reduced.', 'Mefenamic acid, an anthranilic acid derivative, is a member of the fenamate group of nonsteroidal anti-inflammatory drugs (NSAIDs). It exhibits anti-inflammatory, analgesic, and antipyretic activities. Similar to other NSAIDs, mefenamic acid inhibits prostaglandin synthetase.', 'DrugBank');
