INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (10887, 'Sterogyl 15 h 600.000 i.u./1.5ml i.m. amp.', 'ستيروجيل 15 اتش 600000 وحدة دولية امبول', '66', NULL, 'Vitamin d', 'Ergocalciferol(vitamin d2)', 'Platinum pharma', 'Amp', 'أمبول', '1.5ml', '1', 'علاج نقص فيتامين د', NULL, NULL, NULL, 5087, '2023-05-12', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Ergocalciferol is an inactivated vitamin D analog.[A177526] It is synthesized by some plants in the presence of UVB light.[T577] The production of ergocalciferol was prompted by the identification of dietary deficiency, more specifically vitamin D, as the main causative factor for the development of rickets. Ergocalciferol was isolated for the first time from yeast in 1931 and its structure was elucidated in 1932.[T580]

