INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (11573, 'Molnupiravir-rameda 200mg 40 caps', 'مولنوبيرافير راميدا 200مجم 40 كبسولة', '461', '420', 'Antiviral', 'Molnupiravir', 'Eva pharma', 'Cap', 'كبسولة', '200mg', '4', 'مضاد فيروسات', NULL, NULL, NULL, 909, '2025-12-14', 0, '[N4-hydroxycytidine] and its prodrug molnupiravir are being studied for its activity against a number of viral infections including influenza, MERS-CoV, and SARS-CoV-2.[A193014, A193029]



Molnupiravir is approved in the UK for reducing the risk of hospitalization and death in mild to moderate COVID-19 cases for patients at increased risk of severe disease (eg. with obesity, diabetes mellitus, heart disease, or are over 60 years old).[L39050,L39055]



In the US, molnupiravir is authorized for emergency use for the treatment of high-risk adults With mild to moderate COVID-19.[L39588]', 'Molnupiravir is hydrolyzed _in vivo_ to N4-hydroxycytidine, which is phosphorylated in tissue to the active 5’-triphosphate form, and incorporated into the genome of new virions, resulting in the accumulation of inactivating mutations, known as viral error catastrophe.[A193014,A193029] A [remdesivir] resistant mutant mouse hepatitis virus has also been shown to have increased sensitivity to N4-hydroxycytidine.[A193014]', NULL, 'DrugBank', 0, 0, 0);
