INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (11867, 'Depratiox 15 mg 20 f.c. tabs.', 'ديبراتيوكس 15مجم 20 قرص', '230', NULL, 'Psychiatric.antidepressants', 'Vortioxetine', 'Hikma', 'Tab', 'أقراص', '15 mg', '2', NULL, NULL, NULL, NULL, 773, '2022-11-30', 0, 'Vortioxetine is indicated for the treatment of major depressive disorder (MDD).', 'Vortioxetine is classified as a serotonin modulator and simulator (SMS) as it has a multimodal mechanism of action towards the serotonin neurotransmitter system whereby it simultaneously modulates one or more serotonin receptors and inhibits the reuptake of serotonin. More specifically, vortioxetine acts via the following biological mechanisms: as a serotonin reuptake inhibitor (SRI) through inhibition of the serotonin transporter, while also acting as a partial agonist of the 5-HT1B receptor, an agonist of 5-HT1A, and antagonist of the 5-HT3, 5-HT1D, and 5-HT7 receptors.', 'Vortioxetine binds with high affinity to the human serotonin transporter (Ki=1.6 nM), but not to the norepinephrine (Ki=113 nM) or dopamine (Ki>1000 nM) transporters. Vortioxetine potently and selectively inhibits reuptake of serotonin by inhibition of the serotonin transporter (IC50=5.4 nM). Specifically, vortioxetine binds to 5­HT3 (Ki=3.7 nM), 5­HT1A (Ki=15 nM), 5­HT7 (Ki=19 nM), 5­HT1D (Ki=54 nM), and 5­HT1B (Ki=33 nM), receptors and is a 5­HT3, 5­HT1D, and 5­HT7 receptor antagonist, 5­HT1B receptor partial agonist, and 5­HT1A receptor agonist.', 'DrugBank', 0, 0, 0);
