    ) VALUES (24113, 'Ondametic 4mg/2ml 5 ampoules', 'اونداميتيك 4مجم/2مل 5 امبولات', '143', '127.5', 'Antiemetic', 'Ondansetron', 'Pharopharma', 'Ampoule', 'أمبول', '4mg', '5', 'مضاد للقيء والترجيع', 'In the adult patient population:

