INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (21055, 'Cefaxotrez 500 mg i.m. vial', 'سيفاكسوتريز 500مجم عضل فيال', '63', '44', 'Antibiotic.cephalosporin.third-generation', 'Ceftriaxone', 'Boston care pharmaceuticals', 'Vial', 'فيال', '500 mg', '1', NULL, NULL, NULL, NULL, 778, '2025-06-23', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Ceftriaxone is a broad-spectrum third-generation cephalosporin antibiotic.[A215582] It has a very long half-life compared to other cephalosporins and is high penetrable into the meninges[A215582], eyes[A215647], and inner ear[A215627]. Ceftriaxone has broader and stronger gram-negative coverage then first or second-generation cephalosporins, but worse activity against methicillin-susceptible S.aureus. Ceftriaxone is a commonly used antimicrobial due to its good activity against multi-drug resistant Enterobacteriaceae, its relatively safe adverse effect profile, and its long half-life which allows for the convenience of daily or twice-daily dosing.[A215582]', '["J01DD04", "J01DD54"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (21057, 'Itorisperidone 2mg 30 f.c. tabs.', 'ايتوريسبيريدون 2 مجم 30 قرص', '48', NULL, 'Psychiatric.antipsychotics', 'Risperidone', 'Sigma > international trading office', 'Tab', 'أقراص', '2mg', '3', NULL, 'about risperidone second-generation antipsychotic benzisoxazole derivative antimanic atypical antipsychotic. mechanism of action of risperidone risperidone exerts its antipsychotic activity by blocking dopamine and serotonin (5ht2) receptors. the drug has', NULL, NULL, 815, '2022-07-29', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Risperidone is a second-generation antipsychotic (SGA) medication used in the treatment of a number of mood and mental health conditions including schizophrenia and bipolar disorder.[L12885] It is one of the most widely used SGAs. [Paliperidone], another commonly used SGA, is the primary active metabolite of risperidone (i.e. 9-hydroxyrisperidone).[L12885]

