    ) VALUES (22951, 'Montelosab 5 mg 7 chewable tab.', 'مونتيلوساب 5مجم 7 اقراص', '12', NULL, 'Asthma.anti-inflammatory.leukotriene antagonist', 'Montelukast', 'Sabaa', 'Tab', 'أقراص', '5 mg', '1', NULL, 'Montelukast is indicated for:



