INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (12874, 'Zinnat 500mg 10 tab', 'زينات 500مجم 10 اقراص', '45', NULL, 'Antibiotic', 'Cefuroxime', 'Gsk', 'Tab', 'أقراص', '500mg', '1', NULL, NULL, '6221045001010', NULL, 1885, '2022-12-15', 0, 'For the treatment of many different types of bacterial infections such as bronchitis, sinusitis, tonsillitis, ear infections, skin infections, gonorrhea, and urinary tract infections.', 'Cefuroxime, like the penicillins, is a beta-lactam antibiotic. By binding to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, it inhibits the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins; it is possible that cefuroxime interferes with an autolysin inhibitor.', 'Cefuroxime is a &beta;-lactam type antibiotic. More specifically, it is a second-generation cephalosporin. Cephalosporins work the same way as penicillins: they interfere with the peptidoglycan synthesis of the bacterial wall by inhibiting the final transpeptidation needed for the cross-links. This effect is bactericidal. Cefuroxime is effective against the following organisms: Aerobic Gram-positive Microorganisms: <i>Staphylococcus aureus</i>, <i>Streptococcus pneumoniae</i>, <i>Streptococcus pyogenes</i>. Aerobic Gram-negative Microorganisms: <i>Escherichia coli</i>, <i>Haemophilus influenzae</i> (including beta-lactamase-producing strains), <i>Haemophilus parainfluenzae</i>, <i>Klebsiella pneumoniae</i>, <i>Moraxella catarrhalis</i> (including beta-lactamase-producing strains), <i>Neisseria gonorrhoeae</i> (including beta-lactamase-producing strains). Spirochetes: <i>Borrelia burgdorferi</i>. Cefuroxime axetil is the prodrug ', 'DrugBank', 0, 0, 0);
