INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13261, 'Abc immune 10 sachets', 'ايه بي سي اميون 10 اكياس', '40', NULL, 'Vitamin c', 'Vitamin c+zinc', 'Aug pharma', 'Sachet', 'أكياس', NULL, '1', 'فيتامين سي', '300 mg vitamin c + 5 mg zinc lemon & honey flavor dose: 1 sachet daily. pour the content of the sachet in a cup add 150 ml hot but not boiling water stir and drink.', NULL, NULL, 1379, '2022-03-25', 0, 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13263, 'Abc lemon & honey syrup 60 ml', 'ايه بي سي ليمون & عسل شراب 60 مل', '54', NULL, 'Multivitamins', 'Natural honey+citric acid+lemon juice concentrate', 'Aug pharma', 'Syrup', 'شراب', '60 ml', '1', NULL, 'it is an all natural immune booster that is suitable for all ages. helps with: -immune system support. -cough. -irritated throats. -hoarseness.', NULL, NULL, 803, '2022-07-17', 0, NULL, 'The predominant targets of NK cells are ‘stressed’ cells with downregulated expression of class I MHC (MHC-I), a common mechanism used by malignant or virus-infected cells to evade recognition by the body’s immune system. In response to encountering these cells, NK cells release two types of proteins, perforins and granzyme B, which attack the target cell’s membrane, damage its organelles, and trigger apoptosis. NK Cell immunotherapy is an emerging anti-cancer strategy, as these cells are able to exert effects without activating the graft-versus-host disease cascade. In addition, their broad mechanism allows for generally safe administration of NK cells that are not immunologically matched to a patient. ', NULL, 'DrugBank', 0, 0, 0);
