INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13379, 'Acniswab 0.1% cream 30 gm', 'اكنيسواب 0.1% كريم 30 جرام', '45', NULL, 'Psoriasis', 'Tazarotene', 'Uniswab', 'Cream', 'كريم', '0.1%', '1', NULL, 'indication used to treat psoriasis acne and sun damaged skin (photodamage). mechanism of action although the exact mechanism of tazarotene action is not known studies have shown that the active form of the drug (tazarotenic acid) binds to all three member', NULL, NULL, 1074, '2022-06-18', 0, 'Used to treat psoriasis, acne and sun damaged skin (photodamage).', 'Although the exact mechanism of tazarotene action is not known, studies have shown that the active form of the drug (tazarotenic acid) binds to all three members of the retinoic acid receptor (RAR) family: RARa, RARb, and RARg, but shows relative selectivity for RARb, and RARg and may modify gene expression. It also has affinity for RXR receptors.', 'Following topical application, tazarotene undergoes esterase hydrolysis to form its active metabolite, tazarotenic acid. When treating acne tazarotene may be taken in conjunction with an oral antibiotic. Tazarotene has been shown in peer-reviewed double blinded studies to reduce: mottling and hyperpigmentation, sallowness, fine wrinkling and coarse wrinkling in sun damaged skin. Histological studies have shown that long term (greater than 1 year) use of Tazarotene is associated with a significant reduction in atypical melanocytes and keratocytes - cells considered to be precursors of skin cancer. Some studies have shown long term use of Tazarotene to be associated with increased collagen production and better organization of skin collagen bundles.', 'DrugBank', 0, 0, 0);
