INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (9023, 'Nostamine eye/nose drops 15 ml', 'نوستامين قطرة للعين والانف 15 مل', '22', '15', 'Corneal vascularity.antihistamine combined with alpha 1 agonist', 'Chlorpheniramine+naphazoline', 'Eipico', 'Drops', 'نقط', '15 ml', '1', 'مضاد للاحتقان ومضاد للحساسية للعين والانف', 'indications - rhinitis (acute allergic) sinusitis; - nasal hemorrhage (as vasoconstrictor) to stop and alleviate conduction of rhinoscopy; - hay fever; - conjunctivitis (allergic chronic); - asthenopic disorders (accommodation disturbance in case of enlar', '6221032330697', NULL, 10947, '2024-07-26', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6921, 'Dorziborg 2% eye drop 5 ml', 'دورزيبورج 2% قطرة عين 5 مل', '55', NULL, 'Antiglucoma', 'Dorzolamide', 'Borg', 'Drop', 'نقط', '2%', '1', NULL, NULL, NULL, NULL, 864, '2025-08-16', 'Dorzolamide is indicated for the management of elevated intraocular pressure in patients with ocular hypertension or open-angle glaucoma.[L11377] It can also be used in combination with [timolol] for the same indication in patients who are insufficiently responsive to ophthalmic beta-blockers.[L11380]



Its pre-operative use was also investigated to prevent elevated intraocular pressure after neodynium yttrium aluminum garnet laser posterior capsulotomy.[A190183]', 'Elevated intraocular pressure is a characteristic manifestation of ocular hypertension or open-angle glaucoma. The level of intraocular pressure (IOP) is governed by the balance between the production of aqueous humour (by ocular ciliary processes) and its outflow from the anterior segment of the eye via trabecular (conventional) or uveoscleral (unconventional) pathways. When there is an increase in the resistance to the trabecular outflow of aqueous humour, the intraocular pressure is elevated. Subsequently, optic nerve damage can occur from blood flow restrictions and mechanical distortion of ocular structures. Optic nerve damage can further result in optic disc cupping and progressive visual field loss (and blindness in some cases).[A1304] 



Carbonic anhydrase (CA) is a ubiquitous enzyme that catalyzes the reversible hydration of carbon dioxide to bicarbonate ions and dehydration of carbonic acid.[A1304,L11377] In the ocular ciliary processes, the local production of bicarbonate by CAs promotes sodium and fluid transport. CA-II is a key isoenzyme found primarily in red blood cells (RBCs) that regulates aqueous humour production.[A1304] Dorzolamide is a highly specific CA-II inhibitor, where it displays a 4000-fold higher affinity for carbonic anhydrase II than carbonic anhydrase I.[A1304] The inhibition of CA-II in the ciliary process disrupts the formation of bicarbonate ions and reduces sodium and fluid transport, which leads to decreased aqueous humour secretion and reduced intraocular pressure.[A1304,L11377]', 'Dorzolamide is a carbonic anhydrase inhibitor that reduces elevated intraocular pressure in open-angle glaucoma or ocular hypertension. When used in combination with topic beta-adrenergic antagonists, dorzolamide has an additive effect of lowering intraocular pressure. The peak ocular hypotensive effect of dorzolamide is observed at about 2 hours following ophthalmic administration.[A1304]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30052, 'Zymar 0.3% ophthalmic sol. 5 ml', 'زيمار 0.3% محلول للعين 5مل', '33', NULL, 'Antibiotic.quinolone', 'Gatifloxacin', 'Allergan > soficopharm', 'Sol', 'محلول', '0.3%', '1', NULL, NULL, NULL, NULL, 811, '2022-07-21', 'For the treatment of bronchitis, sinusitis, community-acquired pneumonia, and skin infections (abscesses, wounds) caused by S. pneumoniae, H. influenzae, S. aureus, M. pneumoniae, C. pneumoniae, L. pneumophila, S. pyogenes', 'The bactericidal action of Gatifloxacin results from inhibition of the enzymes topoisomerase II (DNA gyrase) and topoisomerase IV, which are required for bacterial DNA replication, transcription, repair, and recombination.', 'Gatifloxacin is a synthetic broad-spectrum 8-methoxyfluoroquinolone antibacterial agent for oral or intravenous administration. is bactericidal and its mode of action depends on blocking of bacterial DNA replication by binding itself to an enzyme called DNA gyrase, which allows the untwisting required to replicate one DNA double helix into two. Notably the drug has 100 times higher affinity for bacterial DNA gyrase than for mammalian. Gatifloxacin is a broad-spectrum antibiotic that is active against both Gram-positive and Gram-negative bacteria. It should be used only to treat or prevent infections that are proven or strongly suspected to be caused by bacteria.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18319, 'Enerzad extra 30 f.c.tabs.', 'انيرزاد اكسترا 30 قرص', '190', NULL, 'Amino acid', 'L-arginine+zinc', 'Arax pharm. ind.', 'Tab', 'أقراص', NULL, '3', NULL, 'dietary supplement used during physical stress. dose: 1-3 capsules daily.', NULL, NULL, 2249, '2022-07-04', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13778, 'Allerfen 120mg 30 f.c. tabs.', 'اليرفين 120مجم 30 قرص', '96', '39', 'Antihistamine.antiallergy', 'Fexofenadine', 'Amoun', 'Tab', 'أقراص', '120mg', '3', NULL, NULL, NULL, NULL, 1117, '2024-12-08', 'In the United States, fexofenadine is indicated for the symptomatic treatment of allergic rhinitis in patients ≥2 years old and chronic idiopathic urticaria in patients ≥6 months old.[L4269] In Canada, fexofenadine carries the same indications but is approved only for patients ≥12 years old.[L10779] Fexofenadine is also available in combination with [pseudoephedrine] for the symptomatic treatment of season allergic rhinitis in patients ≥12 years old.[L10800]', 'The H<sub>1</sub> histamine receptor is responsible for mediating hypersensitivity and allergic reactions. Exposure to an allergen results in degranulation of mast cells and basophils, which then release histamine and other inflammatory mediators. Histamine binds to, and activates, H<sub>1</sub> receptors, which results in the further release of pro-inflammatory cytokines, such as interleukins, from basophils and mast cells. These downstream effects of histamine binding are responsible for a wide variety of allergic symptoms, such as pruritus, rhinorrhea, and watery eyes.[A1495]



Fexofenadine is considered an “inverse agonist” of the H<sub>1</sub> receptor because it binds to and stabilizes the inactive form of the receptor, preventing its activation and subsequent downstream effects.[A1495] It has a potent and selective affinity for H<sub>1</sub> receptors, and there is no evidence that it carries antidopaminergic, antiserotonergic, anticholinergic, sedative, or adrenergic blocking activity.[L10779] Fexofenadine does not cross the blood-brain barrier and thus is unlikely to cause significant CNS effects.[L10779]', 'Fexofenadine relieves allergy symptoms by antagonizing the actions of histamine, an endogenous compound predominantly responsible for allergic symptomatology.[L4269] The relatively long duration of action of fexofenadine (approximately 24 hours)[A1495] allows for once or twice daily dosing, and its rapid absorption allows for an onset of action within 1-3 hours. Fexofenadine should not be taken with fruit juice, as this may impair its absorption.[L4269]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20140, 'Haemodialysis concentrate (bicarbonate) (1:35) (formula 1046)', 'هيمودياليسيس (محلول غسيل الكلى) بيكربونات 1:35', '79', NULL, 'Sterile solution', 'Acetic acid+calcium chloride+magnesium chloride+potassium chloride+sodium chloride', 'El nasr', 'Unknown', 'غير محدد', NULL, '1', NULL, 'the concentrated haemodialysis solutions are electrolyte water solutions with or without glucoses used in the haemodialysis process for patients with non-functioning kidneys.', NULL, NULL, 706, '2022-07-29', 'Used to treat infections in the ear canal.', NULL, NULL, 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15740, 'Mounjaro injection 2.5mg/0.5ml 4 single dose prefilled pens', 'مونجارو حقن 2.5مجم/0.5مل 4 اقلام معبأة للحقن', '9,435', NULL, 'Anti-diabetic.glp-1 receptor agonist', 'Tirazepatide', 'Eli lilly', 'Injection', 'حقن', '2.5mg', '4', NULL, NULL, NULL, NULL, 3502, '2022-09-08', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (8498, 'Mykotral h topical cream 15 gm', 'ميكوترال اتش كريم 15 جم', '7', NULL, 'Antifungal with glucocorticoid', 'Hydrocortisone+miconazole', 'Sigma', 'Cream', 'كريم', '15 gm', '1', NULL, 'about hydrocortisone systemic synthetic glucocorticoid anti-inflammatory indications for hydrocortisone intra-articular injection : 1. rheumatoid arthritis 2. gouty arthritis 3. osteoarthritis 4. joint sequelae of fractures and dislocations 5. other infla', '6221051080139', NULL, 1163, '2023-04-21', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17183, 'Deferoxinova 500mg 10 disp. tabs.', 'ديفيروكسينوفا 500 مجم 10 أقراص', '765', NULL, 'Iron chelating agents', 'Deferasirox', 'Debeiky > innova pharma', 'Tab', 'أقراص', '500mg', '1', NULL, 'about deferasirox a synthetic achiral tridentate triazole derived from salicylic acid oral iron chelating agents. indication for the treatment of chronic iron overload due to blood transfusions (transfusional hemosiderosis) in patients 2 years of age and', NULL, NULL, 1046, '2022-07-15', 'For the treatment of chronic iron overload due to blood transfusions (transfusional hemosiderosis) in patients 2 years of age and older.', 'Two molecules of deferasirox are capable of binding to 1 atom of iron. Deferasirox works in treating iron toxicity by binding trivalent (ferric) iron (for which it has a strong affinity), forming a stable complex which is eliminated via the kidneys.', 'Deferasirox is an orally active chelator that is selective for iron (as Fe3+). It is a tridentate ligand that binds iron with high affinity in a 2:1 ratio. Although deferasirox has very low affinity for zinc and copper there are variable decreases in the serum concentration of these trace metals after the administration of deferasirox. The clinical significance of these decreases is uncertain.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31184, 'Redoxon 1 gm 15 eff. tabs.', 'ريدوكسون 1جم 15 قرص فوار', '395', '240', 'Vitamin c', 'Vitamin c', 'Bayer consumer care > ibn sina', 'Tab', 'أقراص', '1 gm', '1', NULL, NULL, '6285074004688', NULL, 1789, '2025-10-09', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18960, 'Fenugreek 610 mg 100 caps. (illegal import)', 'فينوجريك 610 مجم 100 كبسولة', '0', NULL, 'Lactagauge', 'Fenugreek', 'Puritans pride', 'Cap', 'كبسولة', '610 mg', '1', NULL, 'helps support overall wellness fenugreek seeds are a rich source of saponins which account for many important benefits. fenugreek can be used as part of your healthy lifestyle.', '6223000475119', NULL, 777, '2022-08-03', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30418, 'Donaferon plus 20 caps.', 'دونافيرون بلس 20 كبسولة', '120', '100', 'Nutrition', 'Ferrous bisglycinate+lactoferrin+folic acid', 'Copad egypt > hoffmann pharm', 'Cap', 'كبسولة', NULL, '2', 'مكمل غذائي لتقوية وتعزيز المناعة لمقاومة الامراض التي تسببها البكتريا أوالفيروسات،كما انه مقوي للدم للعلاج والوقاية من انيميا نقص الحديد بالدم .', 'ferrous bisglycinate ... 92 mg lactoferrin ... 50 mg folic acid .... 500 mcg treatment of iron deficiency anaemia', NULL, NULL, 2595, '2025-10-31', 'Ferrous sulfate is used for the prevention and treatment of iron deficiency anemia in adults and children.[A190804,L2240,L11800]

', 'Iron is required to maintain optimal health, particularly for helping to form red blood cells (RBC) that carry oxygen around the body. A deficiency in iron indicates that the body cannot produce enough normal red blood cells.[A32514,L11800] Iron deficiency anemia occurs when body stores of iron decrease to very low levels, and the stored iron is insufficient to support normal red blood cell (RBC) production. Insufficient dietary iron, impaired iron absorption, bleeding, pregnancy, or loss of iron through the urine can lead to iron deficiency.[A32514,L11794]  Symptoms of iron deficiency anemia include fatigue, breathlessness, palpitations, dizziness, and headache.



Taking iron in supplement form, such as ferrous sulfate, allows for more rapid increases in iron levels when dietary supply and stores are not sufficient.[L2175] Iron is transported by the divalent metal transporter 1 (DMT1) across the endolysosomal membrane to enter the macrophage. It can then can be incorporated into ferritin and be stored in the macrophage or carried of the macrophage by ferroportin. This exported iron is  oxidized by the enzyme to ceruloplasmin to Fe3+, followed by sequestration by transferrin for transport in the serum to various sites, including the bone marrow for hemoglobin synthesis or into the liver.[A32524] Iron combines with porphyrin and globin chains to form hemoglobin, which is critical for oxygen delivery from the lungs to other tissues.[L2263]', 'Ferrous sulfate replenishes iron, an essential component in hemoglobin, myoglobin, and various enzymes. It replaces the iron that is usually found in hemoglobin and myoglobin. Iron participates in oxygen transport and storage, electron transport and energy metabolism, antioxidant and beneficial pro-oxidant functions, oxygen sensing, tissue proliferation and growth, as well as DNA replication and repair.[A32524,A32514]



', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19214, 'Flukit 20 tab.', 'فلوكيت 20 قرص', '12', NULL, 'Cold drugs', 'Paracetamol(acetaminophen)+phenylephrine+promethazine+salicylamide', 'Julphar', 'Tab', 'أقراص', NULL, '2', NULL, 'about paracetamol acetanilide derivative non narcotic analgesic antipyretic. mechanism of action of paracetamol paracetamol has analgesic and antipyretic action. it is more active on cyclo-oxygenase enzyme in brain. peripherally it is a poor inhibitor of', NULL, NULL, 952, '2022-07-18', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1874, 'Vulnere 30 gm cream', 'فولنير 30 جم كريم', '30', NULL, 'Skin moisturizing cream', 'PANTHENOL. STEARIC ACID. GLYCERIN. CHLORHEXIDINE', 'Health way', 'Cream', 'كريم', '30 gm', '1', NULL, NULL, NULL, NULL, 909, '2024-12-23', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (81, 'Acnetin- a 0.05% topical sol. 15 ml', 'اكنيتين -ايه 0.05% محلول 15 مل', '6', NULL, 'Anti-acne', 'Tretinoin', 'Sigma', 'Sol', 'محلول', '0.05%', '1', 'لعلاج حب الشباب', 'about tretinoin retinoid vitamin a metabolite anti-acne in hyperpigmentation. mechanism of action of tretinoin tretinoin is all trans retinoic acid (acid form of vitamin a). tretinoin is a potent comedolytic. it stimulates mitosis and epidermal cell turno', '6221051060254', NULL, 1458, '2022-12-01', 'Oral tretinoin is indicated for induction of remission in adults and pediatric patients one year of age and older with acute promyelocytic leukemia (APL), characterized by the presence of t(15;17) translocation or presence of PML/RARα gene expression and who are refractory to or who have relapsed from anthracycline chemotherapy or for whom anthracycline-based chemotherapy is contraindicated.[L45349]



Topical tretinoin is also indicated alone [L45389] or in combination with [benzoyl peroxide] [L34869] or [clindamycin] [L45384] for the treatment of acne vulgaris. It is also used in prescription and over-the-counter for treating various skin conditions such as melasma,[L45394] hyperpigmentation,[A257474] and photoaging [A258180] alone or in combination with other drugs.', 'The exact mechanism of action of tretinoin in skin conditions and acute promyelocytic leukemia (APL) has not been fully elucidated; however, several proposed mechanisms exist. Tretinoin is believed to exert its pharmacological actions by binding to and activating two types of nuclear receptors - retinoic acid receptors (RARs) alpha, beta, and gamma and retinoid X receptors (RXRs). In the human skin, RARs (especially RAR-alpha) form heterodimers with RXR to act as inducible transcription regulators of genes involved in cell differentiation by binding to retinoic acid response elements.[A257724,A258185] Tretinoin binds to RXRs to promote epidermal proliferation. It also blocks the actions of inflammatory mediators, enhancing procollagen production and collagen type I and III formations.[A257474,A257629,A257609,A257699] Some animal and human studies suggest that tretinoin induces the expression of transforming growth factor beta (TGF-β), which stimulates the transcription of several types of collagen messenger RNA. Collagen formation curtails further solar UV-induced skin damage and aging processes.[A257699]



Acne is associated with abnormal follicular formation from excessive keratinization of epithelial cells. Tretinoin promotes cornified cell detachment and enhances keratinocyte shedding. It also stimulates mitotic activity and loosely-adherent corneocyte turnover to expel comedo contents, reducing microcomedo precursor lesions of acne vulgaris.[A257474,L34869] Tretinoin may reduce epidermal melanin and pigmentation by increasing keratinocyte turnover and reducing tyrosinase activity.[A257609,A258180]



RAR-alpha and -beta have also been implicated in APL.[A257724] APL is characterized by a t(15;17) chromosomal translocation, which fuses the promyelocytic myeloid leukemia (PML) gene with the RAR-alpha gene.[A257689,A257694] The resulting PML-RAR-alpha fusion protein plays a role in the pathogenesis of APL by aberrating promyelocyte differentiation. The PML-RAR-alpha fusion protein is found to be predominant in leukemic cells, exerting a dominant negative effect on RAR, RXR and PML function.[A257689] Tretinoin induces terminal differentiation in hemopoietic precursor cell lines and APL cells.[A257694] Tretinoin is believed to promote caspase-mediated cleavage and proteasome-dependent degradation to cause apoptosis and degradation of the PML-RAR-alpha fusion protein.[A257474] It may also convert the fusion protein from a transcription repressor to an activator.[A257474]', 'Tretinoin is a vitamin A derivative that promotes cell production, proliferation, and differentiation. When used topically, tretinoin regulates epidermal cell turnover and collagen production. It also prevents collagen loss, reduces inflammation, and blocks the induction of matrix metalloproteinase (MMP), which are enzymes that disrupt collagen and elastic fibres.[A257474,A257629,A257609,A258180] In short-term and long-term studies, topical application of tretinoin at doses ranging from 0.001% to 0.1% was associated with improvements in clinical signs of photoaging and fine wrinkles, increased epidermal thickness, compaction of the stratum corneum, and decreased melanin content.[A257684,A258180,A258185,A257699] It also improved melanocyte differentiation and distribution, promotion of epidermal hyperplasia, and angiogenesis.[A257699]



Tretinoin exhibits antineoplastic activities when given orally.[L45349] Tretinoin was shown to induce differentiation in tumour cells.[A257694] It induced cytodifferentiation and decreased acute promyelocytic leukemia (APL) cell proliferation in culture and _in vivo_. In patients with APL, tretinoin promoted the initial maturation of the primitive promyelocytes derived from the leukemic clone, followed by a repopulation of the bone marrow and peripheral blood by normal, polyclonal hematopoietic cells in patients achieving complete remission.[L45349]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22619, 'Mestinon 60 mg 150 tab.', 'مستينون 60مجم 150 قرص', '803', '535', 'Cholinesterase inhibitor', 'Pyridostigmin bromide', 'Meda ab > multipharma', 'Tab', 'أقراص', '60 mg', '15', NULL, 'indication for the treatment of myasthenia gravis. pharmacodynamics pyridostigmine is a parasympathomimetic and a reversible cholinesterase inhibitor. since it is a quaternary amine it is poorly absorbed in the gut and doesn t cross the blood-brain barrie', '7680199430388', NULL, 6484, '2025-09-08', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18603, 'Ethanolamine oleate 5% amp', 'ايثانولامين اوليات 5%؜ امبول', '7', '5', NULL, 'Ethanolamine oleate', 'Eipico', 'Amp', 'أمبول', '5%', '1', 'يستخدم في علاج الدوالي', 'ethanolamine oleate is indicated in the treatment of bleeding esophageal varices and the prevention of recurrent bleeding in patients whose varices have recently bled.', NULL, NULL, 1442, '2024-12-03', 'For the treatment of patients with esophageal varices that have recently bled, to prevent rebleeding.', 'The oleic acid component of ethanolamine oleate is responsible for the inflammatory response, and may also activate coagulation in vivo by release of tissue factor and activation of Hageman factor. The ethanolamine component, however, may inhibit fibrin clot formation by chelating calcium, so that a procoagulant action of ethanolamine oleate has not been demonstrated.', 'When injected intravenously, ethanolamine oleate acts primarily by irritation of the intimal endothelium of the vein and produces a sterile dose-related inflammatory response. This results in fibrosis and possible occlusion of the vein. Ethanolamine oleate also rapidly diffuses through the venous wall and produces a dose-related extravascular inflammatory reaction.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (12312, 'Neeromella cream 50 gm', 'نيروميلا كريم 50 جم', '45', '42', 'Soothing & moisturiser for skin', 'Olive oil+jojoba oil+sesame oil+zinc oxide+tea tree oil+panthenol+chamomile extract', 'P pharm', 'Cream', 'كريم', '50 gm', '1', NULL, NULL, NULL, NULL, 1038, '2025-08-06', 'In a clinical context, olive oil is used as a source of calories and fatty acids in patients requiring parenteral nutrition. A formulation called Clinolipid, comprising olive oil and [soybean oil], is indicated in all age groups, including term and preterm neonates, as a source of calories and essential fatty acids for parenteral nutrition (PN) when oral or enteral nutrition is not possible, insufficient, or contraindicated.[L51013]', 'Fatty acids act as a substrate in energy production through beta-oxidation as well as important components of cell membrane structures and prescursors for bioactive molecules like prostaglandins.', 'Provides a source of calories and fatty acids.[L51013]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20270, 'Hayatmel topical cream 30 gm', 'هاياتميل كريم موضعي 30 جم', '17', NULL, NULL, 'Fluocinolone+hydroquinone+tretinoin', 'Delta pharma > international co. for pharmaceuticals drugs', 'Cream', 'كريم', '30 gm', '1', NULL, 'this is a unique triple-combination topical therapy for the short-term (8-week ) treatment of the dark spots associated with moderate-to-severe facial melasma. 1.fluocinolone acetonide 0.01% is a mild corticosteroid that reduces inflammation. 2.hydroquino', NULL, NULL, 809, '2022-07-30', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6559, 'Carnitozala for men 30 hard gelatin capsules', 'كارنيتوزالا للرجال 30 كبسولة', '585', NULL, 'Multivitamin', 'L- carnitine+l-arginine+coenzyme q_10+n_ acetyle+vitamin e+vitamin c+zinc+vitamin b6+vitamin b3+grabed bextract+pantothenic acid+lycopene+beta_carotene+vitamin b2+thiamine+manganese+folate+copper+benfotamin+vitamin b12+biotin+iodine+selenium+chromium+vitamin k+molybdneum+vitamin d3', 'Health&wellness', 'Capsule', 'كبسولة', NULL, '3', NULL, NULL, NULL, NULL, 2261, '2022-12-15', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18146, 'Eldopaque 2% sunblock cream 14.18 gm', 'الدوباك 2% كريم واقي من الشمس 14.18 جم', '20', NULL, 'Whitening topical', 'Hydroquinone', 'Icn pharmaceutical inc u.s.a > eimc', 'Cream', 'كريم', '2%', '1', NULL, NULL, NULL, NULL, 849, '2022-07-13', 'Hydroquinone is used as an OTC topical lightening agent for disorders of hyperpigmentation including melasma, post-inflammatory hyperpigmention, sunspots and freckles. ', 'Hydroquinone reduces melanin pigment production through inhibition of the tyrosinase enzyme, which is involved in the initial step of the melanin pigment biosynthesis pathway. Hydroquinone takes several months to take effect. ', NULL, 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13102, 'Randil 20mg 30 tab', 'رانديل 20مجم 30 قرص', '114', '82.5', 'Anti-ischemic', 'Nicorandil', 'Adwia', 'Tab', 'أقراص', '20mg', '3', 'مضاد للذبحة الصدرية . موسع للأوعية الدموية" يستخدم فى حالات قصور القلب الحاد و الذبحة الصدرية المستقرة المزمنة والذبحة', NULL, '6223002977987', NULL, 6254, '2024-10-21', 'Indicated for the prevention and treatment of chronic stable angina pectoris and reduction in the risk of acute coronary syndromes. ', 'Nicorandil mediates its therapeutic efficacy via two main mechanisms. Nicorandil is an activator and opener of ATP-sensitive (ATP-dependent) potassium channels (KATP channels) that are composed of Kir6.x-type subunits and sulfonylurea receptor (SUR) subunits. Nicorandil binding sites are located in the sulfonylurea receptor 2 (SUR2) in the ATP-sensitive potassium channel [A20322], which are regulatory subunits of the channel that exhibit an ATPase activitiy [A12436]. There are 2 types of SUR2 subunits (2A/2B) that have identical nucleotide binding domains (NBD), where SUR2A is more predominantly expressed in skeletal and cardiac myocytes and SUR2B in smooth muscle cells [A12436]. Nicorandil more potently activates SUR2B/Kir6.2 than SUR2A/Kir6.2 channels to cause hyperpolarization. ATP-NBD1 interaction influences the channel signalling by nicorandil, and the response of the channel to nicorandil is also facilitated and heightened by the interaction of ATP or ADP with NBD2 [A20321]. Potentiated activity of ATP-sensitive channels have cardioprotective role by limiting the duration of action potentials and preventing intraceullar calcium overload [A20326]. This attenuates cellular injury by preserving cellular energetics and ultimately cell survival [A20324]. KATP channel-dependent membrane hyperpolarization can also lead to vasodilation via reduction in Ca2+ influx through the voltage-gated Ca2+ channels and regulation of intracellular Ca2+ mobilization in smooth muscle cells [A20324]. 

Nicorandil contain a nitrate moiety in its structure, making it a good dilator of vascular smooth muscle like other nitroglycerin esters [A20323]. Direct relaxation of venous vascular system arises from NO-donor mediated stimulation of guanylyl cyclase and increased levels of intracellular cyclic GMP (cGMP). Elevated levels of cGMP contributes to the total relaxing effect of nicorandil at higher concentrations of the drug [A7839]. ', 'Nicorandil is a potassium channel opener with nitrovasodilator (NO donor) actions, making it both an arterial and a venous dilator [T28]. It causes sustained dilation of both the arterial resistance and conductive vessels that increases coronary blood flow, however the effect of the drug on coronary arteries does not involve the coronary steal phenomenon [L887]. Activation of potassium channels lead to hyperpolarization of the smooth muscle cells, followed by arterial dilation and afterload reduction. Nicorandil is shown to increase pooling in the capacitance vessels with a decrease in preload through relaxing the venous vascular system. Overall, improved blood flow and reduced infarct size are achieved through reduction of end-diastolix pressure and decreased extravascular component of vascular resistance [L887]. Open studies showed the effectiveness of nicorandil treatment on various types of angina pectoris [A20327]. ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (3766, 'Divakote 500mg 20 e.r. tab.', 'ديفاكوت 500 مجم ممتد المفعول 20 قرص', '106', '79', 'Anticonvulsant', 'Divalproex sodium', 'Mepaco > andalous pharma', 'Tab', 'أقراص', '500mg', '2', 'مضاد للتشنجات -- لعلاج حالات الصرع والتشنجات العصبية -- مهديء عصبي', 'indication for treatment and management of seizure disorders mania and prophylactic treatment of migraine headache. in epileptics valproic acid is used to control absence seizures tonic-clonic seizures (grand mal) complex partial seizures and the seizures', '6224000396190', NULL, 7222, '2024-08-14', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1327, 'B-com forte 30 f.c. tabs.', 'بي-كوم فورت 30 قرص', '15', NULL, 'Vitamin b', 'Vitamin b complex', 'Amoun', 'Tab', 'أقراص', NULL, '3', 'فيتامين ب المركب', 'mechanism of action of biotin biotin excerts it`s pharmacological action by influencing metabolic reactions. it acts as a coenzyme for carbohydrate fat and protein metabolism. it is required for cell growth and production of fatty acids. it is required fo', '6221025019233', NULL, 4863, '2022-12-03', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27138, 'Sodium chloride 0.18% & dextrose 4.3% (otsuka ateco) i.v. inf. 500 ml', 'صوديوم كلوريد 0.18% و دكستروز 4.3% (اوتسوكا اتيكو) محلول وريدي 500 مل', '11', NULL, 'Sterile solution', 'Glucose (dextrose)+sodium chloride', 'El nasr', 'Unknown', 'غير محدد', '0.18%', '1', NULL, 'about dextrose dextrorotatory form of monosaccharide(((glucose))) in hypoglycemia and as a sweetner. mechanism of action of dextrose dextrose is used as an intravenous (((iv))) solution used to supply water and calories to the body. it is also used as dil', NULL, NULL, 784, '2022-07-24', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28769, 'Unitone 4 reveal gel 150ml', 'يونيتون 4 ريفيل جل 150 مل', '169', NULL, 'Skin care', NULL, 'Isis pharma > biotech egypt', 'Gel', 'جل', '150ml', '1', NULL, '*indications it is a cleansing and exfoliating gel formulated with a complex of 4 ingredients. *use it should be applied morning and evening to a slightly moisturised face using circular movements. create lather by gently massaging the gel on the skin for', NULL, NULL, 664, '2022-08-14', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27066, 'Sk albumin 20% (100ml) vial', 'اس كيه البيومين 20% فيال 100 مل', '255', NULL, 'Hypoalbuminemia', 'Human albumin', 'Sk chemicals co korea. > faripharma medical co-egypt', 'Vial', 'فيال', '20%', '1', NULL, NULL, NULL, NULL, 1210, '2022-06-16', 'Human immunoglobulin G is indicated for the following conditions:



### Primary Immunodeficiency

- for the treatment of primary immunodeficiency in adult and pediatric patients[L39920, L39930, L39935, L39940, L39945, L39950, L39955, L39960, L39965, L39975, L39980, L40029, L40034, L40044]

- in combination with [hyaluronidase (human recombinant)] for the treatment of primary immunodeficiency in patients ≥2 years of age.[L42760]



### Immune Thrombocytopenic Purpura (ITP)

- for the treatment of acute or chronic immune thrombocytopenic purpura in adult and pediatric patients[L39940, L39950, L39960, L39965, L39970, L39975, L39980]



### Chronic Inflammatory Demyelinating Polyneuropathy (CIDP)

- for the treatment of CIDP in adult patients[L39955, L39965, L39975, L39980, L40034]

- in combination with hyaluronidase (human recombinant) as a maintenance therapy in adults with chronic inflammatory demyelinating polyneuropathy (CIDP)[L40039]



### Multifocal Motor Neuropathy (MMN)

- for maintenance therapy to improve muscle strength and disability in adult patients with MMN[L39955]



### Prophylaxis of Bacterial Infection

- for the prevention of bacterial infections in patients with hypogammaglobulinemia and/or B-cell chronic lymphocytic leukemia[L39960]



### Coronary Artery Aneurysm Associated With Kawasaki Syndrome

- for the prevention of coronary artery aneurysms in pediatric patients with Kawasaki syndrome[L39960]



### Dermatomyositis

- for the treatment of dermatomyositis in adult patients[L39970]

', 'IVIg interacts with a number of different components of the immune system, including cytokines, complement, Fc receptors and several cell surface immunocompetent molecules. IVIg also impacts different effector cells of the immune system (B and T lymphocytes, dendritic cells, etc.) and regulates a wide range of genes. Its main mechanism of actions are believed to be Fc-dependent and F(ab'')2-dependent. IVIg competitively blocks gamma Fc receptors, preventing the binding and ingestion of phagocytes and suppressing platelet depletion. IVIg contains a number of different antobodies, which prevent infection by attaching to the surface of invading pathogens and aiding in their disposal before they can infect cells. Antibodies remove pathogens via complement activation, agglutination or precipitation, pathogen receptor blocking, macrophage “tagging” or neutralization (via binding) of pathogen toxins. Intact IVIg and F(ab′)2 fragments of IVIg can also neutralize the activity of various autoantibodies. By triggering the production of interleukin-1 receptor antagonist, IVIg modulates of the production of cytokines and cytokine antagonists. It also prevents the generation of the C5b-9 membrane attack complex and subsequent complement-mediated tissue damage by binding active complement components.', 'Used as a replacement therapy in inherited humoral immunodeficiency disorders such as severe combined immunodeficiency syndrome, x-linked agammaglobulinemia, and Wiskott-Aldrich Syndrome. The immunoglobulins target, bind and kill bacterial cells as well as viral particles. IgG is the monomeric immunoglobulin of which there are four subclasses (IgG1, IgG2, IgG3 and IgG4) in differing abundances (66%, 23%, 7% and 4%). IgAs represent about 15% of the immunoglobulins in the blood. These target inhaled or ingested pathogens.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6616, 'Gastrocure 30mg 10 caps.', 'جاستروكيور 30مجم 10 كبسولات', '44', NULL, 'Peptic ulcer.proton pump inhibitor', 'Lansoprazole', 'Alfacure pharmaceuticals', 'Cap', 'كبسولة', '30mg', '1', NULL, NULL, NULL, NULL, 791, '2025-08-08', 'Lansoprazole is used to reduce gastric acid secretion and is approved for short term treatment of active gastric ulcers, active duodenal ulcers, erosive reflux oesophagitis, symptomatic gastroesophageal reflux disease, and non-steroidal anti-inflammatory drug (NSAID) induced gastric and duodenal ulcers. [A4892][A177065][FDA Label]  It may be used in the maintenance and healing of several gastric conditions including duodenal ulcers, NSAID related gastric ulcers, and erosive esophagitis.[FDA Label] Lansoprazole prevents recurrence of gastric ulcers in patients who have a documented history of gastric ulcers who also use NSAIDs chronically. [FDA Label]  Predictably, it is also useful in the management of hypersecretory conditions including Zollinger-Ellison syndrome. [FDA Label]  Lansoprazole is effective at eradicating H. pylori when used in conjunction with amoxicillin and clarithromycin (triple therapy) or with amoxicillin alone (dual therapy). [FDA Label]', 'As a PPI, lansoprazole is a prodrug and requires protonation via an acidic environment to become activated. [A174244]  Once protonated, lansoprazole is able to react with cysteine residues, specifically Cys813 and Cys321, on parietal H+,K+-ATPase resulting in stable disulfides.[A174244][A177077]  PPI''s in general are able to provide prolonged inhibition of acid secretion due to their ability to bind covalently to their targets.[A174244]   ', 'Lansoprazole decreases gastric acid secretion by targeting H+,K+-ATPase, which is the enzyme that catalyzes the final step in the acid secretion pathway in parietal cells. [A177080]  Conveniently, lansoprazole administered any time of day is able to inhibit both daytime and nocturnal acid secretion.[A177080]  The result is that lansoprazole is effective at healing duodenal ulcers, reduces ulcer-related pain, and offers relief from symptoms of heartburn [A177080]  Lansoprazole also reduces pepsin secretion, making it a useful treatment option for hypersecretory conditions such as Zollinger-Ellison syndrome.[A177089][A177080]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (3123, 'Caltonex 20 tabs.', 'كالتونيكس 30 قرص', '84', '68', 'Calcium supplement', 'Calcium+vitamin d3+magnesium+zinc', '> integra pharm', 'Tab', 'أقراص', NULL, '2', 'مكمل غذائى لدعم نمو العظام والوقاية من الكساح وهشاشة العظام وتخفيف توتر العضلات', NULL, '6224009369362', NULL, 10551, '2024-06-21', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13944, 'Amiparen 10% (500ml) i.v. inf.', 'اميبارين 10% 500 مل محلول وريدي', '100', NULL, 'Parenteral solutions', 'Amino acids', 'Otsuka', 'Unknown', 'غير محدد', '10%', '1', NULL, NULL, NULL, NULL, 1556, '2022-07-09', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20327, 'Metanium everyday spray lotion 60 ml', 'ميتانيوم سبراي لوشن 60 مل', '300', NULL, 'Skin care', NULL, 'Stada', 'Lotion', 'لوشن', '60 ml', '1', NULL, NULL, NULL, NULL, 733, '2023-01-02', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20950, 'Irbesartan 300mg 7 f.c. tab.', 'اربيسارتان 300مجم 7 اقراص', '12', NULL, 'Antihypertensive.angiotensin blocker', 'Irbesartan', 'Mepaco', 'Tab', 'أقراص', '300mg', '1', NULL, 'about irbesartan angiotensin ii receptor blocker a non-peptide compound antihypertensive. mechanism of action of irbesartan it is a competitive antagonist of angiotensin-2 at at-1 receptor. it decreases peripheral resistance and lowers bp in hypertensive', '6223002213436', NULL, 1871, '2023-02-06', 'Irbesartan is indicated to treat hypertension and diabetic nephropathy in hypertensive patients with type 2 diabetes, elevated serum creatinine, and proteinuria.[L7456] A combination product with hydrochlorothiazide is indicated for hypertension in patients with uncontrolled hypertension with monotherapy or first line in patients not expected to be well controlled with monotherapy.[L7459]', 'Irbesartan prevents angiotensin II binding to the AT<sub>1</sub> receptor in tissues like vascular smooth muscle and the adrenal gland.[L7456,L7459] Irbesartan and its active metabolite bind the AT<sub>1</sub> receptor with 8500 times more affinity than they bind to the AT<sub>2</sub> receptor.[L7456,L7459] Irbesartan''s prevention of angiotensin II binding causes vascular smooth muscle relaxation and prevents the secretion of aldosterone, lowering blood pressure.[L7456,L7459]



Angiotensin II would otherwise bind to the AT<sub>1</sub> receptor, inducing vasoconstriction and aldosterone secretion, raising blood pressure.[L7456,L7459]', 'Irbesartan is an angiotensin receptor blocker used to treat hypertension and diabetic nephropathy.[L7456,L7459] It has a long duration of action as it is usually taken once daily and a wide therapeutic index as doses may be as low as 150mg daily but doses of 900mg/day were well tolerated in healthy human subjects.[L7456,L7459,A181165]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6260, 'Limitless kids multivitamins syrup 250 ml', 'ليميتلس كيدز مالتي فيتامينز شراب 250 مل', '180', '157', 'Multivitamin', NULL, 'EVA PHARMA', 'Syrup', 'شراب', '250 ml', '1', NULL, NULL, '6223012480033', NULL, 1414, '2025-03-06', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (7225, 'Leukeran 2mg 25 f.c. tabs.', 'لوكيران 2مجم 25 قرص', '680', '66', 'Antineoplastic', 'Chlorambucil', 'Aspen pharma trading limited > egyptian pharmaceutical trading company', 'Tab', 'أقراص', '2mg', '1', 'لعلاج الاورام', NULL, '5010706009455', NULL, 6094, '2024-12-07', 'For treatment of chronic lymphatic (lymphocytic) leukemia,  childhood minimal-change nephrotic syndrome, and malignant lymphomas including lymphosarcoma, giant follicular lymphoma, Hodgkin''s disease,  non-Hodgkin''s lymphomas, and Waldenström’s Macroglobulinemia.', 'Alkylating agents work by three different mechanisms: 1) attachment of alkyl groups to DNA bases, resulting in the DNA being fragmented by repair enzymes in their attempts to replace the alkylated bases, preventing DNA synthesis and RNA transcription from the affected DNA, 2) DNA damage via the formation of cross-links (bonds between atoms in the DNA) which prevents DNA from being separated for synthesis or transcription, and 3) the induction of mispairing of the nucleotides leading to mutations.', 'Chlorambucil is an antineoplastic in the class of alkylating agents that is used to treat various forms of cancer. Alkylating agents are so named because of their ability to add alkyl groups to many electronegative groups under conditions present in cells. They stop tumor growth by cross-linking guanine bases in DNA double-helix strands - directly attacking DNA. This makes the strands unable to uncoil and separate. As this is necessary in DNA replication, the cells can no longer divide. In addition, these drugs add methyl or other alkyl groups onto molecules where they do not belong which in turn inhibits their correct utilization by base pairing and causes a miscoding of DNA. Alkylating agents are cell cycle-nonspecific. Alkylating agents work by three different mechanisms all of which achieve the same end result - disruption of DNA function and cell death.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (8394, 'Septola lozenges 24 tab honey and lemon', 'سبتولا استحلاب 24 قرص عسل وليمون', '75', NULL, 'Dietary supplement', 'Vitamin c', 'Ep pharma', 'Tab', 'أقراص', NULL, '1', NULL, NULL, NULL, NULL, 628, '2023-01-07', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15387, 'Rutin seba-c 20 tabs', 'روتين سيبا سي 20 قرص', '95', '85', 'Anti-oxidant', 'Rutin+vitamin c+citrus bioflavonoids', 'Elsebaeypharma', 'Tab', 'أقراص', NULL, '2', '"* النزيف بعد الولادة والإجهاض المتكرر و حالات الدورة الغزيرة', NULL, '6225000481541', NULL, 2475, '2025-08-08', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14159, 'Angikinase 500 000 i.u. vial', 'انجيكيناز 500000 وحدة دولية فيال', '630', NULL, 'Thrombolytic agent', 'Urokinase', 'Sedico', 'Vial', 'فيال', NULL, '1', NULL, 'about urokinase a protease enzyme a plasminogen activator a thrombolytic agent. mechanism of action of urokinase it exerts it`s fibrinolytic action by activating natural fibrinolytic system. it causes lysis of thrombus or clot and thus recanalyses occlude', NULL, NULL, 804, '2022-07-24', 'In Canada, urokinase is indicated for lysis of acute massive pulmonary emboli, acute thrombi obstructing coronary arteries, occlusive thromboemboli in peripheral arteries and grafts, and restoration of patency to intravenous catheters.[L12141]', 'Urokinase is a serine protease.[A191928] It cleaves plasminogen to form the active fibrinolytic protease, plasmin.[A191928]', 'Urokinase is a serine protease that activates plasminogen to an active fibrinolytic protease.[A191928] The duration of action is short due to the short half life.[L12138] Patients should be counselled regarding the risk of bleeding, anaphylaxis, infusion reactions, and cholesterol embolization.[L12138', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20775, 'Indest susp. 100ml', 'انديست شراب معلق 100 مل', '3', NULL, 'Antiflatulent+antiemetic', 'Dimethicon+metoclopramide', 'Alfacure pharmaceuticals', 'Susp', 'معلق', '100ml', '1', 'مضاد للانتفاخات والقئ ويعمل على دعم الجهاز الهضمى', 'about dimethicone oral anti -foaming agent a flexible silicone polymer antiflatulent antibloating agent. about metoclopramide it is a dopamine antagonist - antiemetic gastroprokinetic agent. mechanism of action of metoclopramide metoclopramide acts throug', NULL, NULL, 1138, '2022-07-08', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1002, 'Pimfast 0.5 gm vial', 'بيمفاست 0.5جم فيال', '44', NULL, 'Antibiotic.cephalosporin.fourth-generation', 'Cefepime', 'Rameda', 'Vial', 'فيال', '0.5 gm', '1', NULL, NULL, '6223003974671', NULL, 660, '2024-12-12', 'Cefepime is indicated for the treatment of pneumonia caused by susceptible bacteria, and for empiric therapy for febrile neutropenic patients. Cefepime is also indicated for the treatment of uncomplicated and complicated urinary tract infections (cUTI) including pyelonephritis, uncomplicated skin and skin structure infections, and complicated intra-abdominal infections (used in combination with [metronidazole]) in adults caused by susceptible bacteria.[L42095,L42100]



Cefepime is also used in combination with [enmetazobactam] to treat cUTI.[L50117]', 'Cefepime is a bactericidal cephalosporin with a mode of action similar to other beta-lactam antibiotics.[L42095,L42100] Cefepime disrupts bacterial cell walls by binding and inhibiting transpeptidases known as penicillin-binding proteins (PBPs), which are enzymes involved in the final stages of peptidoglycan layer synthesis. This results in the lysis and death of susceptible microorganisms.[A249050] Cefepime has a broad spectrum of _in vitro_ activity that includes both Gram-positive and Gram-negative bacteria.[L42095,L42100] Cefepime has affinity for PBP-3 and PBP-1 in _Escherichia coli_ and _Pseudomonas aeruginosa_, as well as PBP-2 in _E. coli_ and _Enterobacter cloacae_.[A249050]', 'Cefepime is a fourth-generation cephalosporin antibiotic.[L42095,L42100] It is active against Gram-negative bacteria such as _Enterobacter_ spp., _Escherichia coli_, _Klebsiella pneumoniae_, _Proteus mirabilis_ and _Pseudomonas aeruginosa_, and Gram-positive bacteria such as _Staphylococcus aureus_ (methicillin-susceptible isolates only), _Streptococcus pneumoniae_, _Streptococcus pyogenes_ and Viridans group streptococci.[L42095,L42100] Compared to third-generation cephalosporins, cefepime has an extended Gram-negative coverage. Whereas other cephalosporins are degraded by plasmid- and chromosome-mediated beta-lactamases, cefepime is stable and not significantly hydrolyzed by these enzymes.[A2457,A249050] Cefepime is also a poor inducer of type 1 beta-lactamases and, therefore, a good alternative against bacteria resistant to third-generation cephalosporins.[A2457] 



In animal models of infection, the time that the unbound plasma concentration of cefepime exceeds the minimum inhibitory concentration (MIC) of infecting organisms has been shown to correlate with treatment efficacy.[L42095,L42100] It has been suggested that cefepime can cross the inflamed blood-brain barrier.[L42095,L42100] This, along with its ability to inhibit γ-aminobutyric acid (GABA), could lead to the neurotoxic effects observed in some of the patients treated with cefepime.[A249045,A249050]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22089, 'L-theanine 200 mg 60 caps. (illegal import)', 'ال-ثيانين 200مجم 60كبسول', '0', NULL, NULL, 'L-theanine', 'Puritans pride', 'Cap', 'كبسولة', '200 mg', '1', NULL, 'as a natural phytochemical found in japanese green tea l-theanine is a non-essential amino acid that is present in the brain.** a close relative of glutamate studies indicate that l-theanine interacts with the neurotransmitter gaba (gammaminobutyrate).**', NULL, NULL, 792, '2022-08-21', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19397, 'Free nose nasal spray 30 ml (6+ years)', 'فري نوز بخاخ للاطفال 30 مل (+6 سنوات)', '75', NULL, 'Nasal decongestant', 'Sea water+purified water', 'Pharmalink s.l. > aug pharma', 'Spray', 'بخاخ', '30 ml', '1', NULL, 'nasal decongestant spray for 6+ years', NULL, NULL, 1006, '2022-07-14', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22951, 'Montelosab 5 mg 7 chewable tab.', 'مونتيلوساب 5مجم 7 اقراص', '12', NULL, 'Asthma.anti-inflammatory.leukotriene antagonist', 'Montelukast', 'Sabaa', 'Tab', 'أقراص', '5 mg', '1', NULL, 'about montelukast leukotriene(cyslt1) receptor antagonist antiinflammatory antiasthma. mechanism of action of montelukast montelukast is competitively and selectively antagonizes cyslt1 receptor (leukotrienes receptor) mediated bronchoconstriction.montelu', NULL, NULL, 1046, '2022-07-10', 'Montelukast is indicated for:



(a) the prophylaxis and chronic treatment of asthma in adults and pediatric patients who are 12 months of age and older[L6301], although other regional health authorities specifically note this indication for adults and adolescents who are 15 years and older[L6304,L6307] and also include indications for preventing day and night-time symptoms, and the treatment of acetylsalicylic acid-sensitive asthma[L6304];



(b) the prevention of exercise-induced bronchoconstriction (EIB) in patients who are 6 years of age and older[L6301], although other regional health authorities specifically note this indication for adults and adolescents who are 15 years and older[L6304,L6307]; and



(c) the relief of symptoms of seasonal allergic rhinitis in patients 2 years of age and older and perennial allergic rhinitis in patients 6 months of age and older[L6301], although other regional health authorities specifically note the relief of seasonal allergic rhinitis symptoms for adults and adolescents who are 15 years and older[L6304,L6307].



Furthermore, some formulations like chewable montelukast tablets may also be specifically indicated by particular regulatory bodies for the prophylaxis and chronic treatment of asthma, including the prevention of day and night-time symptoms, the treatment of acetylsalicylic acid based asthma, and the prevention of exercise-induced bronchoconstriction in adult and pediatric patients aged 2 and older[L6328], between the ages 2 and 5[L6325], or between the ages of 6 and 14 years.[L6331]



Moreover, when employed for such indications montelukast is considered effective as monotherapy or when combined with other medications indicated for the maintenance treatment of chronic asthma.[L6304,L6328] For instance, montelukast and inhaled corticosteroids can be used concomitantly to demonstrate additive effects to control asthma or to decrease the necessary inhaled corticosteroid dose while still maintaining clinical stability.[L6304,L6328]



Additionally, in patients who continue to experience asthma symptoms, montelukast can also be combined with an ''as required'' short-acting beta-agonist, an inhaled corticosteroid, or inhaled corticosteroid paired with a long-acting beta-agonist.[L6304,L6328]', 'Cysteinyl leukotrienes (CysLT) like LTC4, LTD4, and LTE4, among others, are eicosanoids released by a variety of cells like mast cells and eosinophils.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] When such CysLT bind to corresponding CysLT receptors like CysLT type-1 receptors located on respiratory airway smooth muscle cells, airway macrophages, and on various pro-inflammatory cells like eosinophils and some specific myeloid stem cells activities that facilitate the pathophysiology of asthma and allergic rhinitis are stimulated.[L6301,L6304,L6307,L6310,L6325,L6328,L6331]



In particular, CysLT-mediated airway bronchoconstriction, occluding mucous secretion, vascular permeability, and eosinophil recruitment are all types of effects that facilitate asthma.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] Alternatively, in allergic rhinitis, CysLTs are released by the nasal mucosa when exposed to allergens during both early and late phase reactions and participate in eliciting symptoms of allergic rhinitis like a congested nose and airway.[L6301,L6304,L6307,L6310,L6325,L6328,L6331]



Subsequently, montelukast is a leukotriene receptor antagonist that binds with high affinity and selectivity to the CysLT type 1 receptor, which consequently assists in inhibiting any physiological actions of CysLTs like LTC4, LTD4, and LTE4 at the receptor that may facilitate asthma or allergic rhinitis.[L6301,L6304,L6307,L6310,L6325,L6328,L6331]', 'Montelukast is a leukotriene receptor antagonist that demonstrates a marked affinity and selectivity to the cysteinyl leukotriene receptor type-1 in preference to many other crucial airway receptors like the prostanoid, cholinergic, or beta-adrenergic receptors.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] As a consequence, the agent can elicit substantial blockage of LTD4 leukotriene-mediated bronchoconstriction with doses as low as 5 mg.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] Moreover, a placebo-controlled, crossover study (n=12) demonstrated that montelukast is capable of inhibiting early and late phase bronchoconstriction caused by antigen challenge by 75% and 57% respectively.[L6301,L6304,L6307,L6310,L6325,L6328,L6331]



In particular, it has been documented that montelukast can cause bronchodilation as soon as within 2 hours of oral administration.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] This action can also be additive to the bronchodilation caused by the concomitant use of a beta agonist.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] Nevertheless, clinical investigations performed with adults 15 years of age and older revealed that no additional clinical benefit is obtained when doses of montelukast greater than 10 mg a day are used.[L6301,L6304,L6307,L6310,L6325,L6328,L6331]



Additionally, in clinical trials with adults and pediatric asthmatic patients aged 6 to 14 years, it was also determined that montelukast can reduce mean peripheral blood eosinophils by about 13% to 15% from baseline in comparison to placebo during double-blind treatment periods.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] At the same time, in patients aged 15 years and older who were experiencing seasonal allergic rhinitis, the use of montelukast caused a median reduction of 13% in peripheral blood eosinophil counts when compared to placebo as well.[L6301,L6304,L6307,L6310,L6325,L6328,L6331]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29595, 'Vorox 10 sachets', 'فوروكس 10 اكياس', '65', NULL, 'Iron supplement', 'Lactoferrin+ferric pyrophosphate+zinc+folic acid', 'Medcare > abbott laboratories', 'Sachet', 'أكياس', NULL, '1', 'علاج الانيميا ونقص الحديد -- تقوية المناعة لمقاومة الامراض والفيروسات', 'lactoferrin 100 mg', NULL, NULL, 1147, '2022-05-13', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20628, 'Hykomap rectal oint. 15 gm', 'هيكوماب مرهم 15 جم', '13', NULL, 'Haemorrhoids', 'Cinchocaine+policresulen', 'Multi-apex', 'Oint', 'مرهم', '15 gm', '1', NULL, 'about cinchocaine an amide derivative most potent and toxic long-acting local anesthetics a local anaesthetic mechanism of action of cinchocaine cinchocaine is a local anesthetic. it works by blocking the pathway of pain signals along nerves. it inhibits', '6223003205218', NULL, 2565, '2022-07-11', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6965, 'Tiratam 500mg 30 f.c. tablets', 'تيراتام 500مجم 30 قرص', '222', '165', 'Anti-epileptic.pyrrolidine', 'Levetiracetam', 'Sigma > andalous pharma', 'Tablet', 'أقراص', '500mg', '3', NULL, NULL, '6223004691416', NULL, 3216, '2024-06-14', 'Levetiracetam is indicated as an adjunctive therapy in the treatment of partial onset seizures in epileptic patients who are one month of age and older. Additionally, it is indicated as an adjunct in the treatment of myoclonic seizures in patients with juvenile myoclonic epilepsy who are 12 years of age and older, and in primary generalized tonic-clonic seizures in patients with idiopathic generalized epilepsy who are 6 years of age and older.[L8606]



Levetiracetam is also available as an orally dissolvable tablet that is indicated as an adjunct in the treatment of partial onset seizures in patients with epilepsy who are 4 years of age and older and weigh more than 20kg.[L8609]', 'The exact mechanism through which levetiracetam exerts its anti-epileptic effects is unclear, but is thought to be unique amongst other anti-epileptic medications. Current knowledge suggests that levetiracetam’s binding to synaptic vesicle protein 2A (SV2A) is a key driver of its action. SV2A is a membrane-bound protein that is found on synaptic vesicles and is ubiquitous throughout the CNS[A12546] - it appears to play a role in vesicle exocytosis[L8606,L8615] and in the modulation of synaptic transmission by increasing the available amount of secretory vesicles available for neurotransmission.[A12552] Stimulation of pre-synaptic SV2A by levetiracetam may inhibit neurotransmitter release,[A12551] but this action does not appear to affect normal neurotransmission. This has led to the suggestion that levetiracetam exclusively modulates the function of SV2A only under pathophysiological conditions.[A12546] Levetiracetam and related analogues showed a correlation between affinity for SV2A and anti-epileptic potency, further suggesting that action at this site contributes to the anti-epileptic activity of the drug.[L8606,L8615]



Levetiracetam has also been shown to indirectly affect GABAergic neurotransmission (despite having no direct effect on GABAergic or glutamatergic receptors) and modulate ionic currents.[A12552] Similarly, levetiracetam has been shown in vitro to inhibit N-type calcium channels.[A16562] How, or even if, these actions are implicated in its anti-epileptic action have yet to be elucidated.', 'Levetiracetam appears to prevent seizure activity via the selective inhibition of hypersynchronized epileptiform burst firing without affecting normal neuronal transmission, though the exact mechanism through which this occurs is unclear.[L8606,L8615] The therapeutic index of levetiracetam is wide,[L8615,A185918] making it relatively unique amongst other anti-epileptic medications.



Anti-epileptic drugs, including levetiracetam, may increase the risk of suicidal ideation or behaviour - patients taking levetiracetam should be monitored for the emergence or worsening of depressive symptoms, suicidal ideation, and behavioural abnormalities.[L8606,L8600,L8615]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (7663, 'Traka omega 20 cap soft gelatin', 'تراكا اوميجا 20 كبسولة', '150', NULL, 'Multivitamin', 'Fish oil+vitd', 'Three med pharma', 'Cap', 'كبسولة', NULL, '2', NULL, NULL, NULL, NULL, 676, '2025-10-13', 'Under FDA approval, fish oil pharmaceuticals are typically products consisting of a combination of the omega-3-fatty acids eicosapentaenoic acid (EPA) and docosahexaenoic acid (DHA) and are indicated primarily as an adjunct to diet to reduce triglyceride levels in adult patients with severe (>=500 mg/dL) hypertriglyceridemia [FDA Label, F36].



Under EMA approval, such fish oil pharmaceuticals comprised of virtually the same fish and fish oil derived omega-3-fatty acids EPA and DHA are indicated specifically for (a) adjuvant treatment in secondary prevention after myocardial infarction, in addition to other standard therapy (ie. statins, antiplatelet medicinal products, beta blockers, ACE inhibitors), and (b) as a supplement to diet when dietary measures alone are insufficient to produce an adequate response, particularly with type IV hypertriglyceridemia in monotherapy or type IIb/III in combination with statins, when control of triglycerides is insufficient [L2661]. In addition, prescribing information for EMA approved fish oil pharmaceuticals are also indicated as an adjunct to diet to reduce very high (>=500 mg/dL) triglyceride levels in adult patients, much like similar FDA approved indications [F37, FDA Label].', 'The specific mechanism of action by which the fish oil EPA and DHA acids are capable of reducing serum triglyceride levels is not yet fully understood [FDA Label, A32933]. Nevertheless, it is proposed that such omega-3-fatty acids may not be the preferred substrates of the enzyme diacylglycerol O-acyltransferase that participates in the generation of triglycerides; that  they might interact with nuclear transcription factors that manage lipogenesis; or that their presence and increase in levels can cause cellular metabolism to subsequently shift toward a decrease in triglyceride synthesis and an increase in fatty acid oxidation [A32933]. Moreover, the EPA and DHA acids are also believed to be able to promote apolipoprotein B degradation in the liver through the stimulation of an autophagic process [A32933]. It may also be possible that these fish oil acids can accelerate the clearance of very-low-density lipoprotein (VLDL) particles and chylomicron [A32933]. The combination of all these actions results in fewer VLDL particles being assembled and secreted, which is of considerable importance as VLDL particles are the major endogenous source of triglycerides [A32933].



Moreover, new paradigms of how inflammation is contained and dissipated involve various newly discovered chemical mediators, resolvins, and protectins [A32933]. Such agents are believed to be directly involved in blocking neutrophil migration, infiltration, recruitment, as well as blocking T-cell migration and promoting T-cell apoptosis [A32933]. Additionally, such protectins can also reduce tumor necrosis factor and interferon secretion [A32933]. Of particular importance, however, is the fact that protectins and resolvins are exclusively derived from omega-3-fatty acids and that EPA is the substrate of the resolvins family and DHA can be converted to both resolvins and protectins [A32933]. It is believed that these effects of such fish oil acids underlie the actions that fish oil have demonstrated on eliciting stability for vulnerable inflammatory plaques [A32933].



Finally, fish oil acids have demonstrated certain direct electrophysiological effects on the myocardium [A32933]. In animal studies, it was shown that the ventricular fibrillation threshold could be increased in both animals fed or infused with omega-3-fatty acids [A32933]. Further studies subsequently revealed that such fatty acids could reduce both sodium currents and L-type calcium currents on a cellular and ion channel level [A32933]. It is consequently hypothesized that during ischemia, a reduction in the sodium ion current protects hyperexcitable tissue, and a reduction in the calcium ion current could reduce arrhythmogenic depolarizing currents - and that perhaps the use of EPA and DHA fish oil acids could facilitate such activity [A32933]. For the time being, however, omega-3-fatty acids in pharmaceutical supplement form have not been shown to elicit such protection against heart conditions [L2662].', 'In general, the only practical method of obtaining and increasing the levels of eicosapentaenoic acid (EPA) and docosahexaenoic acid (DHA) fish oil omega-3-fatty acids in the body is to consume them directly from foods and/or dietary supplements [L784]. Having EPA and DHA is important because they facilitate numerous important functions in the human body. As an example, such supplementation of fish oil EPA and DHA demonstrates a legitimate ability to decrease triglyceride levels as their presence in the body can act as poor substrates for the enzymes that are ordinarily responsible for triglyceride synthesis and also inhibit the esterification of other fatty acids, among other mechanisms [FDA Label, L2661].



Moreover, such fish oil acids can become important components of the phospholipids that form the structures of cell membranes [L784]. Specifically, DHA is particularly high in the retina, brain, and sperm [L784]. Additionally, these acids also provide energy for the body and are used to form eicosanoids - signaling molecules that have similar chemical structures to the fish oil fatty acids from which they are derived [L784]. Furthermore, such eicosanoids possess wide-ranging functions in the cardiovascular, pulmonary, immune, and endocrine systems [L784].', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4541, 'Exforge hct 5/160/12.5mg 14 f.c. tab', 'اكسفورج اتش سي تي 5/ 160/12.5مجم 14 قرص', '270', '166', 'Antihypertensive. combined angiotensin blocker with calcium channel blocker', 'Amlodipine+hydrochlorothiazide+valsartan', 'Novartis', 'Tab', 'أقراص', '12.5mg', '2', 'علاج ارتفاع ضغط الدم', NULL, '6223002642168', NULL, 5227, '2024-09-25', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30265, 'Capixy hair shampoo 250 ml', 'كابيكسي شامبو 250 مل', '275', '225', 'Hair care', 'Capixyl+glycerin+aloe vera+shea butter+caffeine+tocopherol acetate+polyquaternium-7+saw palmetto+coconut oil+argan oil', 'Nikim > majestic biopharma', 'Cap', 'كبسولة', '250 ml', '1', NULL, NULL, '6224010938106', NULL, 777, '2024-02-12', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16491, 'Clearogen foaming cleanser 125 ml (step 1)', 'كليروجين منظف ​​رغوي 125 مل', '308', NULL, 'Anti-acne', 'Sulfur', 'Vamer pharma', 'Cleanser', 'منظف', '125 ml', '1', NULL, 'effective cleanser with gentle foam which efficiently removes dead cells dirts excess oils & make-up.', '4565879331106', NULL, 720, '2022-08-01', 'Echocardiography: Sulfur hexafluoride is indicated for use in adult patients with suboptimal echocardiograms to opacify the left ventricular chamber and to improve the delineation of the left ventricularendocardial border. 

Ultrasonography of the Liver: Sulfur hexafluoride is indicated for use with ultrasound of the liver in adult and pediatric patients to characterize focal liver lesions.', 'Within the blood, the acoustic impedance of Lumason microspheres is lower than that of the surrounding non-aqueous tissue.  Therefore, an ultrasound beam is reflected from the interface between the microspheres and the surrounding tissue. The reflected ultrasound signal provides a visual image that shows a contrast between the blood and the surrounding tissues. ', 'Sulfur hexafluoride provides useful echocardiographic signal intensity for two minutes after the injection. Sulfur hexafluoride microspheres are destroyed and contrast enhancement decreases as the mechanical index increases (values of 0.8 or less are recommended). 

For ultrasonography of the liver, Sulfur hexafluoride provides dynamic patterns of differential signal intensity enhancement between focal liver lesions and liver parenchyma during the arterial, portal venous, and late phase of signal intensity enhancement of the microvasculature. ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (10268, 'Sekem baby calm herbs 15 filter bags', 'سيكم بيبي كالم 15 فلتر', '28', '25', 'Antispasmodic', 'Anise-caraway-chamomile-fennel fruit-liq', 'Atos pharma', 'Unknown', 'غير محدد', NULL, '1', 'اعشاب للرضع -- تخفيف تقلصات المعدة -- تخفيف الغازات والانتفاخات', NULL, '6223000471012', NULL, 1231, '2025-05-26', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (32463, 'Avano panthenol cream 30 gm', 'افانو بانثينول كريم 30 جم', '20', '20', 'Skin care.moisturizing', 'Panthenol', 'Avano pharma', 'Cream', 'كريم', '30 gm', '1', NULL, 'about panthenol alcohol analog of pantothenic acid (vitamin b5) and is thus a provitamin of b5. mechanism of action of panthenol skin: it binds to the hair shaft readily and it coats the hair and seals its surface lubricating the hair shaft and making str', NULL, NULL, 1019, '2025-02-01', 'Panthenol (containing a racemic mixture of dexpanthenol and levopanthenol) is not currently available in any FDA-approved products and therefore does not have an indication.



Please see [DB09357] for FDA-approved uses of the dextrorotatory form of Panthenol. ', 'Panthenol is an alcohol derivative of pantothenic acid, a component of the B complex vitamins and an essential component of a normally functioning epithelium. Dexpanthenol, the active form of panthenol, is enzymatically cleaved to form pantothenic acid (Vitamin B5), which is an essential component of Coenzyme A that acts as a cofactor in many enzymatic reactions that are important for protein metabolism in the epithelium [A32373].



Dermatological effects of the topical use of dexpanthenol include increased fibroblast proliferation and accelerated re-epithelialization in wound healing. Furthermore, it acts as a topical protectant, moisturizer, and has demonstrated anti-inflammatory properties [A32377].', 'Pantothenic acid is a precursor of coenzyme A, which serves as a cofactor for a variety of enzyme-catalyzed reactions involving transfer of acetyl groups. The final step in the synthesis of acetylcholine consists of the choline acetylase transfer of acetyl group from acetylcoenzyme A to choline. Acetylcholine is the neurohumoral transmitter in the parasympathetic system and as such maintains the normal functions of the intestine. Decrease in acetylcholine content would result in decreased peristalsis and in extreme cases adynamic ileus. ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30589, 'Gemacysteine 300 mg 30 caps.', 'جيماسيستاين 300مجم 30 كبسولة', '120', '90', 'Mucolytic', 'Acetylcysteine', 'Sedico > gema pharma', 'Cap', 'كبسولة', '300 mg', '3', 'مذيب للبلغم', 'description acetylcysteine (also known as n-acetylcysteine or n-acetyl-l-cysteine or nac) is primarily used as a mucolytic agent and in the management of acetaminophen poisoning. it is a derivative of cysteine with an acetyl group attached to the amino gr', NULL, NULL, 5641, '2024-06-04', 'Acetylcysteine is indicated for mucolytic therapy and in the management of [acetaminophen] overdose.[L31378,L31383,L31388,L31478]', 'A number of possible mechanisms for the mucolytic activity of acetylcysteine have been proposed. Acetylcysteine''s sulfhydryl groups may hydrolize disulfide bonds within mucin, breaking down the oligomers, and making the mucin less viscous.[A228173,L31388] Acetylcysteine has also been shown to reduce mucin secretion in rat models.[A228173] It is an antioxidant in its own right but is also deacetylated to cysteine, which participates in the synthesis of the antioxidant glutathione.[A228173] The antioxidant activity may also alter intracellular redox reactions, decreasing phosphorylation of EGFR and MAPK, which decrease transcription of the gene MUC5AC which produces mucin.[A228173]



In the case of acetaminophen overdoses, a portion of the drug is metabolized by CYP2E1 to form the potentially toxic metabolite N-acetyl-p-benzoquinone imine (NAPQI).[L31388] The amount of NAPQI produced in an overdose saturates and depletes glutathione stores.[L31388] The free NAPQI promiscuously binds to proteins in hepatocytes, leading to cellular necrosis.[L31388] Acetylcysteine can directly conjugate NAPQI or provide cysteine for glutathione production and NAPQI conjugation.[L31388]', 'Acetylcysteine is indicated for mucolytic therapy and in the management of acetaminophen overdose.[L31378,L31383,L31388,L31478] It has a short duration of action as it is given every 1-8 hours depending on route of administration, and has a wide therapeutic window.[L31378,L31383,L31388,L31478] Patients should be counselled regarding diluting oral solutions in cola for taste masking,[A228153] the risk of hypersensitivity, and the risk of upper gastrointestinal hemorrhage.[L31378,L31383,L31388,L31478]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26804, 'Septazole 400/80mg 20*10 tab.', 'سيبتازول 400/80مجم 20*10 اقراص', '340', '229', 'Antibiotic', 'Sulphamethoxazole+trimethoprim', 'Alexandria', 'Tab', 'أقراص', '80mg', '20', NULL, NULL, '6223000170595', NULL, 1900, '2025-05-27', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31556, 'Vegaskine-d 240 tabs.', 'فيجاسكين د 240 قرص', '276', '132', 'Cold products', 'Aspirin+caffeine+chlorpheniramine+dextromethorphan+paracetamol(acetaminophen)', 'Alexandria', 'Tab', 'أقراص', NULL, '24', NULL, 'acetylsalicylic acid indication for use in the temporary relief of various forms of pain inflammation associated with various conditions (including rheumatoid arthritis juvenile rheumatoid arthritis systemic lupus erythematosus osteoarthritis and ankylosi', '6223000170502', NULL, 734, '2024-11-09', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16702, 'Conazoglob 200mg 10 f.c. tabs.', 'كونازوجلوب 200مجم 10 اقراص', '932', '650', 'Antifungals.triazoles', 'Voriconazole', 'Global napi pharmaceuticals > global pharmaceutical industries', 'Tab', 'أقراص', '200mg', '1', 'مضاد فطريات', NULL, NULL, NULL, 2440, '2025-07-04', 'For the treatment of esophageal candidiasis, cadidemia, invasive pulmonary aspergillosis, and serious fungal infections caused by <i>Scedosporium apiospermum</i> and <i>Fusarium</i> spp.[L9821, L15571] ', 'Voriconazole is used to treat fungal infections caused by a variety of organisms but including _Aspergillus spp._ and _Candida spp_. Voriconazole is a triazole antifungal exhibiting fungistatic activity against fungal pathogens.[A236050, A236045, L9821] Like other triazoles, voriconazole binds to 14-alpha sterol demethylase, also known as CYP51, and inhibits the demethylation of lanosterol as part of the ergosterol synthesis pathway in yeast and other fungi. The lack of sufficient ergosterol disrupts fungal cell membrane function and limits fungal cell growth. With fungal growth limited, the host''s immune system is able to clear the invading organism.', 'Voriconazole is a fungistatic triazole antifungal used to treat infections by inhibiting fungal growth.[L9821] It is known to cause hepatotoxic and photosensitivity reactions in some patients.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (3110, 'Omegazlar 30 chewable gumies', 'اوميجازلار 30 اقراص قابلة للمضغ', '135', NULL, 'Multivitamin', 'Omega3+vitamin a+vitamin e+vitamin d3+vitamin c+vitamin b6+vitamin b12+follic acid+biotin+zinc+pantothenic acid+iodine', 'Utp', 'Unknown', 'غير محدد', NULL, '1', NULL, NULL, NULL, NULL, 987, '2025-02-23', NULL, NULL, NULL, NULL, 0, 0, 0);
