INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16206, 'Cetrodel forte 15% solution', 'ستروديل فورت 15% محلول', '4', NULL, 'Antiseptic', 'Cetrimide+chlorhexidine gluconate', 'El nasr', 'Solution', 'محلول', '15%', '1', NULL, 'about cetrimide quaternary ammonium compound a cationic surfactant antiseptic agent a detergent disinfectant. mechanism of action of cetrimide cetrimide is a cationic surfactant. it destroys or damages the cell membrane by lowering surface tension. it cau', NULL, NULL, 923, '2022-07-23', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (3673, 'Digest eze 20 caps', 'دايجست ايز 20 كبسولة', '94', '58', 'Git disturbances', 'Bromelain+papain+chamomile+ginger root+peppermint leaves+anise flavour', 'Western pharmaceuticals industries > international medical guide', 'Cap', 'كبسولة', NULL, '2', '1- تحسين عملية الهضم.', NULL, '6224007308028', NULL, 17233, '2024-07-04', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (12915, 'Zoladex 3.6mg prefilled syringe', 'زولادكس 3.6 مجم سرنجة معبأة', '2,781', '2139', 'Gnrh agonist', 'Goserelin', 'Astra zeneca', 'Syringe', 'حقنة', '3.6mg', '1', 'لعلاج حالات التهاب بطانة الرحم -- علاج هرموني في بعض اضطرابات امراض النساء', 'goserelin is a drug used to suppress production of the sex hormones (testosterone and estrogen) particularly in the treatment of breast and prostate cancer. it is an injectable gonadotropin releasing hormone superagonist (gnrh agonist) also known as a lut', '5000455018204', NULL, 18671, '2025-06-23', 'Goserelin is indicated for:



- Use in combination with flutamide for the management of locally confined carcinoma of the prostate

- Palliative treatment of advanced carcinoma of the prostate

- The management of endometriosis

- Use as an endometrial-thinning agent prior to endometrial ablation for dysfunctional uterine bleeding

- Use in the palliative treatment of advanced breast cancer in pre- and perimenopausal women', 'Goserelin is a synthetic decapeptide analogue of LHRH. Goserelin acts as a potent inhibitor of pituitary gonadotropin secretion when administered in the biodegradable formulation. The result is sustained suppression of LH and serum testosterone levels.', 'The pharmacokinetics of goserelin have been determined in both male and female healthy volunteers and patients. In these studies, goserelin was administered as a single 250µg (aqueous solution) dose and as a single or multiple 3.6 mg depot dose by subcutaneous route.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26434, 'Sacnel after shave lotion 120ml', 'ساكنل لوشن بعد الحلاقة 120 مل', '58', '32', 'Anti-acne', 'Salicylic acid+benzethonium+green tea+tea tree oil+hamamelis+allantoin+panthenol+menthol+aloe vera', 'Weiser', 'Lotion', 'لوشن', '120ml', '1', NULL, 'after shave lotion - for acne-prone skin & razor bumps-prone skin', NULL, NULL, 1070, '2024-05-21', 'Key additive in many skin-care products for the treatment of acne, psoriasis, callouses, corns, keratosis pilaris and warts.', 'Salicylic acid directly irreversibly inhibits COX-1 and COX-2 to decrease conversion of arachidonic acid to precursors of prostaglandins and thromboxanes. Salicylate''s use in rheumatic diseases is due to it''s analgesic and anti-inflammatory activity. Salicylic acid is a key ingredient in many skin-care products for the treatment of acne, psoriasis, calluses, corns, keratosis pilaris, and warts. Salicylic acid allows cells of the epidermis to more readily slough off. Because of its effect on skin cells, salicylic acid is used in several shampoos used to treat dandruff. Salicylic acid is also used as an active ingredient in gels which remove verrucas (plantar warts). Salicylic acid competitively inhibits oxidation of uridine-5-diphosphoglucose (UDPG) with nicotinamide adenosine dinucleotide (NAD) and noncompetitively with UDPG. It also competitively inhibits the transferring of the glucuronyl group of uridine-5-phosphoglucuronic acid (UDPGA) to a phenolic acceptor. Inhibition of mucopoly saccharide synthesis is likely responsible for the slowing of wound healing with salicylates.', 'Salicylic acid treats acne by causing skin cells to slough off more readily, preventing pores from clogging up. This effect on skin cells also makes salicylic acid an active ingredient in several shampoos meant to treat dandruff. Use of straight salicylic solution may cause hyperpigmentation on unpretreated skin for those with darker skin types (Fitzpatrick phototypes IV, V, VI), as well as with the lack of use of a broad spectrum sunblock. Subsalicylate in combination with bismuth form the popular stomach relief aid known commonly as Pepto-Bismol. When combined the two key ingredients help control diarrhea, nausea, heartburn, and even gas. It is also very mildly anti-biotic.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22236, 'Malari-co 10 tabs.', 'مالاري-كو 10 اقراص', '100', NULL, NULL, 'Artemether+lumefantrine', 'Multi-apex', 'Tab', 'أقراص', NULL, '1', NULL, NULL, NULL, NULL, 787, '2022-10-01', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15556, 'Budexan 1 mg/2ml 20 susp. amp. for inh', 'بوديكسان 1مجم/2مل 20 امبول معلق للاستنشاق', '529', '368', 'Bronchodilator.anti-inflammatory.glucocorticoid steroid', 'Budesonide', 'Genetic s.p.a > amriya', 'Susp', 'معلق', '1 mg', '1', 'موسع للشعب الهوائية ومضاد للالتهابات يستخدم فى دعم الجهاز التنفسي', NULL, '6224001616037', NULL, 5251, '2024-07-20', 'Budesonide extended-release capsules are indicated for the treatment and maintenance of mild to moderate Crohn’s disease.[L10601,L10604] Various inhaled budesonide products are indicated for prophylactic therapy in asthma [L10607,L10613,L10619] and to reduce exacerbations of COPD.[L10619] A budesonide nasal spray is available over the counter for symptoms of hay fever and upper respiratory allergies.[L10616] Extended-release capsules are indicated to induce remission of mild to moderate ulcerative colitis[L10622] and a rectal foam is used for mild to moderate distal ulcerative colitis.[L10625] In addition, a delayed-release capsule formulation of budesonide is indicated to reduce proteinuria in adults with IgA nephropathy at risk of rapid disease progression.[L42515,L49540] Budesonide is indicated to treat eosinophilic esophagitis (EoE): For this indication, it is only approved for use in adults in Europe [L42525] while it is approved for short-term use (12 weeks) in patients 11 years of age and older in the US.[L50161]', 'The short term effects of corticosteroids are decreased vasodilation and permeability of capillaries, as well as decreased leukocyte migration to sites of inflammation.[A187463] Corticosteroids binding to the glucocorticoid receptor mediates changes in gene expression that lead to multiple downstream effects over hours to days.[A187463]



Glucocorticoids inhibit neutrophil apoptosis and demargination; they inhibit phospholipase A2, which decreases the formation of arachidonic acid derivatives; they inhibit NF-Kappa B and other inflammatory transcription factors; they promote anti-inflammatory genes like interleukin-10.[A187463]



Lower doses of corticosteroids provide an anti-inflammatory effect, while higher doses are immunosuppressive.[A187463] High doses of glucocorticoids for an extended period bind to the mineralocorticoid receptor, raising sodium levels and decreasing potassium levels.[A187463]', 'Budesonide is a glucocorticoid used to treat respiratory and digestive conditions by reducing inflammation.[L10601,L10604,L10607,L10613,L10619,L10622,L10625] It has a wide therapeutic index, as dosing varies highly from patient to patient.[L10601,L10604,L10607,L10613,L10619,L10622,L10625] Patients should be counselled regarding the risk of hypercorticism and adrenal axis suppression.[L10601,L10604,L10607,L10613,L10619,L10622,L10625]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (32077, 'Grow max lotion 125ml', 'جرو ماكس لوشن 125 مل', '197', '129', 'Hair lotion', 'Procapil+caffeine+saw palmetto extract+argan oil+garlic oil+keratin+biotin+linolenic acid+rosemary oil+olive oil+almond oil+castor oil+vit e+panthenol+vit b12', 'Nile cosmetics > revaro pharm', 'Lotion', 'لوشن', '125ml', '1', 'لوشن غنى بالفيتامينات والمعادن لتغذية وترطيب الشعر ومنع التساقط والتقصف والهيشان', 'anti hair loss lotion provides a natural approach to hair loss with an invigorating formula that delivers longer stronger healthier hair and scalp. inhibits the production of the hormone dht associated with androgenic alopecia. stimulates microcirculation', NULL, NULL, 1043, '2025-08-08', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (3261, 'Sanihex mouth wash 120 ml', 'سانيهيكس 120 مل غسول للفم', '70', NULL, 'Oral care', 'Chlorhexidine+active oxygen+eugenol+cetylpyridinium', 'Cira pharma', 'Mouth wash', 'غسول للفم', '120 ml', '1', NULL, NULL, NULL, NULL, 826, '2025-03-07', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16121, 'Cephlex 250 mg 12 caps.', 'سيفلكس 250 مجم 12 كبسولة', '9', NULL, 'Antibiotic.cephalosporin.first-generation', 'Cefalexin', 'Kahira', 'Cap', 'كبسولة', '250 mg', '1', NULL, 'about cefalexin a first-generation cephalosporin antibiotic. mechanism of action cephalexin is a 1st generation cephalosporin which exerts its bactericidal action against both gram positive & gram negative organisms by inhibiting bacterial cell wall synth', NULL, NULL, 958, '2022-07-30', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13632, 'Albunorm 20% i.v.infusion', 'البونورم 20% للتسريب الوريدي', '1,450', '545', 'Human albumin', 'Human albumin', 'Octapharma > pharma over seas', 'Unknown', 'غير محدد', '20%', '1', 'البيومين بشري', NULL, NULL, NULL, 1648, '2025-08-06', 'Human immunoglobulin G is indicated for the following conditions:



### Primary Immunodeficiency

- for the treatment of primary immunodeficiency in adult and pediatric patients[L39920, L39930, L39935, L39940, L39945, L39950, L39955, L39960, L39965, L39975, L39980, L40029, L40034, L40044]

- in combination with [hyaluronidase (human recombinant)] for the treatment of primary immunodeficiency in patients ≥2 years of age.[L42760]



### Immune Thrombocytopenic Purpura (ITP)

- for the treatment of acute or chronic immune thrombocytopenic purpura in adult and pediatric patients[L39940, L39950, L39960, L39965, L39970, L39975, L39980]



### Chronic Inflammatory Demyelinating Polyneuropathy (CIDP)

- for the treatment of CIDP in adult patients[L39955, L39965, L39975, L39980, L40034]

- in combination with hyaluronidase (human recombinant) as a maintenance therapy in adults with chronic inflammatory demyelinating polyneuropathy (CIDP)[L40039]



### Multifocal Motor Neuropathy (MMN)

- for maintenance therapy to improve muscle strength and disability in adult patients with MMN[L39955]



### Prophylaxis of Bacterial Infection

- for the prevention of bacterial infections in patients with hypogammaglobulinemia and/or B-cell chronic lymphocytic leukemia[L39960]



### Coronary Artery Aneurysm Associated With Kawasaki Syndrome

- for the prevention of coronary artery aneurysms in pediatric patients with Kawasaki syndrome[L39960]



### Dermatomyositis

- for the treatment of dermatomyositis in adult patients[L39970]

', 'IVIg interacts with a number of different components of the immune system, including cytokines, complement, Fc receptors and several cell surface immunocompetent molecules. IVIg also impacts different effector cells of the immune system (B and T lymphocytes, dendritic cells, etc.) and regulates a wide range of genes. Its main mechanism of actions are believed to be Fc-dependent and F(ab'')2-dependent. IVIg competitively blocks gamma Fc receptors, preventing the binding and ingestion of phagocytes and suppressing platelet depletion. IVIg contains a number of different antobodies, which prevent infection by attaching to the surface of invading pathogens and aiding in their disposal before they can infect cells. Antibodies remove pathogens via complement activation, agglutination or precipitation, pathogen receptor blocking, macrophage “tagging” or neutralization (via binding) of pathogen toxins. Intact IVIg and F(ab′)2 fragments of IVIg can also neutralize the activity of various autoantibodies. By triggering the production of interleukin-1 receptor antagonist, IVIg modulates of the production of cytokines and cytokine antagonists. It also prevents the generation of the C5b-9 membrane attack complex and subsequent complement-mediated tissue damage by binding active complement components.', 'Used as a replacement therapy in inherited humoral immunodeficiency disorders such as severe combined immunodeficiency syndrome, x-linked agammaglobulinemia, and Wiskott-Aldrich Syndrome. The immunoglobulins target, bind and kill bacterial cells as well as viral particles. IgG is the monomeric immunoglobulin of which there are four subclasses (IgG1, IgG2, IgG3 and IgG4) in differing abundances (66%, 23%, 7% and 4%). IgAs represent about 15% of the immunoglobulins in the blood. These target inhaled or ingested pathogens.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15935, 'Zadoglobin 20 caps', 'زادوجلوبين 20 كبسولة', '135', '75', 'Multivitamins', 'Iron+folic acid+vitamin c+b6+b12', 'Safe life', 'Cap', 'كبسولة', NULL, '2', 'مكمل غذائى يستخدم لعلاج فقر الدم ودعم الجهاز العصبى والمناعى للجسم', NULL, NULL, NULL, 6046, '2024-06-07', NULL, NULL, NULL, NULL, 0, 0, 0);
