INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (101, 'Actifed expectorant syrup 120 ml', 'اكتيفيد اكسبكتورانت طارد بلغم 120 مل شراب', '6', NULL, 'Anti-cough.antitussive and expectorant', 'Guaifenesin+pseudoephedrine+triprolidine', 'Glaxo smithkline', 'Syrup', 'شراب', '120 ml', '1', 'مهديء للسعال وطارد بلغم', 'description an expectorant that also has some muscle relaxing action mechanism of action guaifenesin may act as an irritant to gastric vagal receptors and recruit efferent parasympathetic reflexes that cause glandular exocytosis of a less viscous mucus mi', '6221045000631', NULL, 1878, '2022-12-01', NULL, NULL, NULL, NULL, 0, 0, 0);
