INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28123, 'Thiolkan 600mg 10 tab.', 'ثيولكان 600مجم 10 اقراص', '16', NULL, 'Antioxidant', 'Thioctic acid(alpha lipoic acid)', 'Hikma pharma', 'Tab', 'أقراص', '600mg', '1', 'مضاد للأكسدة', 'uses alpha-lipoic acid (ala) has been used as an antioxidant for the treatment of diabetes and hiv. it also has been used for cancer liver ailments and various other conditions. dosing oral dosage of alpha-lipoic acid given in numerous clinical studies ra', NULL, NULL, 2141, '2022-05-28', 'Investigated for use/treatment in diabetes mellitus type 2, neuropathy (diabetic), and vascular diseases.', NULL, NULL, 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18779, 'Irofetch 20 caps.', 'ايروفيتش 20 كبسولة', '85', NULL, 'Iron supplement', 'Iron+vitamin c+zinc+vitamin b6+folic acid+selenium', 'Paxal', 'Cap', 'كبسولة', NULL, '2', NULL, NULL, '6224011120173', NULL, 1025, '2022-11-29', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (11158, 'Kamillodent mouth spray', 'كاميلودنت سبراي للفم', '75', NULL, NULL, NULL, 'Company', 'Spray', 'بخاخ', NULL, '1', NULL, NULL, NULL, NULL, 1385, '2022-08-02', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21690, 'Lemox 30 tabs. jar', 'ليموكس 30 قرص', '95', '65', 'Vitamin', 'Vitamin c+zinc+vitamin e+beta carotene+selenium', 'Pharma zad > bionil', 'Tab', 'أقراص', NULL, '1', 'مقوي للمناعة ومضاد للاكسدة', 'for enhancing the immune system and prophylactic prevention of infection 500 mg vitamin c + 20 mg zinc', '6224009569076', NULL, 2289, '2025-06-03', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14968, 'Beco 100 tab.', 'بيكو 100 قرص', '12', NULL, 'Vitamin b', 'Calcium pantothenate+nicotinamide+vitamin b1+vitamin b2+vitamin b6+vitamin b12', 'Misr', 'Tab', 'أقراص', NULL, '1', NULL, NULL, NULL, NULL, 1101, '2022-06-18', 'Calcium acetate is one of a number of calcium salts used to treat hyperphosphatemia (too much phosphate in the blood) in patients with kidney disease.', 'Calcium acetate and other calcium salts are phosphate binders. They work by binding with the phosphate in the food you eat, so that it is eliminated from the body without being absorbed.', 'Patients with advanced renal insufficiency (creatinine clearance less than 30 ml/min) exhibit phosphate retention and some degree of hyperphosphatemia. The retention of phosphate plays a pivotal role in causing secondary hyperparathyroidism associated with osteodystrophy, and soft-tissue calcification. The mechanism by which phosphate retention leads to hyperparathyroidism is not clearly delineated. Therapeutic efforts directed toward the control of hyperphosphatemia include reduction in the dietary intake of phosphate, inhibition of absorption of phosphate in the intestine with phosphate binders, and removal of phosphate from the body by more efficient methods of dialysis. The rate of removal of phosphate by dietary manipulation or by dialysis is insufficient. Dialysis patients absorb 40% to 80% of dietary phosphorus. Therefore, the fraction of dietary phosphate absorbed from the diet needs to be reduced by using phosphate binders in most renal failure patients on maintenance dialysis. Calcium acetate when taken with meals combines with dietary phosphate to form insoluble calcium phosphate which is excreted in the feces. Maintenance of serum phosphorus below 6.0 mg/dl is generally considered as a clinically acceptable outcome of treatment with phosphate binders. Calcium acetate is highly soluble at neutral pH, making the calcium readily available for binding to phosphate in the proximal small intestine.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19512, 'Futapramin 10 mg 30 f.c. tabs', 'فوتابرامين 10مجم 30 قرص', '78', '56.25', 'Psychiatric.antidepressants', 'Escitalopram', 'Future pharmaceutical industries (fpi)', 'Tab', 'أقراص', '10 mg', '3', NULL, NULL, '6223004190483', NULL, 1887, '2024-07-18', 'Escitalopram is indicated for the acute and maintenance treatment of major depressive disorder (MDD) in adults and pediatric patients 12 years old and older and for the acute treatment of generalized anxiety disorder (GAD) in adults and pediatric patients 7 years old and older.[L8513,L8513] It is additionally indicated for symptomatic relief of obsessive-compulsive disorder (OCD) in Canada.[L8516]', 'Escitalopram, like other selective serotonin re-uptake inhibitors, enhances serotonergic activity by binding to the orthosteric (i.e. primary) binding site on the serotonin transporter (SERT), the same site to which endogenous 5-HT binds, and thus prevents the re-uptake of serotonin into the presynaptic neuron.[A185420,A39738,A185726] Escitalopram, along with [paroxetine], is also considered an allosteric serotonin re-uptake inhibitor - it binds to a secondary allosteric site on the SERT molecule to more strongly inhibit 5-HT re-uptake. Its combination of orthosteric and allosteric activity on SERT allows for greater extracellular 5-HT levels, a faster onset of action, and greater efficacy as compared to other SSRIs. [A185825,A185726] The sustained elevation of synaptic 5-HT eventually causes desensitization of 5-HT<sub>1A</sub> auto-receptors, which normally shut down endogenous 5-HT release in the presence of excess 5-HT - this desensitization may be necessary for the full clinical effect of SSRIs and may be responsible for their typically prolonged onset of action.[A185828,A185726]



Escitalopram has shown little-to-no binding affinity at a number of other receptors, such as histamine and muscarinic receptors, and minor activity at these off-targets may explain some of its adverse effects.[L8513,L8516,L8522,A185726]', 'Escitalopram belongs to a class of medications called selective serotonin re-uptake inhibitors (SSRIs). These agents cause an increase in serotonin levels in neuronal synapses by preventing the re-uptake of serotonin (5-HT) into the presynaptic terminals of serotonergic neurons.[A185420,A39738,L8513,L8516] As compared to other SSRIs, it appears to have a relatively quick onset of effect due to its potency.[A185819,A185822]



SSRIs as a class have been associated with abnormal bleeding, particularly in patients receiving concomitant therapy with other medications affecting hemostasis, and with the development of serotonin syndrome. Use escitalopram with caution in patients with a higher-than-baseline risk of bleeding and in patients receiving concomitant therapy with other serotonergic drugs. Escitalopram may also cause a discontinuation syndrome with abrupt removal of the drug, and should be slowly tapered if discontinuation of therapy is warranted.[L8513,L8516,L8522]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4117, 'Flexsoya 30 tabs', 'فليكسويا 30 قرص', '285', NULL, 'Multivitamins', 'Glucosamine hydrochloride 1500 mg chondroitin sulfate 800 mg avocado-soybean unsaponifiables 300mg vitamin e 20 mg vitamin c 60 mg manganese 2mg', 'Novazone', 'Tab', 'أقراص', NULL, '1', NULL, NULL, NULL, NULL, 880, '2025-06-12', 'Glucosamine is generally used over the counter in the symptomatic treatment of osteoarthritis and joint pain,[A2352] frequently combined with chondroitin sulfate and/or ibuprofen.[L32699]', 'The mechanism of action of glucosamine in joint health is unclear,[A231894] however there are several possible mechanisms that contribute to its therapeutic effects. Because glucosamine is a precursor for glycosaminoglycans, and glycosaminoglycans are a major component of joint cartilage,[A232289] glucosamine supplements may help to rebuild cartilage and treat the symptoms of arthritis.  Some in vitro studies show evidence that glucosamine reduces inflammation via inhibition of interferon gamma[A12619,A12616,A12621]  and Nuclear factor kappa B subunit 65 (NF-κB p65),[A12616,A232284] improving the symptoms of arthritis and joint pain. Clinical relevance is unknown at this time.', 'The administration of glucosamine, in theory, provides a building block towards the synthesis of glycosaminoglycans, slowing the progression of osteoarthritis and relieving symptoms of joint pain. Studies to this date examining the efficacy of glucosamine sulfate have been inconclusive. Glycosaminoglycans contribute to joint cartilage elasticity, strength, and flexibility.[A232144] A systematic review of various studies and guidelines determined that modest improvements were reported for joint pain and function in patients taking glucosamine. A consistent joint space narrowing was observed, but with an unclear clinical significance.[A231894]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27723, 'Supranem 500/500mg i.v.vial for i.v inf.', 'سوبرانيم 500 / 500 مجم فيال عن طريق الوريد للاطفال', '133', NULL, 'Antibiotic', 'Cilastatin+imipenem', 'Misr > rameda', 'Vial', 'فيال', '500mg', '1', 'مضاد حيوي', NULL, NULL, NULL, 1454, '2022-11-27', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14975, 'Bedremine 100mg 30 exten. rel. tab.', 'بيدريمين 100مجم 30 قرص', '198', '147', 'Psychiatric.antidepressants', 'Desvenlafaxine', 'Mash premiere', 'Tab', 'أقراص', '100mg', '3', NULL, NULL, '6222001401653', NULL, 2206, '2024-11-25', 'Desvenlafaxine is indicated for the treatment of major depressive disorder in adults.[L47936] It has also been used off-label to treat hot flashes in menopausal women.[A261166]', 'The exact mechanism of the antidepressant action of desvenlafaxine is unknown but is thought to be related to the potentiation of serotonin and norepinephrine in the central nervous system, through inhibition of their reuptake.[L47936] Particularly, desvenlafaxine has been found to inhibit the serotonin, norepinephrine, and dopamine transporters with varying degrees of affinity. Desvenlafaxine inhibits serotonin transporters with 10 times the affinity of norepinephrine transporters, and dopamine transporters with the lowest affinity.[A6991] ', 'Desvenlafaxine is a selective serotonin and norepinephrine reuptake inhibitor.[A6990,A6991,L47936] It lacks significant activity on muscarinic-cholinergic, H<sub>1</sub>-histaminergic, or &alpha;<sub>1</sub>-adrenergic receptors <i>in vitro</i>, or inhibitory activity against monoamine oxidase.[L47936] Desvenlafaxine does not appear to exert activity against calcium, chloride, potassium and sodium ion channels and also lacks monoamine oxidase (MAO) inhibitory activity.[A6991] It was also shown to lack significant activity against the cardiac potassium channel, hERG, <i>in vitro</i>.[A36975]



Electrocardiograms were obtained from 1,492 desvenlafaxine treated patients with major depressive disorder and 984 placebo-treated patients in clinical studies lasting up to 8 weeks. No clinically relevant differences were observed between desvenlafaxine treated and placebo-treated patients for QT, QTc, PR, and QRS intervals. In a thorough QTc study with prospectively determined criteria, desvenlafaxine did not cause QT prolongation. No difference was observed between placebo and desvenlafaxine treatments for the QRS interval.[L47936] ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29218, 'Vfend 200 mg 10 f.c.tabs.', 'فيفيند 200مجم 10 اقراص', '4,183', '2925', 'Antifungals.triazoles', 'Voriconazole', 'Pfizer', 'Tab', 'أقراص', '200 mg', '1', NULL, NULL, NULL, NULL, 1820, '2024-11-07', 'For the treatment of esophageal candidiasis, cadidemia, invasive pulmonary aspergillosis, and serious fungal infections caused by <i>Scedosporium apiospermum</i> and <i>Fusarium</i> spp.[L9821, L15571] ', 'Voriconazole is used to treat fungal infections caused by a variety of organisms but including _Aspergillus spp._ and _Candida spp_. Voriconazole is a triazole antifungal exhibiting fungistatic activity against fungal pathogens.[A236050, A236045, L9821] Like other triazoles, voriconazole binds to 14-alpha sterol demethylase, also known as CYP51, and inhibits the demethylation of lanosterol as part of the ergosterol synthesis pathway in yeast and other fungi. The lack of sufficient ergosterol disrupts fungal cell membrane function and limits fungal cell growth. With fungal growth limited, the host''s immune system is able to clear the invading organism.', 'Voriconazole is a fungistatic triazole antifungal used to treat infections by inhibiting fungal growth.[L9821] It is known to cause hepatotoxic and photosensitivity reactions in some patients.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14218, 'Anthelios xl spf 50+ lait (lotion) 100 ml', 'انثيليوس اكس ال بمعامل حماية+50لوشن 100 مل', '399', NULL, 'Sun block', NULL, 'La roche-posay > united company for trading & distribution', 'Lotion', 'لوشن', '100 ml', '1', NULL, '*properties very high face and body protection. ultra uva (ppd 34). sensitive skin to skin prone to sun intolerance commonly known as sun allergies. extreme sunshine intensity. prevention of sun-induced pigment spots (pregnancy photosensitization...). ver', NULL, NULL, 681, '2022-07-28', NULL, NULL, NULL, NULL, 0, 0, 0);
