INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26243, 'Roferon a 3m.i.u/0.5ml s.c.prefilled syringe', 'روفيرون ا 3وحدة عالمية/0.5مل', '87', NULL, 'Antiviral .interferon alfa', 'Interferon alfa-2a', 'F.hoffman la roche', 'Syringe', 'حقنة', '0.5ml', '1', NULL, 'indication for the treatment of chronic hepatitis c hairy cell leukemia aids-related kaposi s sarcoma and chronic myelogenous leukemia. also for the treatment of oral warts arising from hiv infection. mechanism of action interferon alpha binds to type i i', NULL, NULL, 913, '2022-07-24', 'For the treatment of venereal or genital warts caused by the Human Papiloma Virus.', 'Interferon alpha binds to type I interferon receptors (IFNAR1 and IFNAR2c) which, upon dimerization, activate two Jak (Janus kinase) tyrosine kinases (Jak1 and Tyk2). These transphosphorylate themselves and phosphorylate the receptors. The phosphorylated INFAR receptors then bind to Stat1 and Stat2 (signal transducers and activators of transcription)which dimerize and activate multiple (~100) immunomodulatory and antiviral proteins. Interferon alpha binds less stably to type I interferon receptors than interferon beta.', 'Upregulates the expression of MHC I proteins, allowing for increased presentation of peptides derived from viral antigens. This enhances the activation of CD8+ T cells that are the precursors for cytotoxic T lymphocytes (CTLs) and makes the macrophage a better target for CTL-mediated killing. Interferon alpha also induce the synthesis of several key antiviral mediators, including 2''-5'' oligoadenylate synthetase (2''-5'' A synthetase) and protein kinase R.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21466, 'Koate-dvi 500 iu i.v. vial 5 ml', 'كواتي-ديفي اي 500 وحدة فيال 5 مل', '3,500', NULL, 'Blood-coagulation factor', 'Blood-coagulation factor viii', 'Grifols biological inc.-usa > biolinx medical inc.', 'Vial', 'فيال', '500 iu', '1', NULL, 'koate-dvi is indicated for the treatment of classical hemophilia (hemophilia a) in which there is a demonstrated deficiency of activity of the plasma clotting factor factor viii. koate-dvi provides a means of temporarily replacing the missing clotting fac', NULL, NULL, 852, '2022-07-08', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24778, 'Pevaryl 1% topical cream 15 gm', 'بيفاريل 1% كريم موضعي 15جم', '7', NULL, 'Antifungals.imidazoles', 'Econazole', 'Mina pharm > janssen cilag', 'Cream', 'كريم', '1%', '1', NULL, 'about econazole synthetic imidazole derivative antifungal. mechanism of action of econazole econazole exerts its effects by altering cellular membrane and interfering with cellular enzymes.it is active against many fungi dermatophytes and yeasts as well a', '6222003700334', NULL, 876, '2022-07-22', 'For topical application in the treatment of tinea pedis, tinea cruris, and tinea corporis caused by <i>Trichophyton rubrum</i>, <i>Trichophyton mentagrophytes</i>, <i>Trichophyton tonsurans</i>, <i>Microsporum canis</i>, <i>Microsporum audouini</i>, <i>Microsporum gypseum</i>, and <i>Epidermophyton floccosum</i>, in the treatment of cutaneous candidiasis, and in the treatment of tinea versicolor.', 'Econazole interacts with 14-&alpha; demethylase, a cytochrome P-450 enzyme necessary to convert lanosterol to ergosterol. As ergosterol is an essential component of the fungal cell membrane, inhibition of its synthesis results in increased cellular permeability causing leakage of cellular contents. Econazole may also inhibit endogenous respiration, interact with membrane phospholipids, inhibit the transformation of yeasts to mycelial forms, inhibit purine uptake, and impair triglyceride and/or phospholipid biosynthesis.', 'Econazole is an antifungal medication related to fluconazole (Diflucan), ketoconazole (Nizoral), itraconazole (Sporanox), and clotrimazole (Lotrimin, Mycelex). Econazole prevents fungal organisms from producing vital substances required for growth and function. This medication is effective only for infections caused by fungal organisms. It will not work for bacterial or viral infections.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19517, 'Futasone cream 15 gm', 'فوتازون كريم 15 جم', '10', NULL, 'Antibiotic with glucocorticoid', 'Betamethasone+fucidic acid', 'Julphar', 'Cream', 'كريم', '15 gm', '1', 'مضاد للإلتهاب، حيث يخفف من التورم، والاحمرار، والحكة، ويستخدم في حالات التهاب الجلد والأكزيما.', 'description betamethasone is a type of medicine called a topical corticosteroid. corticosteroids are medicines used for reducing inflammation. skin inflammation in eczema occurs due to the irritation of the skin and is caused by the release of various sub', '6291100080922', NULL, 1059, '2022-12-05', NULL, NULL, NULL, NULL, 0, 0, 0);
