INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14723, 'Avene trixera+ selectiose emollient balm 200ml', 'افين تريكسيرا + بلسم مرطب سالينوز 200 مل', '259', NULL, 'Moisturizing topicals', NULL, 'Pierre - fabre france > biotech egypt', 'Unknown', 'غير محدد', '200ml', '1', NULL, 'ultra-rich emollient cream for severely dry atopic skin conditions that nourishes restores the skin barrier soothes irritation and reduces skin hypersensitivity. ï¿½ selectioseï¿½ reduces skin hypersensitivity and irritation and controls skin inflammation', '3282779478433', NULL, 912, '2022-08-04', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31795, 'Deepfreeze pharmakon cold gel 125 gm', 'ديبفريز فارماكون كولد جل 125 جم', '138', NULL, 'Topical analgesic', 'Ethyl chloride+menthol+thymol+eucalyptus oil+camphor+allantoin glycyrrhetinic acid+aloe vera+tocopheryl acetate+isopropyl myristate+melaleuca alternifolia leaf oil', 'Pharmakon', 'Gel', 'جل', '125 gm', '1', NULL, NULL, '6224008056829', NULL, 689, '2023-03-28', 'Investigated for use/treatment in burns and burn infections, cardiac surgery, inflammatory disorders (unspecified), ischemic reperfusion injury, and sepsis and septicemia.', 'CTI-01 inhibits the systemic release of cytokines, such as TNF-alpha and HMGB1, which promote the body''s inflammatory response. The over-expression of these cytokines has been linked to diseases that occur in critical care settings, such as severe organ damage following cardiopulmonary bypass (CPB) and post-operative ileus following abdominal surgery.', NULL, 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30375, 'Daivobet topical gel 15 gm', 'دايفوبيت جل موضعي 15 جم', '183', '146', 'Psoriasis', 'Betamethasone+calcipotriol(calcipotriene)', 'Leo pharmaceutical products', 'Gel', 'جل', '15 gm', '1', NULL, 'uses: this medication is used to treat psoriasis. it helps to reduce the redness thickening and scaling of the skin that occurs with this condition. healthy skin lowers your risk for infection and protects you against the environment. this product contain', NULL, NULL, 1358, '2024-02-08', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14566, 'Atoreza 40/10mg 28 f.c. tab.', 'اتوريزا 40/10مجم 28 قرص', '210', '158', 'Antihyperlipidemic.cholesterol absorption inhibitors.combined with statin', 'Atorvastatin+ezetimibe', 'Marcyrl co.', 'Tab', 'أقراص', '10mg', '2', 'علاج ارتفاع الدهون والكوليسترول الضار بالدم', 'indications for atorvastatin + ezetimibe 1.homozygous familial hypercholesterolemia 2.combined hyperlipidaemia typical dosage for atorvastatin + ezetimibe adult: 1 tablet / day about ezetimibe cholesterol absorption inhibitor 2-azetidinone derivative hypo', '6223003571344', NULL, 8633, '2024-06-30', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4627, 'Fam 5mg/ml eye drops 5 ml', 'فام 5مجم/مل قطرة عين 5 مل', '9', NULL, 'Nsaid.acetic acid derivatives', 'Ketorolac tromethamine', 'Alfacure pharmaceuticals', 'Drops', 'نقط', '5mg', '1', NULL, 'mechanism of action ketorolac is a nonsteroidal anti-inflammatory drug (nsaid) chemically related to indomethacin and tolmetin. ketorolac tromethamine is a racemic mixture of [-]s- and [+]r-enantiomeric forms with the s-form having analgesic activity. its', NULL, NULL, 771, '2023-05-03', 'Ketorolac is a Non-steroidal anti-inflammatory drug (NSAID) and has antipyretic, analgesic and anti-inflammatory properties.[A176131]  It is indicated for short term management of acute pain that requires the calibre of pain management offered by opioids.[L3674]  Clinicians may choose to initiate ketorolac to manage post-operative pain, spinal and soft tissue pain, rheumatoid arthritis, osteoarthritis, ankylosing spondylitis, menstrual disorders and headaches among other ailments.[L6520] Regardless of the etiology of pain, patients should use the lowest possible dose, and avoid using ketorolac for an extended period of time (ideally ≤ 5 days).[L3674] A benefit of choosing ketorolac over other analgesics with similar potency is that that there does not appear to be a risk of dependence or tolerance with ketorolac use.[A176131]     ', 'Ketorolac inhibits key pathways in prostaglandin synthesis which is crucial to it''s mechanism of action.[A176143]  Although ketorolac is non-selective and inhibits both COX-1 and COX-2 enzymes, it''s clinical efficacy is derived from it''s COX-2 inhibition. The COX-2 enzyme is inducible and is responsible for converting arachidonic acid to prostaglandins that mediate inflammation and pain.  By blocking this pathway, ketorolac achieves analgesia and reduces inflammation.[A176134] Ketorolac is administered as a racemic mixture; however, the "S" enantiomer is largely responsible for it''s pharmacological activity.[A176246]', 'Ketorolac is a non-selective NSAID and acts by inhibiting both COX-1 and COX-2 enzymes which are normally responsible for converting arachidonic acid to prostaglandins. The COX-1 enzyme is constitutively active and can be found in platelets, gastric mucosa, and vascular endothelium. On the other hand, the COX-2 enzyme is inducible and mediates inflammation, pain and fever.  



As a result, inhibition of the COX-1 enzyme is linked to an increased risk of bleeding and risk of gastric ulceration, while the desired anti-inflammatory and analgesic properties are linked to inhibition of the COX-2 enzyme.[A176134] Therefore, despite it''s effectiveness in pain management, ketorolac should not be used long-term since this increases the risk of serious adverse effects such as gastrointestinal bleeding, peptic ulcers, and perforations.[A178678]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26634, 'Sebaclar hydra cream 50 ml', 'سيباكلار هيدرا كريم 50مل', '286', '248', 'Skin care', NULL, 'Egyptian company for cosmetics > hayah lab.', 'Cream', 'كريم', '50 ml', '1', NULL, 'emollient cream for oily and acne-prone skin', '6224008838128', NULL, 1225, '2025-04-06', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24123, 'One daily mens multivitamin 100 caplets (illegal import)', 'ون دايلي مالتي فيتامين 100 كبسولة', '0', NULL, 'Multivitamins', 'Vitamins(a+c+d3+e+k+b1+b2+b6+b12)+folic acid+biotin+calcium+magnesium+zinc+selenium+copper+manganese+chromium+lycopene', 'Puritans pride', 'Cap', 'كبسولة', NULL, '1', NULL, NULL, NULL, NULL, 735, '2022-07-18', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (33836, 'Vardatrin 10 mg 4 f.c. tabs.', 'فارداترين 10 مجم 4 قرص', '29', '20', 'Tonic for men', 'Vardenafil', 'Wadi elneel benta', 'Tab', 'أقراص', '10 mg', '1', 'مقوى للرجال', 'about vardenafil phosphodiesterase-5 enzyme inhibitor in erectile dysfunction.', '6224009259359', NULL, 2225, '2025-06-03', 'Vardenafil is indicated for the treatment of erectile dysfunction.[L45563,L45568,L45573]', 'Vardenafil inhibits cyclic guanosine monophosphate (GMP) specific phosphodiesterase type 5 (PDE5), which is responsible for the degradation of cyclic GMP in the corpus cavernosum located around the penis. Penile erection during sexual stimulation is caused by increased penile blood flow resulting from the relaxation of penile arteries and corpus cavernosal smooth muscle. This response is mediated by the release of nitric oxide (NO) from nerve terminals and endothelial cells, which stimulates the synthesis of cyclic GMP in smooth muscle cells. Cyclic GMP causes smooth muscle relaxation and increased blood flow into the corpus cavernosum. The tissue concentration of cyclic GMP is regulated by both the rates of synthesis and degradation via phosphodiesterases (PDEs), and the most abundant PDE in the human corpus cavernosum is PDE5. Therefore, the inhibition of PDE5 by vardenafil enhances erectile function by increasing the amount of cyclic GMP.[L45563]', 'Vardenafil is a potent and selective inhibitor of cyclic guanosine monophosphate (cGMP)-specific phosphodiesterase type 5 (PDE5), an enzyme responsible for the degradation of cGMP in the corpus cavernosum. The presence of cGMP in the corpus cavernosum leads to smooth muscle relaxation, an increased inflow of blood and an erection. Therefore, in patients with erectile dysfunction given vardenafil, normal sexual stimulation will increase cGMP levels in the corpus cavernosum. Without sexual stimulation and no cGMP production, vardenafil should not cause an erection.[L45563,L45568] 



Vardenafil should not be used in men for whom sexual activity is not recommended due to their underlying cardiovascular status. There is also a risk of developing prolonged erections that last longer than 4 hours, as well as priapism. In the event of a sudden loss of vision in one or both eyes, patients should stop using vardenafil. Patients taking PDE5 inhibitors, such as vardenafil, may also develop sudden hearing loss and experience a prolonged QT interval.[L45563,L45568]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15361, 'Blanka-s cream 40 gm', 'بلانكا-اس كريم 40 جم', '149', '139', 'Whitening topical', NULL, 'Leader cosmetics > farabi', 'Cream', 'كريم', '40 gm', '1', NULL, 'sensitive areas whitening intimate care effective results', '6224007935347', NULL, 1068, '2024-06-27', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30250, 'Calcitonium 50 i.u./ml 5 amps.', 'كالسيتونيوم 50وحدة/مل 5 امبول', '88', '62', 'Osteoporosis.antiresorptives', 'Calcitonin', 'Acdima international trading', 'Amp', 'أمبول', NULL, '5', 'علاج هشاشة العظام بعد انقطاع الطمث عند النساء أكثر من 5 سنوات، والعلاج المبكر لحالات الطوارئ بفرط كالسيوم الدم.', 'prescribed for: injectable calcitonin is used to treat osteoporosis and paget s disease. injectable calcitonin also is used to treat seriously elevated blood calcium levels. the nasal spray is used only for osteoporosis in women who are at least 5 years p', '6224000588618', NULL, 4255, '2025-05-14', 'Investigated for use/treatment in myocardial infarction, heart disease, and asthma.', 'While current drugs for treating HF and MI have a single mechanism of action, CGRP appears to work on several levels: for example, it (1) increases local blood flow enhancing hemodynamic proficiency; (2) protects heart muscle cells from the damage due to lack of oxygen (ischemia); and, (3) modulates the immune system by boosting anti-inflammatory cytokine expression while suppressing pro-inflammatory cytokines, which reduces the inflammatory response to provide a cardioprotective effect. All these actions are meant to minimize heart muscle damage and subsequent scar tissue formation, while promoting the healing process.', NULL, 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31775, 'Dolnerva 30 caps', 'دولنيرفا 30 كبسولة', '165', '105', 'Multivitamin', 'Benfotiamine+folic acid+methylcobalamin+riboflavin+pyridoxine+pantothenic acid', 'Copad > dolls pharma', 'Cap', 'كبسولة', NULL, '3', NULL, 'vitamin b1 (benfotiamine) ... 150 mg vitamin b2 (riboflavin) ... 10 mg vitamin b5 (pantothenic acid) ... 50 mg vitamin b6 (pyridoxine) ... 10 mg vitamin b9 (folic acid) ... 167.9 mcg vitamin b12 (methylcobalamin) ... 500 mcg dolnerva aids neurological sus', '6224007901915', NULL, 865, '2024-06-13', NULL, NULL, NULL, NULL, 0, 0, 0);
