INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26783, 'Sensitelial emollient care 200 ml', 'سينستيليال اميولنت كير 200مل', '149', NULL, 'Skin acre', NULL, 'Acm dermatological laboratories > biotech egypt', 'Unknown', 'غير محدد', '200 ml', '1', NULL, 'properties sensitelial emollient care has been formulated to moisturize dry skin and skin with atopic tendency. thanks to its combination of moisturizing nourishing and soothing active ingredients known for their efficiency and high tolerance sensitelial', NULL, NULL, 734, '2022-08-01', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26380, 'Royal ton 10 caps.', 'رويال تون 10 كبسولات', '52', NULL, 'Multivitamins', 'Royal jelly+ginseng+bee propolis', 'Spi pharmaceuticals > ibn sina', 'Cap', 'كبسولة', NULL, '1', NULL, NULL, NULL, NULL, 1945, '2022-08-12', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25169, 'Pregavalex 50mg 30 caps.', 'بريجافالكس 50مجم 30 كبسول', '70', NULL, 'Anti-epileptic.gaba analogs', 'Pregabalin', 'Eva pharma', 'Cap', 'كبسولة', '50mg', '3', NULL, 'about pregabalin a gaba agonist anticonvulsant. mechanism of action of pregabalin pregabalin is a gaba neurotransmitter analog. it produces its action by modulating calcium channels and also reduces the release of neurotransmitters such as glutamate norep', '6223004510113', NULL, 753, '2022-07-24', 'Pregabalin is indicated for the management of neuropathic pain associated with diabetic peripheral neuropathy, postherpetic neuralgia, fibromyalgia, neuropathic pain associated with spinal cord injury, and as adjunctive therapy for the treatment of partial-onset seizures in patients 1 month of age and older.[L7066]', 'Although the mechanism of action has not been fully elucidated, studies involving structurally related drugs suggest that presynaptic binding of pregabalin to voltage-gated calcium channels is key to the antiseizure and antinociceptive effects observed in animal models.[L7066]  



By binding presynaptically to the alpha2-delta subunit of voltage-gated calcium channels in the central nervous system, pregabalin modulates the release of several excitatory neurotransmitters including glutamate, substance-P, norepinephrine, and calcitonin gene related peptide.[A31163] In addition, pregabalin prevents the alpha2-delta subunit from being trafficked from the dorsal root ganglia to the spinal dorsal horn, which may also contribute to the mechanism of action.[A36628] 



Although pregabalin is a structural derivative of the inhibitory neurotransmitter gamma-aminobutyric acid (GABA), it does not bind directly to GABA or benzodiazepine receptors.[A31165]  ', 'Although the structure of pregabalin is similar to gamma-aminobutyric acid (GABA), it does not bind to GABA receptors.[A31163,A187190] Instead, it binds the alpha2-delta subunit of presynaptic voltage-gated calcium channels in the central nervous system.[A31163,A187190] Pregabalin does not modulate dopamine receptors, serotonin receptors, opiate receptors, sodium channels or cyclooxygenase activity.[A187190] ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14649, 'Authentic spray 120 ml', 'اوثنتيك سبراي 120 مل', '90', '60', 'Massage spray', 'Camphor+ menthol+ eucalyptus', 'Leader > flare pharma', 'Spray', 'بخاخ', '120 ml', '1', 'بخاخ للمساج -- مسكن للالام -- علاج الشد العضلي', 'massage spray for low back pain osteoarthritis rhumatic pain', '385090384965', NULL, 3635, '2024-01-30', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25147, 'Predilone 5mg 10 tab. (25 strips pack)', 'بريديلون 5مجم 10 اقراص - علبة 25 شريط', '250', '162.5', 'Glucocorticoid', 'Prednisolone', 'Kahira', 'Tab', 'أقراص', '5mg', '1', NULL, 'about prednisolone systemic synthetic glucocorticoid immunosuppressant. mechanism of action of prednisolone the drug exerts it`s pharmacological action by penetrating and binding to cytoplasmic receptor protein and causes a structural change in steroid re', NULL, NULL, 2409, '2025-01-01', 'Prednisolone is indicated to treat endocrine, rheumatic, and hematologic disorders; collagen, dermatologic, ophthalmic, respiratory, and gastrointestinal diseases; allergic and edematous states; and other conditions like tuberculous meningitis.[L9542]', 'The short term effects of corticosteroids are decreased vasodilation and permeability of capillaries, as well as decreased leukocyte migration to sites of inflammation.[A187463] Corticosteroids binding to the glucocorticoid receptor mediates changes in gene expression that lead to multiple downstream effects over hours to days.[A187463]



Glucocorticoids inhibit neutrophil apoptosis and demargination; they inhibit phospholipase A2, which decreases the formation of arachidonic acid derivatives; they inhibit NF-Kappa B and other inflammatory transcription factors; they promote anti-inflammatory genes like interleukin-10.[A187463]



Lower doses of corticosteroids provide an anti-inflammatory effect, while higher doses are immunosuppressive.[A187463] High doses of glucocorticoids for an extended period bind to the mineralocorticoid receptor, raising sodium levels and decreasing potassium levels.[A187463]', 'Corticosteroids bind to the glucocorticoid receptor, inhibiting pro-inflammatory signals, and promoting anti-inflammatory signals.[A187463] Prednisolone has a short duration of action as the half life is 2.1-3.5 hours.[A187394] Corticosteroids have a wide therapeutic window as patients make require doses that are multiples of what the body naturally produces.[A187463] Patients taking corticosteroids should be counselled regarding the risk of hypothalamic-pituitary-adrenal axis suppression and increased susceptibility to infections.[A187463]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (11206, 'Tavegyl 1mg/10ml syrup 100ml', 'تافيجيل 1مجم/10مل شراب 100مل', '9', NULL, 'Antihistamine.antiallergy', 'Clemastine', 'Novartis > glaxo smithkline', 'Syrup', 'شراب', '1mg', '1', NULL, 'description an ethanolamine-derivative first generation histamine h1 antagonist used in hay fever rhinitis allergic skin conditions and pruritus. indication for the relief of symptoms associated with allergic rhinitis such as sneezing rhinorrhea pruritus', '6223002640218', NULL, 939, '2023-05-01', 'For the relief of symptoms associated with allergic rhinitis such as sneezing, rhinorrhea, pruritus and acrimation. Also for the management of mild, uncomplicated allergic skin manifestations of urticaria and angioedema. Used as self-medication for temporary relief of symptoms associated with the common cold.', 'Clemastine is a selective histamine H1 antagonist and binds to the histamine H1 receptor. This blocks the action of endogenous histamine, which subsequently leads to temporary relief of the negative symptoms brought on by histamine.', 'Clemastine is an antihistamine that also induces anticholinergic and sedative effects. Antihistamines competitively antagonize various physiological effects of histamine including increased capillary permeability and dilatation, the formation of edema, the "flare" and "itch" response, and gastrointestinal and respiratory smooth muscle constriction. Within the vascular tree, H1- receptor antagonists inhibit both the vasoconstrictor and vasodilator effects of histamine. Depending on the dose, H1- receptor antagonists can produce CNS stimulation or depression. Most antihistamines exhibit central and/or peripheral anticholinergic activity. Antihistamines act by competitively blocking H1- receptor sites. Antihistamines do not pharmacologically antagonize or chemically inactivate histamine, nor do they prevent the release of histamine.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30561, 'Fortazin cream', 'فورتازين كريم', '110', '50', 'Skin rejuvenating cream specialized for 1st & 2nd degree of burns wound healing ulcers and abrasions', 'Hyaluronic acid+calendula officinalis extract+propolis extract+vitamin e+phenoxyethanol+niacinamide+d-panthenol+dimethicone+iplant olive oil+tea tree oil+iplant rosemary+ulmus fulva bark extract+glycerin+propylene glycol+honey+allantoin+triglycerides+whea', 'Egyptian company for cosmetics e.c.c. > mertel group for health care', 'Cream', 'كريم', NULL, '1', NULL, '- fortazin is a premium formula specialized in reducing the healing time of 1st & 2nd degree of wounds and developing new blood vessels - possess a potential antioxidant activity which prevent oxidative damage and promote healing process - it has anti-in', NULL, NULL, 1143, '2025-08-13', 'The intra-articular preparations of hyaluronic acid are indicated for knee pain associated with osteoarthritis.[L32228] Hyaluronic acid is used in cosmetic applications to prevent and reduce the appearance of wrinkles on the face, and as a dermal filler to correct facial imperfections or other imperfections on other parts of the body.[L32208,L32223] It is frequently an ingredient in topical applications for wound healing and symptomatic treatment of skin irritation from various causes.[L32198] Hyaluronic acid may also be indicated in ophthalmological preparations or oral capsules to treat discomfort caused by dry eyes or conjunctivitis and for its protective qualities during and before eye surgery.[A230128,L32213] Finally, hyaluronic acid can be used off-label to coat the bladder for relief of interstitial cystitis symptoms.[A230533]', 'General principles and hyaluronic acid receptor binding 



Hyaluronic acid works by two basic mechanisms: serving as a passive structural molecule or serving as signaling molecule, depending on the molecule size. The physicochemical properties of high molecular weight HA contribute to passive structural effects, demonstrating hygroscopicity and viscoelasticity and improving hydration, water balance, and structural integrity. As a signalling  molecule interacting with proteins, HA causes several opposing effects based on molecular weight: pro- or anti-inflammatory effects, promotion or inhibition of cell migration, and activating or inhibiting cell division.[A230133] 



Hyaluronic acid exerts its therapeutic effects through binding to three primary types of cell surface receptors: CD44 (a membrane glycoprotein), the receptor for hyaluronate-mediated motility (RHAMM), and the Intercellular Adhesion Molecule 1 (ICAM-1). CD44 is considered the most widely distributed receptor for hyaluronic acid, demonstrating cellular interactions with osteopontin, collagen, and matrix metalloproteinases (MMPs).  High and low molecular weight hyaluronic acids demonstrate differing molecular and cellular mechanisms in their interaction with CD44 receptors. Some examples of these effects include modification of chondrocyte survival pathways in addition to alteration of apoptosis pathways.  Lymphatic vessel endothelial hyaluronan receptor (LYVE-1), and hyaluronic acid receptor for endocytosis (HARE), (also known as Stabilin-2) also bind to hyaluronic acid.[A230128] 



Hyaluronic acid for skin conditions and cosmetics



Hyaluronic acid''s anionic proprieties cause it to attract water and induce swelling, increasing tissue volume and skin structural integrity. The aging process is associated with reduced production of skin hyaluronic acid and collagen, causing the appearance of wrinkles and the loss of facial volume. Dermal fillers of hyaluronic acid replace lost tissue volume, imparting a full and youthful appearance to skin that has lost its elasticity. Hyaluronic acid fillers contain cross-linked hyaluronic acid particles, rendering a concentrated substance with resistance to various forms of physical and chemical breakdown. The cosmetic benefits of hyaluronic acid filler may last up to 6 months, depending on the brand and technique used for injection.[L32193] Additionally, dermal hyaluronic acid fillers are known to increase the production of fibroblasts[L32193], supporting wound healing and offering relief from irritating and inflammatory skin conditions.[A230118]





Hyaluronic acid for joint pain



Most cells in the human body are capable of synthesizing HA. It is a primary component of the extracellular matrix (ECM) and can be found in bone marrow, cartilage, and synovial fluid in joints.[A230128] In osteoarthritis, the concentration of naturally occurring hyaluronic acid gradually decreases, lowering the viscosity of synovial fluid that protects joints from excess friction. Administration of intra-articular hyaluronic acid increases viscosity of synovial joint fluid, reducing friction and subsequently relieving painful arthritic symptoms.[L32193]





Hyaluronic acid for ophthalmic conditions and ophthalmological procedures



Solutions of hyaluronic acid with a concentration greater than 0.1% moisturize the surface of the eyes to treat symptoms of dry eye while improving the stabilization of tear film, replenishing deficiencies of HA, reducing friction, and preventing binding of foreign substances to the ocular tissue.[A230128,L32193] Hyaluronic acid is frequently used during and after ophthalmological surgeries and plays important roles by virtue of its moisturizing, viscoelastic, and protective properties. It promotes tissue healing of the corneal epithelium and other parts of the eye following ophthalmological surgery, minimizing the risk of adhesions and free radical formation.[A230128]

', 'HA has long-acting lubricant, shock absorbing, joint stabilizing, and water balancing properties.[A230128,L32193] It is similar to the naturally occurring glycosaminoglycan (GAG) in joints. Hyaluronic acid works by acting as a lubricant and shock absorber, facilitating joint mobility and thereby reducing osteoarthritic pain.  Hyaluronic acid has antioxidative, anti-inflammatory, and analgesic effects.[A230128] The water-balancing properties and viscoelasticity of hyaluronic acid are beneficial in cosmetic injections, imparting volume and reducing the appearance of imperfections and wrinkles.[L32193] Due to the abovementioned properties, HA has a protective effect on the eyes and cornea.[A230133]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24849, 'Pharmix anti-bacterial body wash spray 100 ml', 'فارميكس سبراي مضاد للبكتريا 100مل', '45', NULL, 'Antiseptic', 'Ethyl alcohol 70%+chlorhexidine+chloroxylenol+aloe vera', 'Egyptian co. for cosmetics > pharmix', 'Spray', 'بخاخ', '100 ml', '1', NULL, 'leaves body feeling fresh', NULL, NULL, 694, '2022-07-30', 'Investigated for use/treatment in burns and burn infections, cardiac surgery, inflammatory disorders (unspecified), ischemic reperfusion injury, and sepsis and septicemia.', 'CTI-01 inhibits the systemic release of cytokines, such as TNF-alpha and HMGB1, which promote the body''s inflammatory response. The over-expression of these cytokines has been linked to diseases that occur in critical care settings, such as severe organ damage following cardiopulmonary bypass (CPB) and post-operative ileus following abdominal surgery.', NULL, 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26052, 'Riba 200mg 10 caps.', 'ريبا 200مجم 10 كبسول', '30', NULL, 'Antiviral.nucleoside', 'Ribavirin', 'Global napi pharmaceuticals', 'Cap', 'كبسولة', '200mg', '1', NULL, 'about ribavirin a nucleoside antimetabolite triazole derivative antiviral. mechanism of action of ribavirin ribavirin is a nucleoside analogue and it is phosphorylated by adenosine kinase enzyme in the host cell to form ribavirin monophosphate.this will i', '6223002145164', NULL, 978, '2022-12-08', 'Indicated for the treatment of chronic Hepatitis C virus (HCV) infection in combination with other antiviral agents with the intent to cure or achieve a sustained virologic response (SVR). Typically added to improve SVR and reduce relapse rates [A19644].



The addition of ribavirin in Technivie therapy indicated for treating HCV genotype 1a and 4 infections is recommended in patients with or without cirrhosis. 



Resistance: viral genetic determinants resulting in variable response to ribavirin therapy has not been yet determined. ', 'Ribavirin is reported to have several mechanism of actions that lead to inhibition of viral RNA and protein synthesis. 

After activation by adenosine kinase to ribavirin mono-, di-, and triphosphate metabolites. Ribavirin triphosphate (RTP) is the predominant metabolite which directly inhibits viral mRNA polymerase by binding to the nucleotide binding site of the enzyme. This prevents the binding of the correct nucleotides, leading to a reduction in viral replication or to the production of defective virions [A19645]. RTP also demonstrates an inhibitory action on viral mRNA guanylyltransferase and mRNA 2′-O-methyltransferase of dengue virus. Inhibition of these enzymes disrupts the posttranslational capping of the 5′ end of viral mRNA through ribavirin being incorporated at the 5′ end in place of guanosine and preventing the cap methylation step. 



Inhibition of host inosine monophosphate dehydrogenase (IMPDH) and subsequent depletion of GTP pool is proposed to be another mechanism of action of ribavirin. IMPDH catalyzes the rate-limiting step where inosine 5′-monophosphate is converted to xanthine monophosphate during guanosine monophosphate (GMP) synthesis. GMP is later converted to guanosine triphoshpate (GTP). Ribavirin monophosphate mimics inosine 5′-monophosphate and acts as a competitive inhibitor of IMPDH. Inhibited de novo synthesis of guanine nucleotides and decreased intracellular GTP pools leads to a decline in viral protein synthesis and limit replication of viral genomes [A19645].



Ribavirin acts as a mutagen in the target virus to cause an ''error catastrophe'' due to increased viral mutations. RTP pairs with cytidine triphosphate or uridine triphosphate with equal efficiency and to block HCV RNA elongation. It causes premature termination of nascent HCV RNA and increases mutagenesis by producing defective virions [A19645].



Ribavirin also exerts an immunomodulatory action of the host to the virus by shifting a Th2 response in favor of a Th1 phenotype. Th2 response and production of type 2 cytokines such as IL-4, IL-5, and IL-10 stimulates the humoral response which enhances immunity toward the virus [A19645]. Ribavirin enhanced induction of interferon-related genes, including the interferon-α receptor, and down-regulation of genes involved in interferon inhibition, apoptosis, and hepatic stellate cell activation in vitro [A19644]. ', 'Ribavirin mediates direct antiviral activity against a number of DNA and RNA viruses by increasing the mutation frequency in the genomes of several RNA viruses. It is a member of the nucleoside antimetabolite drugs that interfere with duplication of the viral genetic material. The drug inhibits the activity of the enzyme RNA dependent RNA polymerase, due to its resemblence to building blocks of the RNA molecules. ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27925, 'Tasigna 200 mg 28 caps.', 'تاسيجنا 200مجم 28 كبسولة', '6,735', '5700', 'Antitumors', 'Nilotinib', 'Novartis', 'Cap', 'كبسولة', '200 mg', '4', NULL, 'indication for the potential treatment of various leukemias including chronic myeloid leukemia (cml). pharmacodynamics nilotinib is a transduction inhibitor that targets bcr-abl c-kit and pdgf for the potential treatment of various leukemias including chr', NULL, NULL, 1920, '2024-07-29', 'For the potential treatment of various leukemias, including chronic myeloid leukemia (CML).', 'Chronic myelogenous leukaemia (CML) is caused by the BCR-ABL oncogene. Nilotinib inhibits the tyrosine kinase activity of the BCR-ABL protein. Nilotinib fits into the ATP-binding site of the BCR-ABL protein with higher affinity than imatinib, over-riding resistance caused by mutations. The ability of AMN107 to inhibit TEL-platelet-derived growth factor receptor-beta (TEL-PDGFRbeta), which causes chronic myelomonocytic leukaemia, and FIP1-like-1-PDGFRalpha, which causes hypereosinophilic syndrome, suggests potential use of AMN107 for myeloproliferative diseases characterised by these kinase fusions (Stover et al, 2005; Weisberg et al, 2005). AMN107 also inhibits the c-Kit receptor kinase, including the D816V-mutated variant of KIT, at pharmacologically achievable concentrations, supporting potential utility in the treatment of mastocytosis, and gastrointestinal stromal tumours (Weisberg et al, 2005; von Bubnoff et al, 2005; Gleixner et al, 2006).', 'Nilotinib is a transduction inhibitor that targets BCR-ABL, c-kit and PDGF, for the potential treatment of various leukemias, including chronic myeloid leukemia (CML).', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28215, 'Tiralepsy 1000mg 30 f.c. tab.', 'تيراليبسي 1000مجم 30 قرص', '261', '231', 'Anti-epileptic.pyrrolidine', 'Levetiracetam', 'El-obour > protech pharma', 'Tab', 'أقراص', '1000mg', '3', NULL, 'about levetiracetam it is s-enantiomer of etiracetam. anticonvulsant medication used to treat epilepsy. mechanism of action of levetiracetam the exact mechanism for levetiracetam is unknown. however the drug binds to a synaptic vesicle protein sv2a which', '6224000866365', NULL, 1270, '2025-02-04', 'Levetiracetam is indicated as an adjunctive therapy in the treatment of partial onset seizures in epileptic patients who are one month of age and older. Additionally, it is indicated as an adjunct in the treatment of myoclonic seizures in patients with juvenile myoclonic epilepsy who are 12 years of age and older, and in primary generalized tonic-clonic seizures in patients with idiopathic generalized epilepsy who are 6 years of age and older.[L8606]



Levetiracetam is also available as an orally dissolvable tablet that is indicated as an adjunct in the treatment of partial onset seizures in patients with epilepsy who are 4 years of age and older and weigh more than 20kg.[L8609]', 'The exact mechanism through which levetiracetam exerts its anti-epileptic effects is unclear, but is thought to be unique amongst other anti-epileptic medications. Current knowledge suggests that levetiracetam’s binding to synaptic vesicle protein 2A (SV2A) is a key driver of its action. SV2A is a membrane-bound protein that is found on synaptic vesicles and is ubiquitous throughout the CNS[A12546] - it appears to play a role in vesicle exocytosis[L8606,L8615] and in the modulation of synaptic transmission by increasing the available amount of secretory vesicles available for neurotransmission.[A12552] Stimulation of pre-synaptic SV2A by levetiracetam may inhibit neurotransmitter release,[A12551] but this action does not appear to affect normal neurotransmission. This has led to the suggestion that levetiracetam exclusively modulates the function of SV2A only under pathophysiological conditions.[A12546] Levetiracetam and related analogues showed a correlation between affinity for SV2A and anti-epileptic potency, further suggesting that action at this site contributes to the anti-epileptic activity of the drug.[L8606,L8615]



Levetiracetam has also been shown to indirectly affect GABAergic neurotransmission (despite having no direct effect on GABAergic or glutamatergic receptors) and modulate ionic currents.[A12552] Similarly, levetiracetam has been shown in vitro to inhibit N-type calcium channels.[A16562] How, or even if, these actions are implicated in its anti-epileptic action have yet to be elucidated.', 'Levetiracetam appears to prevent seizure activity via the selective inhibition of hypersynchronized epileptiform burst firing without affecting normal neuronal transmission, though the exact mechanism through which this occurs is unclear.[L8606,L8615] The therapeutic index of levetiracetam is wide,[L8615,A185918] making it relatively unique amongst other anti-epileptic medications.



Anti-epileptic drugs, including levetiracetam, may increase the risk of suicidal ideation or behaviour - patients taking levetiracetam should be monitored for the emergence or worsening of depressive symptoms, suicidal ideation, and behavioural abnormalities.[L8606,L8600,L8615]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (33574, 'Salusca syrup 120 ml', 'سالوسكا 120 مل', '75', '39', 'Multivitamin', 'Calcium+zinc+magnesium+vitamin d3', 'Salus', 'Syrup', 'شراب', '120 ml', '1', NULL, NULL, NULL, NULL, 1735, '2025-07-31', NULL, NULL, NULL, NULL, 0, 0, 0);
