INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (5889, 'Solantra 50+ spf sun block aquafluid 100 ml', 'سولانترا 50+ اس بي اف صن بلوك اكوافلويد 100 مل', '300', NULL, 'Sun block', 'Sun protection formula', 'Mash premiere', 'Sol', 'محلول', '100 ml', '1', NULL, NULL, NULL, NULL, 807, '2025-07-13', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (10632, 'Sofenacin 1mg/ml oral susp. 120 ml', 'سوفيناسين 1مجم/مل معلق بالفم 120 مل', '88', '67', 'Urinary incontinence.antispasmodics.muscarinic antagonists', 'Solifenacin succinate', 'Marcyrl co.', 'Susp', 'معلق', '1mg', '1', 'مضاد لتقلصات الجهاز البولي -- علاج سلس البول', NULL, '6223003572457', NULL, 1786, '2024-07-30', 'Solifenacin tablets are indicated to treat an overactive bladder with urinary incontinence, urgency, and frequency.[L7511]', 'Solifenacin is a competitive muscarinic receptor antagonist.[L7511] It has the highest affinity for M3, M1, and M2 muscarinic receptors.[A181247] 80% of the muscarinic receptors in the bladder are M2, while 20% are M3.[A181262] Solifenacin''s antagonism of the M3 receptor prevents contraction of the detrusor muscle, while antagonism of the M2 receptor may prevent contraction of smooth muscle in the bladder.[A181262]', 'Solifenacin antagonizes the M2 and M3 muscarinic receptors in the bladder to treat an overactive bladder.[L7511,A181262] It has a long duration of action as it is usually taken once daily.[L7511] Patients taking solifenacin should be aware of the risks of angioedema and anaphylaxis.[L7511]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (7383, 'Artava 15 cap', 'ارتافا 15 كبسولة', '200', NULL, NULL, NULL, NULL, 'Cap', 'كبسولة', NULL, '1', NULL, NULL, '6224002520074', NULL, 968, '2025-09-12', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14655, 'Avanjoy 100 mg 4 tabs.', 'افانجوي 100 مجم 4 اقراص', '26', NULL, 'Tonic for men', 'Avanafil', 'Eva pharma', 'Tab', 'أقراص', '100 mg', '1', NULL, 'avanafil is a pde5 inhibitor approved for erectile dysfunction by the fda on april 27 2012 and by ema on june 21 2013. avanafil is known by the trademark names stendra and spedra and was developed by vivus inc. avanafil acts by inhibiting a specific phosp', NULL, NULL, 1520, '2022-07-16', 'Avanafil is indicated for the treatment of erectile dysfunction.[L32058]', 'Avanafil inhibits the cGMP-specific phosphodiesterase type 5 (PDE5) which is responsible for the degradation of cGMP in the corpus cavernosum located around the penis. Sexual arousal results in the local release of nitric oxide, which in turn stimulates the enzyme guanylate cyclase to produce cGMP. Elevated levels of cGMP result in local smooth muscle relaxation and increased blood flow to the penis (i.e. an erection).[L32058,L32113]



As PDE5 inhibitors like avanafil require the endogenous release of nitric oxide in order to exert their pharmacologic effect, they have no effect on the user in the absence of sexual stimulation/arousal.[L32058,L32113]', 'Avanafil is a strong competitive inhibitor of phosphodiesterase 5 (PDE5) with a demonstrated _in vitro_ IC<sub>50</sub> of 5.2 nM.[L32113] Its inhibitory effects on PDE5 are 100-fold more potent than on PDE6 and >1000-fold more potent than on other PDE enzymes,[L32058] meaning it is less likely to cause visual disturbances and cardiovascular adverse effects when compared with less selective PDE5 inhibitors such as [sildenafil] and [vardenafil].[L32113] It has a relatively quick onset of action allowing for administration as early as 15 minutes prior to sexual activity.[L32058]



PDE5 inhibitors like avanafil can cause significant drug interactions when administered alongside certain antihypertensive agents (e.g. alpha blockers, substantial amounts of alcohol).[L32058] PDE5 inhibitors have also been associated with the development of non-arteritic anterior ischemic optic neuropathy (NAION), a rare condition that typically presents as sudden loss of vision in one or both eyes and appears to be more common in patients with a "crowded" optic disc. Patients presenting with any degree of vision loss should immediately discontinue use of all PDE5 inhibitors and seek medical attention.[L32058] In some jurisdictions, a history of NAION or other degenerative retinal disorders is considered a contraindication to avanafil therapy.[L32233]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16974, 'Cutemol baby lotion 150 ml', 'كيوتمول بيبي لوشن 150 مل', '100', NULL, 'Soothing topical', NULL, 'Kempetro for chemical industries > novell pharma', 'Lotion', 'لوشن', '150 ml', '1', NULL, NULL, NULL, NULL, 692, '2022-08-04', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20581, 'Hyalsolo vaginal gel 30 gm', 'هيالسولو جل مهبلي 30 جرام', '895', '490', 'Vaginal care', 'Soyabean phospholipids+sodium hyaluronate+phenoxyethanol+sodium benzoat+alcohol+na hydroxide+arginin', 'Crab sinergy > abbott laboratories', 'Gel', 'جل', '30 gm', '1', 'جل مهبلي للعناية الشخصية للسيدات لاستعادة الليونة والطبيعة المطاطية للانسجة', 'moisturizing vaginal gel (medical device): - vaginal dryness burning irritation - restoring physiological elasticity / natural moisturizing of the mucosa / without side effects', '8033776700944', NULL, 8587, '2025-05-16', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20559, 'Human albumin octapharma 20% i.v.infusion (hospitals price)', 'البيومين بشري اوكتافارما 20% محلول وريدي  مستشفيات', '545', NULL, 'Human albumin', 'Human albumin', 'Octapharma > pharma over seas', 'Unknown', 'غير محدد', '20%', '1', NULL, NULL, NULL, NULL, 1494, '2022-07-11', 'Human immunoglobulin G is indicated for the following conditions:



### Primary Immunodeficiency

- for the treatment of primary immunodeficiency in adult and pediatric patients[L39920, L39930, L39935, L39940, L39945, L39950, L39955, L39960, L39965, L39975, L39980, L40029, L40034, L40044]

- in combination with [hyaluronidase (human recombinant)] for the treatment of primary immunodeficiency in patients ≥2 years of age.[L42760]



### Immune Thrombocytopenic Purpura (ITP)

- for the treatment of acute or chronic immune thrombocytopenic purpura in adult and pediatric patients[L39940, L39950, L39960, L39965, L39970, L39975, L39980]



### Chronic Inflammatory Demyelinating Polyneuropathy (CIDP)

- for the treatment of CIDP in adult patients[L39955, L39965, L39975, L39980, L40034]

- in combination with hyaluronidase (human recombinant) as a maintenance therapy in adults with chronic inflammatory demyelinating polyneuropathy (CIDP)[L40039]



### Multifocal Motor Neuropathy (MMN)

- for maintenance therapy to improve muscle strength and disability in adult patients with MMN[L39955]



### Prophylaxis of Bacterial Infection

- for the prevention of bacterial infections in patients with hypogammaglobulinemia and/or B-cell chronic lymphocytic leukemia[L39960]



### Coronary Artery Aneurysm Associated With Kawasaki Syndrome

- for the prevention of coronary artery aneurysms in pediatric patients with Kawasaki syndrome[L39960]



### Dermatomyositis

- for the treatment of dermatomyositis in adult patients[L39970]

', 'IVIg interacts with a number of different components of the immune system, including cytokines, complement, Fc receptors and several cell surface immunocompetent molecules. IVIg also impacts different effector cells of the immune system (B and T lymphocytes, dendritic cells, etc.) and regulates a wide range of genes. Its main mechanism of actions are believed to be Fc-dependent and F(ab'')2-dependent. IVIg competitively blocks gamma Fc receptors, preventing the binding and ingestion of phagocytes and suppressing platelet depletion. IVIg contains a number of different antobodies, which prevent infection by attaching to the surface of invading pathogens and aiding in their disposal before they can infect cells. Antibodies remove pathogens via complement activation, agglutination or precipitation, pathogen receptor blocking, macrophage “tagging” or neutralization (via binding) of pathogen toxins. Intact IVIg and F(ab′)2 fragments of IVIg can also neutralize the activity of various autoantibodies. By triggering the production of interleukin-1 receptor antagonist, IVIg modulates of the production of cytokines and cytokine antagonists. It also prevents the generation of the C5b-9 membrane attack complex and subsequent complement-mediated tissue damage by binding active complement components.', 'Used as a replacement therapy in inherited humoral immunodeficiency disorders such as severe combined immunodeficiency syndrome, x-linked agammaglobulinemia, and Wiskott-Aldrich Syndrome. The immunoglobulins target, bind and kill bacterial cells as well as viral particles. IgG is the monomeric immunoglobulin of which there are four subclasses (IgG1, IgG2, IgG3 and IgG4) in differing abundances (66%, 23%, 7% and 4%). IgAs represent about 15% of the immunoglobulins in the blood. These target inhaled or ingested pathogens.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20207, 'Hair-clinic dry hair shampoo 125 gm', 'هير كلينيك شامبو للشعر الجاف 125 جم', '28', NULL, 'Hair care', NULL, 'Borg', 'Amp', 'أمبول', '125 gm', '1', NULL, 'hair-clinic anti-dandruff shampoo with salicylic acid provides these following advantages: *it guarantees maximum effectiveness by otopiroxï- antimicrobial & anti oxidative actions. *eliminates moderate to heavy dandruff problems. *eliminates flakes and i', '6223002572137', NULL, 689, '2022-12-05', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19010, 'Ferro-z syrup 120 ml', 'فيرو -زد شراب 120 مل', '11', NULL, 'Anemia.iron supplement', 'Cyanocobalamine+iron+zinc', 'Grand pharma > novell pharma', 'Syrup', 'شراب', '120 ml', '1', NULL, NULL, NULL, NULL, 778, '2022-07-18', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18508, 'Erbitux 100mg/20ml vial', 'اربيتوكس 100مجم/20 مل فيال', '3,631', '3026', 'Antineoplastic', 'Cetuximab', 'Boehringer ingelheim > serono scientific office', 'Vial', 'فيال', '100mg', '1', NULL, 'cetuximab is an epidermal growth factor receptor (egfr) inhibitor used for the treatment of metastatic colorectal cancer metastatic non-small cell lung cancer and head and neck cancer. cetuximab is a chimeric (mouse/human) monoclonal antibody given by int', NULL, NULL, 1316, '2025-04-05', 'Cetuximab indicated for the treatment of locally or regionally advanced squamous cell carcinoma of the head and neck in combination with radiation therapy. It is indicated for treating a recurrent locoregional disease or metastatic squamous cell carcinoma of the head and neck in combination with platinum-based therapy with fluorouracil. It is indicated for recurrent or metastatic squamous cell carcinoma of the head and neck progressing after platinum-based therapy.[L30448]



Cetuximab is also indicated for K-Ras wild-type, EGFR-expressing, metastatic colorectal cancer as determined by an FDA-approved test in combination with FOLFIRI, a chemotherapy combination that includes [leucovorin], [fluorouracil], and [irinotecan]; in combination with irinotecan in patients who are refractory to irinotecan-based chemotherapy; or as monotherapy in patients who have failed oxaliplatin- and irinotecan-based chemotherapy or who are intolerant to irinotecan.[L30448]



Additionally, cetuximab is also indicated for metastatic colorectal cancer that is BRAF V600E mutation-positive (as determined by an FDA-approved test) in combination with [encorafenib] but only after prior therapy.[L39045]



Cetuximab is not indicated for the treatment of Ras-mutant colorectal cancer or when the results of the Ras mutation tests are unknown.[L30448]', 'The epidermal growth factor receptor (EGFR) is a transmembrane glycoprotein and a type I receptor tyrosine kinase expressed on both normal and malignant cells. It has been investigated as a therapeutic target for anticancer treatment, as it is often upregulated in cancer types, including head and neck, colon, and rectal cancers.[L30448] When activated by its ligand, EGFR undergoes a conformational change and dimerization to form homodimers or heterodimers with another member of the ErbB family of receptors. Dimerization of EGFR activates the intracellular tyrosine kinase region of EGFR and promotes autophosphorylation, initiating a series of downstream signalling cascades, including cell differentiation, proliferation, migration, angiogenesis, and apoptosis. This EGFR signalling pathway is often dysregulated in cancer cells, leading to aberrant cell growth and enhanced cell survival.[L31418]



Cetuximab is a monoclonal antibody that binds specifically to the EGFR on both normal and tumour cells to competitively inhibit the binding of epidermal growth factor (EGF) and other ligands that are produced by normal and tumour tissue epithelial cells.[A11, L30448] Upon binding to domain III of EGFR - which is the binding site for its growth factor ligands - cetuximab prevents the receptor from adopting an extended conformation and thereby inhibits EGFR activation, as well as phosphorylation and activation of receptor-associated kinases (MAPK, PI3K/Akt, Jak/Stat).[A11, A228078] Inhibition of the EGFR signalling pathway ultimately leads to inhibition of cell cycle progression, cell survival pathways, and tumour cell motility and invasion.[A227963] Cetuximab also induces cell apoptosis and decreases matrix metalloproteinase and vascular endothelial growth factor (VEGF) production.[A228078, L30448] _In vitro_, cetuximab was shown to inhibit tumour angiogenesis.[A227978] Binding of cetuximab to EGFR also results in internalization of the antibody-receptor complex, leading to an overall downregulation of EGFR expression.[A227973]



K-ras is a small G-protein downstream of EGFR that plays an important role in promoting the EGFR signalling cascade: in some malignant cells, K-ras can acquire activating mutations in exon 2 [L31418] and thus be continuously active regardless of EGFR regulation.[L30448] Since mutant Ras proteins can isolate the pathway from the effect of EGFR, K-Ras mutations can render EGFR inhibitors like cetuximab ineffective in exerting anti-tumour effects.[L30448, L31418] Cetuximab is thus only limited in its use for K-Ras wild-type, EGFR-expressing cancers.[L30448]', 'Cetuximab is an anticancer agent that works by inhibiting the growth and survival of epidermal growth factor receptor (EGFR)-expressing tumour cells with high specificity and higher affinity than epidermal growth factor (EGF) and transforming growth factor-alpha (TGF-α), which are natural ligands of EGFR.[L31418] Cetuximab works by inhibiting the growth and survival of EGFR-positive tumours.[A227963] _In vitro_, it promotes antibody-dependent cellular cytotoxicity (ADCC) against certain human tumour types. On the contrary, cetuximab does not exert its anti-tumour effects on human tumour xenografts lacking EGFR expression.[A227963, L30448]



Cetuximab potentiates the cytotoxic effects of chemotherapeutics and radiation therapy when used in combination.[L30448] In human tumour xenograft models in mice, cetuximab and irinotecan synergistically inhibited the growth of orthotopic anaplastic thyroid carcinoma xenografts _in vitro_ and _in vivo_. Cetuximab potentiated the _in vitro_ anti-proliferative and pro-apoptotic effect of irinotecan and achieved 93% _in vivo_ inhibition of tumour growth when combined with irinotecan, compared to 77% and 79% inhibition when cetuximab and irinotecan were used alone, respectively.[A227978]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16412, 'Cisplatine mylan 100mg/100ml vial(n/a)', 'سيسبلاتين ميلان 100 مجم/100 مل فيال', '110', NULL, 'Antineoplastic', 'Cisplatin', 'Mylan s.a.s-france > ramco', 'Vial', 'فيال', '100mg', '1', NULL, NULL, NULL, NULL, 653, '2022-07-30', 'For the treatment of metastatic testicular tumors, metastatic ovarian tumors and advanced bladder cancer.', 'Alkylating agents work by three different mechanisms: 1) attachment of alkyl groups to DNA bases, resulting in the DNA being fragmented by repair enzymes in their attempts to replace the alkylated bases, preventing DNA synthesis and RNA transcription from the affected DNA, 2) DNA damage via the formation of cross-links (bonds between atoms in the DNA) which prevents DNA from being separated for synthesis or transcription, and 3) the induction of mispairing of the nucleotides leading to mutations.', 'Cisplatin is an antineoplastic in the class of alkylating agents and is used to treat various forms of cancer. Alkylating agents are so named because of their ability to add alkyl groups to many electronegative groups under conditions present in cells. They stop tumor growth by cross-linking guanine bases in DNA double-helix strands - directly attacking DNA. This makes the strands unable to uncoil and separate. As this is necessary in DNA replication, the cells can no longer divide. In addition, these drugs add methyl or other alkyl groups onto molecules where they do not belong which in turn inhibits their correct utilization by base pairing and causes a miscoding of DNA. Alkylating agents are cell cycle-nonspecific. Alkylating agents work by three different mechanisms all of which achieve the same end result - disruption of DNA function and cell death.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15759, 'Solo cream 60 gm', 'سولو كريم 60 جم', '90', '80', NULL, NULL, 'Health well egypt', 'Cream', 'كريم', '60 gm', '1', NULL, NULL, NULL, NULL, 827, '2025-08-03', NULL, NULL, NULL, NULL, 0, 0, 0);
