INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29577, 'Pastofeet cream 60 gm', 'باستوفيت كريم لتشققات القدمين 60 جم', '60', NULL, NULL, 'Urea', 'Pasteur skin care', 'Cream', 'كريم', '60 gm', '1', 'لتشققات القدمين', NULL, '6224009617067', NULL, 789, '2024-07-17', 'Urea is used topically for debridement and promotion of normal healing of hyperkeratotic surface lesions, particularly where healing is retarded by local infection, necrotic tissue, fibrinous or purulent debris or eschar. Urea is useful for the treatment of hyperkeratotic conditions such as dry, rough skin, dermatitis, psoriasis, xerosis, ichthyosis, eczema, keratosis, keratoderma, corns and calluses, as well as damaged, devitalized and ingrown nails.[L41484]', NULL, 'Urea is a keratolytic emollient that works to treat or prevent dry, rough, scaly, itchy skin.[L41484]', 'DrugBank', 0, 0, 0);
