INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21717, 'Lergopan 5 mg 10 f.c.tab.', 'ليرجوبان 5 مجم 10 اقراص', '8', NULL, 'Antihistamine.non-sedative.third-generation', 'Levocetirizine', 'Pharopharma', 'Tab', 'أقراص', '5 mg', '1', NULL, NULL, NULL, NULL, 741, '2022-07-27', 'Levocetirizine is indicated to treat symptoms of perennial allergic rhinitis and uncomplicated skin manifestations of chronic idiopathic urticaria.[L7694] It is also used over the counter for a variety of mild allergy symptoms.[A181748]', 'Levocetirizine selectively inhibits histamine H<sub>1</sub> receptors.[L7694] This action prevents histamine from activating this receptor and causing effects like smooth muscle contraction, increased permeability of vascular endothelium, histidine uptake in basophils, stimulation of cough receptors, and stimulation of flare responses in the nervous system.[A181790]', 'Levocetirizine is a second generation histamine H<sub>1</sub> antagonist used to treat various allergic symptoms.[A181748,A181790,L7694] It has a long duration of action as it is generally taken once daily, and a wide therapeutic window as animal studies show the maximal nonlethal dose is over 100x a normal dose.[L7694] Patients are cautioned to avoid tasks that require complete alertness, avoid alertness, and use caution in patients with factors predisposing urinary retention.[L7694]', 'DrugBank', 0, 0, 0);
