INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24214, 'Orangedaily anti-aging retinol complex 28 ml', 'اورانجدايلي مانع للشيخوخة ريتينول كومبليكس 28مل', '378', NULL, 'Skin care', NULL, 'Ampac usa inc. > spring', 'Unknown', 'غير محدد', '28 ml', '1', NULL, 'this product combines three different types of vitamin a - retinol retinol acetate and retinyl palmitate - in our exclusive tri retinol complex. the superior triple action of the vitamin a contained in our tri retinol complexworks effectively to stimulate', NULL, NULL, 643, '2022-07-25', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19957, 'Glutamic 500mg 30 tab.', 'جلوتاميك 500مجم 30 قرص', '16', NULL, NULL, 'Glutamic acid', 'Sigma > horizon pharma', 'Tab', 'أقراص', '500mg', '3', NULL, 'about glutamic acid miscellaneous gastrointestinal agent non-essential amino acid treatment of hypochlorhydria and achlorhydria.', NULL, NULL, 720, '2022-08-03', 'Considered to be nature''s "Brain food" by improving mental capacities; helps speed the healing of ulcers; gives a "lift" from fatigue; helps control alcoholism, schizophrenia and the craving for sugar.', 'Glutamate activates both ionotropic and metabotropic glutamate receptors. The ionotropic ones being non-NMDA (AMPA and kainate) and NMDA receptors. Free glutamic acid cannot cross the blood-brain barrier in appreciable quantities; instead it is converted into L-glutamine, which the brain uses for fuel and protein synthesis. It is conjectured that glutamate is involved in cognitive functions like learning and memory in the brain, though excessive amounts may cause neuronal damage associated in diseases like amyotrophic lateral sclerosis, lathyrism, and Alzheimer''s disease. Also, the drug phencyclidine (more commonly known as PCP) antagonizes glutamate at the NMDA receptor, causing behavior reminiscent of schizophrenia. Glutamate in action is extremely difficult to study due to its transient nature.', 'In addition to being one of the building blocks in protein synthesis, it is the most widespread neurotransmitter in brain function, as an excitatory neurotransmitter and as a precursor for the synthesis of GABA in GABAergic neurons.

', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1393, 'Benzanil gel 80 gm', 'بنزانيل جل 80 جم', '35', '24', 'Scabicide', 'Benzyl alcohol+benzyl benzoate', 'El nile.', 'Gel', 'جل', '80 gm', '1', 'مضاد للجرب', NULL, '6221077072118', NULL, 4032, '2025-06-15', 'Used to kill lice and the mites responsible for the skin condition scabies.', 'Benzyl benzoate exerts toxic effects on the nervous system of the parasite, resulting in its death. It is also toxic to mite ova, though its exact mechanism of action is unknown. In vitro, benzyl benzoate has been found to kill the Sarcoptes mite within 5 minutes.', 'Benzyl benzoate is one of the older preparations used to treat scabies. Scabies is a skin infection caused by the mite sarcoptes scabiei. It is characterised by severe itching (particularly at night), red spots, and may lead to a secondary infection. Benzyl benzoate is lethal to this mite and so is useful in the treatment of scabies. It is also used to treat lice infestation of the head and body. Benzyl benzoate is not the treatment of choice for scabies due to its irritant properties.', 'DrugBank', 0, 0, 0);
