INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (22324, 'Marovarex 120 mg 14 dela. rel. caps.', 'ماروفاركس 120مجم 14 كبسولة ممتد المفعول', '1,765', NULL, 'Multiple sclerosis', 'Dimethyl fumarate', 'Hikma pharma', 'Cap', 'كبسولة', '120 mg', '1', 'علاج المرضى البالغين الذين يعانون من التصلب المتعدد والصدفية', 'dimethyl fumarate is an anti-inflammatory. it is indicated for multiple sclerosis patients with relapsing forms and is also being investigated for the treatment of psoriasis. the mechanism of action of dimethyl fumarate in multiple sclerosis is not well u', NULL, NULL, 2541, '2022-07-04', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Dimethyl fumarate is an agent indicated for the treatment of relapsing forms of multiple sclerosis.[A253942,L43752] The mechanism of action of dimethyl fumarate in multiple sclerosis is not well understood. It is thought to involve dimethyl fumarate degradation to its active metabolite monomethyl fumarate (MMF) then MMF up-regulates the Nuclear factor (erythroid-derived 2)-like 2 (Nrf2) pathway that is activated in response to oxidative stress.[L43752] Dimethyl fumarate is marketed under the brand name Tecfidera, and it was the third oral disease-modifying agent for multiple sclerosis approved by the FDA, following [fingolimod] and [teriflunomide].[A253917] Prior to its FDA approval, dimethyl fumarate had been used in Germany for treatment of psoriasis.[A253917]', '["L04AX07"]', '[]');
