INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (22549, 'Mepaclind 100mg 3 vaginal ovules', 'ميباكليند 100 مجم 3 لبوس مهبلي', '8', NULL, 'Antibiotic.lincomycins', 'Clindamycin', 'Safe pharma > mepaco', 'Unknown', 'غير محدد', '100mg', '3', NULL, 'mechanism clindamycin works by preventing bacteria from producing proteins that are essential to them. without these proteins the bacteria cannot grow replicate and increase in numbers. the remaining bacteria eventually die or are destroyed by the immune', '6223000173138', NULL, 1187, '2022-07-09', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Clindamycin is a semi-synthetic lincosamide antibiotic used in the treatment of a variety of serious infections due to susceptible microorganisms[L11599,L11596] as well as topically for acne vulgaris.[L11593] It has a relatively narrow spectrum of activity that includes anaerobic bacteria as well as gram-positive cocci and bacilli and gram-negative bacilli.[A190621] Interestingly, clindamycin appears to carry some activity against protozoans, and has been used off-label in the treatment of toxoplasmosis, malaria, and babesiosis.[A190657]

Clindamycin is derived from, and has largely replaced, [lincomycin], a naturally occurring lincosamide and the eponymous member of this antibiotic class, due to its improved properties over the parent compound. The name lincomycin is derived from Lincoln, Nebraska, where it was first isolated from _Streptomyces lincolnensis_ found in a soil sample.[A190621]', '["D10AF01", "J01FF01", "D10AF51", "G01AA10"]', '[]');
