INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (22757, 'Milnacip 50 mg 30 h.g. caps.', 'ميلناسيب 50مجم 30 كبسولة', '66', NULL, 'Psychiatric.antidepressants', 'Milnacipran hydrochloride', 'Andalous pharma', 'Cap', 'كبسولة', '50 mg', '3', 'علاج للاكتئاب', 'milnacipran is a serotonin-norepinephrine reuptake inhibitor (snri). exactly how milnacipran works to treat fibromyalgia is not known. it may work by restoring the balance of certain natural substances in the brain (serotonin and norepinephrine).', NULL, NULL, 1006, '2022-05-27', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Milnacipran is a selective serotonin and norepinephrine reuptake inhibitor (SNRI) and like many agents in this category was originally developed for and continues to be approved and indicated for the treatment of depression [F3928, F3934, A175786, A175951]. Furthermore, in 2009 the US FDA approved milnacipran for the additional indication of treating fibromyalgia [F3925], although other regional regulatory authorities like the EMA, among others, have not yet approved the agent for such treatment, citing lack of robust evidence of efficacy, insufficient demonstration of maintenance of effect, and other concerns [F3928, F3934]. Nevertheless, milnacipran demonstrates a somewhat unique characteristic among SNRIs to elicit a relatively balanced reuptake inhibition of both serotonin and noradrenaline, with a somewhat increased preference for noradrenaline reuptake inhibition - which is potentially a point of interest given the plausible proposal that noradrenaline plays an important role in the mitigation of pain signals in the descending inhibitory pain pathways in the brain and spinal cord [A175759, A175843, A175846].

Moreover, recent research has shown that the levorotatory enantiomer of milnacipran, levomilnacipran, may have the capacity to inhibit the activity of beta-site amyloid precursor protein cleaving enzyme-1 (BACE-1), which has investigationally been associated with β-amyloid plaque formation - making the agent a possible course of treatment for Alzheimer''s disease [A175957].', '["N06AX17"]', '[]');
