INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17253, 'Dentocaine 7.5% dental gel 10 gm', 'دنتوكايين 7.5% جل للاسنان 10 جم', '4', NULL, 'Analgesic.local anesthetic', 'Benzocaine', 'Pharco', 'Gel', 'جل', '7.5%', '1', NULL, 'about benzocaine ester local anesthetic mechanism of action of benzocaine it binds to sodium channel and reversibly stabilizes the neuronal membrane which decreases its permeability to sodium ions. this results the blocking of depolarization of the neuron', '6223000013267', NULL, 1391, '2022-12-04', 'Benzocaine is indicated for local anesthesia in dentistry, minor trauma, and as preparation for infiltrative anesthesia.[A231219] Benzocaine products are indicated for topical anesthesia in a wide variety of conditions including skin irritation,[L32454] oral pain,[L32459] and hemorrhoids.[L32464]', 'Benzocaine diffuses into nerve cells where it binds to sodium channels, preventing the channels from opening, and blocking the influx of sodium ions.[A231129,A6271,A231219] Nerve cells unable to allow sodium into cells cannot depolarize and conduct nerve impulses.[A6271,A231219]', 'Benzocaine is indicated for use as a topical anesthetic.[A231219] It has a duration of action of approximately 10 minutes[A231254] and a wide therapeutic window.[A231219] Patients should be counselled regarding the risks of methemoglobinemia.[A231219,A231234,A231244,A231249]', 'DrugBank', 0, 0, 0);
