INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (12978, 'Dapsone 100mg 20 tab.', 'دابسون 100 مجم 20 قرص', '158', '103', 'Antimalarial', 'Dapsone', 'El nile.', 'Tab', 'أقراص', '100mg', '2', NULL, NULL, '6221077011612', NULL, 2095, '2024-11-30', 'For the treatment and management of leprosy and dermatitis herpetiformis.', 'Dapsone acts against bacteria and protozoa in the same way as sulphonamides, that is by inhibiting the synthesis of dihydrofolic acid through competition with para-amino-benzoate for the active site of dihydropteroate synthetase. The anti-inflammatory action of the drug is unrelated to its antibacterial action and is still not fully understood.', 'Dapsone is a sulfone with anti-inflammatory immunosuppressive properties as well as antibacterial and antibiotic properties. Dapsone is the principal drug in a multidrug regimen recommended by the World Health Organization for the treatment of leprosy. As an anti-infective agent, it is also used for treating malaria and, recently, for Pneumocystic carinii pneumonia in AIDS patients. Dapsone is absorbed rapidly and nearly completely from the gastrointestinal tract. Dapsone is distributed throughout total body water and is present in all tissues. However, it tends to be retained in skin and muscle and especially in the liver and kidney: traces of the drug are present in these organs up to 3 weeks after therapy cessation.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (9410, 'Ospen 1.5 miu 12 f.c.tabs', 'اوسبن 1500 - 12 قرص', '41', NULL, 'Antibiotic', 'Phenoxymethyl penicillin', 'Sandoz', 'Tab', 'أقراص', NULL, '1', 'علاج التهابات الجهاز التنفسي -- التهاب الاذن الوسطي', NULL, '9002260021673', NULL, 2479, '2022-12-07', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2975, 'Concor 5 mg 30 f.c. tabs.', 'كونكور 5مجم 30 قرص', '72', '51', 'Antihypertensive', 'Bisoprolol fumarate', 'Amoun', 'Tab', 'أقراص', '5 mg', '3', 'لعلاج ضغط الدم المرتفع', NULL, '6221025032348', NULL, 25020, '2024-07-07', 'Bisoprolol is indicated for the treatment of mild to moderate hypertension.[L7219] It may be used off-label to treat heart failure, atrial fibrillation, and angina pectoris.[A180460,A180463]', 'Though the mechanism of action of bisoprolol has not been fully elucidated in hypertension, it is thought that therapeutic effects are achieved through the antagonism of β-1adrenoceptors to result in lower cardiac output. Bisoprolol is a competitive, cardioselective β1-adrenergic antagonist. When β1-receptors (located mainly in the heart)[T640] are activated by adrenergic neurotransmitters such as epinephrine, both the blood pressure and heart rate increase, leading to greater cardiovascular work, increasing the demand for oxygen.  Bisoprolol reduces cardiac workload by decreasing contractility and the need for oxygen through competitive inhibition of β1-adrenergic receptors.[A180502,T640]



Bisoprolol is also thought to reduce the output of renin in the kidneys, which normally increases blood pressure. Additionally, some central nervous system effects of bisoprolol may include diminishing sympathetic nervous system output from the brain, decreasing blood pressure and heart rate.[L7219]', 'Bisoprolol decreases heart rate (chronotropy), decreases contractility (inotropy), and reduces blood pressure.[T640,L7219] The results of various clinical studies indicate that bisoprolol reduces cardiovascular mortality and all-cause mortality in patients with heart failure and decreased cardiac ejection fraction (EF).[A180466,A180505] ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19051, 'Finshape 120mg 30 caps.', 'فينشيب 120مجم 30 كبسولة', '240', '216', 'Weight loss', 'Orlistat', 'Mash premiere', 'Cap', 'كبسولة', '120mg', '3', NULL, 'about orlistat anti-obese drug. saturated derivative of lipstatin. mechanism of action of orlistat its primary function is preventing the absorption of fats from the human diet thereby reducing caloric intake.orlistat works by inhibiting gastric and pancr', '6222001402209', NULL, 1836, '2025-12-12', 'Orlistat is indicated for obesity management including weight loss and weight maintenance when used in combination with calorie reduction in overweight and obese adults; this indication applies to both the prescription formulation of 120 mg[L11130] and the over-the-counter formulation of 60 mg.[L31963] Orlistat in the 120 mg prescription formulation is also indicated to reduce the risk of weight regain following weight loss.[L11130]', 'Orlistat is a potent and selective inhibitor of various lipase enzymes responsible for the metabolism of fat. It acts in the gastrointestinal (GI) tract via covalent binding to the serine residues located on the active site of both gastric and pancreatic lipase. When orlistat is taken with food containing fat, it partially inhibits the hydrolysis of triglycerides. This decreases absorption of monoaclglycerides and free fatty acids, contributing to weight maintenance and weight loss.[A37653,L11130]', 'Orlistat helps with weight reduction and maintenance by inhibiting the absorption of dietary fats via the inhibition of lipase enzymes.[A1734,A1735]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25377, 'Pross hair lotion 120 ml', 'بروس لوشن للشعر 120مل', '175', '135', 'Hair care', 'Saw palmetto+caffeine+ginkgo+ginseng+royal jelly+aloe+rosemary+chamomil+clove+jojoba+lavender+panthe', 'Hi-care > roventis pharma', 'Lotion', 'لوشن', '120 ml', '1', NULL, '**indications ï-treatment of hair loss. ï- effective and innovative natural treatment of androgenetic alopecia. ï-maintains healthy hair growth and density. ï-damaged brittle and fragile hair. ï-split ends. ï- weak hair growth rates. ** directions for use', NULL, NULL, 1459, '2025-03-03', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22895, 'Mograflox 400mg 5 f.c. tabs.', 'موجرافلوكس 400مجم 5 اقراص', '45', NULL, 'Antibiotic.quinolone', 'Moxifloxacin', 'Organopharma > modern office for import', 'Tab', 'أقراص', '400mg', '1', NULL, 'about moxifloxacin a third generation synthetic fluoroquinolone antibiotic a broad spectrum antibiotic. mechanism of action of moxifloxacin moxifloxacin acts by inhibiting the enzyme dna gyrase (topoisomerase 2) and topoisomerase 4.dna gyrase helps in the', NULL, NULL, 716, '2022-07-24', 'For the treatment of sinus and lung infections such as sinusitis, pneumonia, and secondary infections in chronic bronchitis. Also for the treatment of bacterial conjunctivitis (pinkeye).', 'The bactericidal action of moxifloxacin results from inhibition of the enzymes topoisomerase II (DNA gyrase) and topoisomerase IV. DNA gyrase is an essential enzyme that is involved in the replication, transcription and repair of bacterial DNA. Topoisomerase IV is an enzyme known to play a key role in the partitioning of the chromosomal DNA during bacterial cell division.', 'Moxifloxacin is a quinolone/fluoroquinolone antibiotic. Moxifloxacin can be used to treat infections caused by the following bacteria: Aerobic Gram-positive microorganisms: _Corynebacterium_ species, _Micrococcus luteus_, _Staphylococcus aureus_, _Staphylococcus epidermidis_, _Staphylococcus haemolyticus_, _Staphylococcus hominis_, _Staphylococcus warneri_, _Streptococcus pneumoniae_, and _Streptococcus viridans_ group. Aerobic Gram-negative microorganisms: _Acinetobacter lwoffii_, _Haemophilus influenzae_, and _Haemophilus parainfluenzae_. Other microorganisms: _Chlamydia trachomatis_. Moxifloxacin is bactericidal and its mode of action depends on blocking of bacterial DNA replication by binding itself to an enzyme called DNA gyrase, which allows the untwisting required to replicate one DNA double helix into two. Notably the drug has 100 times higher affinity for bacterial DNA gyrase than for mammalian. Moxifloxacin is a broad-spectrum antibiotic that is active against both Gram-positive and Gram-negative bacteria.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (3100, 'Caudal hair lotion 120 ml', 'كودال لوشن للشعر 120 مل', '99', '79', 'Hair care', NULL, 'Leader cosmetics > farabi', 'Lotion', 'لوشن', '120 ml', '1', NULL, NULL, NULL, NULL, 751, '2025-07-18', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4890, 'Flacort 6mg 20 tabs.', 'فلاكورت 6مجم 20 قرص', '54', '38', 'Corticosteroid', 'Deflazacort', 'Egpi', 'Tab', 'أقراص', '6mg', '2', 'تقليل الالتهابات والحساسية -- تقليل الالام', NULL, '6223003930707', NULL, 6415, '2024-07-23', 'Deflazacort is indicated for the treatment of Duchenne Muscular Dystrophy (DMD) in patients 2 years of age and older.[FDA label]', 'Deflazacort is a corticosteroid prodrug with an active metabolite, 21-deflazacort, which binds to the glucocorticoid receptor to exert anti-inflammatory and immunosuppressive effects on the body.[A18725,A179461,A179464] The exact mechanism by which deflazacort exerts its therapeutic effects in patients with DMD is unknown but likely occurs via its anti-inflammatory activities.[FDA label] ', 'Deflazacort exerts anti-inflammatory activity in DMD, likely improving various symptoms, including muscle weakness and cardiorespiratory symptoms in addition to delaying their onset.[A179455] This allows for an increased quality of life and prevents the necessity for surgical procedures, such as those for scoliosis, which is associated with DMD. Studies showed significant preservation of muscle mass in patients generally treated with 0.9 mg/kg/day of deflazacort compared to a control group. The following findings are based on clinical studies using deflazacort on a long term basis[A179455,A179458]:



**Effects on muscle strength** 



At age 16, individuals treated with long-term deflazacort had 63 ± 4% score in muscle strength compared to a mean muscle strength score of 31 ± 3% for control patients[A179455]. Significant improvements in climbing stairs and rising from a supine position were also seen in patients taking deflazacort.[A179455] 



**Effects on ambulation**



Ambulation was significantly higher by 12 years of age and 18 years of age in patients taking deflazacort when compared with the control group. The control group showed a mean loss of ambulation of 2 years sooner than with deflazacort treatment.[A179458]  



**Effects on cardiac function**



Mean left ventricular ejection fraction (a measure of cardiac function) was higher in patients treated with deflazacort over the long term. Preservation of cardiac function was demonstrated by a mean difference in ejection fraction of about 7%, favoring study groups taking deflazacort over control groups.[A179458]



**Effects on spinal alignment**



Children treated with deflazacort also significantly lowered the rate and severity of scoliosis and eliminated the need for scoliosis surgery after long-term treatment.[A179455,A179458]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (3326, 'Dapsone 50mg 20 tab.', 'دابسون 50 مجم 20 قرص', '103', '50', 'Antimalarial', 'Dapsone', 'El nile.', 'Tab', 'أقراص', '50mg', '2', NULL, 'about dapsone a synthetic sulfone(4 4`-diaminodiphenylsulfone (dds)) antileprotic antimalarial antibacterial. mechanism of action of dapsone dapsone is an antileprotic sulfone chemically related to sulfonamides & has same mechanism of action. it exerts it', '6221077010813', NULL, 2405, '2024-11-30', 'For the treatment and management of leprosy and dermatitis herpetiformis.', 'Dapsone acts against bacteria and protozoa in the same way as sulphonamides, that is by inhibiting the synthesis of dihydrofolic acid through competition with para-amino-benzoate for the active site of dihydropteroate synthetase. The anti-inflammatory action of the drug is unrelated to its antibacterial action and is still not fully understood.', 'Dapsone is a sulfone with anti-inflammatory immunosuppressive properties as well as antibacterial and antibiotic properties. Dapsone is the principal drug in a multidrug regimen recommended by the World Health Organization for the treatment of leprosy. As an anti-infective agent, it is also used for treating malaria and, recently, for Pneumocystic carinii pneumonia in AIDS patients. Dapsone is absorbed rapidly and nearly completely from the gastrointestinal tract. Dapsone is distributed throughout total body water and is present in all tissues. However, it tends to be retained in skin and muscle and especially in the liver and kidney: traces of the drug are present in these organs up to 3 weeks after therapy cessation.', 'DrugBank', 0, 0, 0);
