INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4146, 'Isotretam 10 mg 20 s.g. caps.', 'ايزوتريتام 10مجم 20 كبسولة', '115', NULL, 'Anti-acne', 'Isotretinoin', 'Medizen', 'Cap', 'كبسولة', '10 mg', '2', NULL, NULL, '6222012401888', NULL, 1018, '2025-06-13', 'Isotretinoin is indicated to treat severe recalcitrant nodular acne and patients ≥12 years enrolled in the iPLEDGE program.[Label,L6579]', 'Isotretinoin produces its effects through altering progress through the cell cycle, cell differentiation, survival, and apoptosis.[A179091] These actions reduce sebum production, preventing the blockage of pores, and growth of acne causing bacteria.[A179091] Isotretinoin and 4-oxo-isotretinoin both significantly reduce the production of sebum.[A179091,Label] Isotretinoin has little to no affinity for retinol binding proteins (RBPs) and retinoic acid nuclear receptors (RARs).[A179091] Tretinoin and 4-oxo-tretinion bind to the RAR-γ receptor, which is suspected to be part of the action of acne treatment by isotretinoin.[A179091] Isotretinoin induces apoptosis in sebocytes, leading to a decrease in sebum production.[A179091] Isotretinoin also reduces the formation of comedones by reducing hyperkeratinization through an unknown mechanism.[A179091] Isotretinoin does not directly kill bacteria but it does reduce the size of sebum ducts and makes the microenvironment less hospitable to acne causing bacteria.[A179091] It may also increase immune mechanisms and alter chemotaxis of monocytes to reduce inflammation.[A179091]



There is preliminary evidence suggesting isotretinoin may interact with FoxO1, which may explain a substantial number of isotretinoin''s unexplained actions.[A179122]', 'The pharmacodynamics of isotretinoin are poorly understood.[Label]', 'DrugBank', 0, 0, 0);
