INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22132, 'Lutchi hair shampoo 200 ml', 'لوتشي شامبو للشعر 200 مل', '195', '118', 'Hair care', 'Selenium sulfide+zinc pyrithione+climbazol+tea tree oil+nigella sativa+rosemary+biotin+aloe vera+vitamin e+argan oil', 'Egyptian co. for cosmetics > derma sun', 'Amp', 'أمبول', '200 ml', '1', 'للإهتمام والعناية بالشعر', NULL, NULL, NULL, 2128, '2025-02-24', 'For treatment of tinea versicolor, tinea capitis, dandruff and seborrheic dermatitis of the scalp.', 'Topical selenium sulfide may act by an antimitotic action, resulting in a reduction in the turnover of epidermal cells. It also has local irritant, antibacterial, and mild antifungal activity, which may contribute to its effectiveness. An antimitotic mechanism of action is suggested by data showing that selenium sulfide decreases the rate of incorporation of radioactively labeled thymidine into the DNA of dermal epithelial cells. The following organisms are generally considered susceptible to selenium sulfide in vitro: <i>Malassezia furfur</i>, <i>Microsporum sp.</i> including <i>Microsporum audouinii</i> and <i>Microsporum canis</i>, <i>Pityrosporon sp.</i>, <i>Trichophyton sp.</i> including <i>Trichophyton schoenleinii</i> and <i>Trichophyton tonsurans</i>. Selenium sulfide has been shown to be sporicidal to <i>T. tonsurans</i>, the most common etiologic agent of tinea capitis. One in-vitro study demonstrated that 2.5% selenium sulfide was equivalent in sporicidal activity to both 1% and 2% zinc pyrithione.', 'Selenium sulfide is an antifungal agent often used in shampoos for the treatment of dandruff and seborrheic dermatitis. Selenium sulfide is highly active in inhibiting the growth of <i>P. ovale</i>. It is also a proven cytostatic agent, slowing the growth of both hyperproliferative and normal cells in dandruff and seborrheic dermatitis. A 0.6% micronized form of selenium sulfide is also safe and effective for dandruff.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17413, 'Dexaflabu 400mg 20 f.c. tab.', 'ديكسافلابو 400 مجم 20 قرص', '22', NULL, 'Nsaid.propionic acid derivatives', 'Dexibuprofen', 'Delta pharma', 'Tab', 'أقراص', '400mg', '2', NULL, 'a type of medicine called a non-steroidal anti-inflammatory drug (nsaid). nsaids are used to relieve pain and inflammation. what is it used for? osteoarthritis. pain in the muscles bones or joints. period pain (dysmenorrhea). toothache. warning! this medi', NULL, NULL, 950, '2022-07-09', 'For more information, refer to [ibuprofen].', 'Like common NSAIDs, dexibuprofen is an active enantiomer of [ibuprofen] that suppresses the prostanoid synthesis in the inflammatory cells via inhibition of the COX-2 isoform of the arachidonic acid COX.[T28] For more information, refer to [ibuprofen].', 'For more information, refer to [ibuprofen].', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17964, 'Durabiotic 500mg 8 caps.', 'دورابيوتيك 500 مجم 8 كبسولات', '9', NULL, 'Antibiotic.cephalosporin.first-generation', 'Cefadroxil', 'Logestic pharma for pharmaceuticals', 'Cap', 'كبسولة', '500mg', '1', NULL, 'about cefadroxil first generation cephalosporin para-hydroxy derivative of cefalexin oral broad-spectrum antibiotic mechanism of action cefadroxil is a 1st generation cephalosporin which exerts its bactericidal action against both gram positive & gram neg', NULL, NULL, 708, '2022-07-09', 'For the treatment of the following infections (skin, UTI, ENT) caused by; <i>S. pneumoniae, H. influenzae, staphylococci, S. pyogenes</i> (group A beta-hemolytic streptococci), <i>E. coli, P. mirabilis, Klebsiella</i> sp, coagulase-negative staphylococci and <i>Streptococcus pyogenes</i>', 'Like all beta-lactam antibiotics, cefadroxil binds to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, causing the inhibition of the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins; it is possible that cefadroxil interferes with an autolysin inhibitor.', 'Cefadroxil, a first-generation cephalosporin antibiotic, is used to treat urinary tract infections, skin and skin structure infections, pharyngitis, and tonsillitis.', 'DrugBank', 0, 0, 0);
