INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23739, 'Normonat 30 mg 7 tabs.', 'نورمونات 30مجم 7 اقراص', '800', NULL, 'Arginine vasopressin receptor 2 antagonist . hyponatremia associated with congestive heart failure, cirrhosis, and the syndrome of inappropriate antidiuretic hormone', 'Tolvaptan', 'Marcyrl co.', 'Tab', 'أقراص', '30 mg', '1', '-- حالات امراض القلب', 'tolvaptan is used to treat low blood sodium levels (hyponatremia) associated with various conditions like congestive heart failure cirrhosis and syndrome of inappropriate antidiuretic hormones (siadh). tolvaptan is a selective and competitive arginine vas', NULL, NULL, 2348, '2023-04-08', 'Treatment of symptomatic and resistant to fluid restriction euvolemic or hypervolemic hyponatremia associated with congestive heart failure, SIADH, and cirrhosis. ', 'Tolvaptan is a selective and competitive arginine vasopressin receptor 2 antagonist. Vasopressin acts on the V2 receptors found in the walls of the vasculature and luminal membranes of renal collecting ducts. By blocking V2 receptors in the renal collecting ducts, aquaporins do not insert themselves into the walls thus preventing water absorption. This action ultimately results in an increase in urine volume, decrease urine osmolality, and increase electrolyte-free water clearance to reduce intravascular volume and an increase serum sodium levels. Tolvaptan is especially useful for heart failure patients as they have higher serum levels of vasopressin. ', 'Urine volume and fluid intake increase in a dose dependent manner which results in overall negative fluid balance in patients taking tolvaptan. Increases in serum sodium and osmolality can be observed 4-8 hours post-administration and is maintained for 24 hours. The magnitude of serum sodium and osmolality change increases with escalating doses. Furthermore, a decrease in urine osmolality and increase in free water clearance can be observed 4 hours after post-administration of tolvaptan. The affinity for V2 receptors is 29x greater than that of V1a receptors and does not have any appreciable affinity for V2 receptors. ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24996, 'Plavicard 75mg 30 f.c. tab', 'بلافيكارد 75مجم 30 قرص', '117', '85.5', 'Antiplatelet.adp receptor blocker', 'Clopidogrel', 'Jedco int. co. for pharmaceuticals > four a pharma', 'Tab', 'أقراص', '75mg', '3', 'مضاد للتجلط -- الحفاظ علي صحة القلب -- علاج الجلطات التي تتكون في الجسم -- الحفاظ علي سيولة الدورة الدموية', NULL, '6224007326596', NULL, 3820, '2024-11-27', 'Clopidogrel is indicated to reduce the risk of myocardial infarction for patients with non-ST elevated acute coronary syndrome (ACS), patients with ST-elevated myocardial infarction, and in recent MI, stroke, or established peripheral arterial disease,[L7213]', 'Clopidogrel is activated via a 2 steps reaction to an active thiol-containing metabolite.[A180508] This active form is a platelet inhibitor that irreversibly binds to P2Y<sub>12</sub> ADP receptors on platelets.[L7213] This binding prevents ADP binding to P2Y<sub>12</sub> receptors, activation of the glycoprotein GPIIb/IIIa complex, and platelet aggregation.[L7213]', 'Clopidogrel is a prodrug of a platelet inhibitor used to reduce the risk of myocardial infarction and stroke.[A180508,L7213] It has a long duration of action as it is taken once daily and a large therapeutic window as it is given in doses of 75-300mg daily.[L7213]', 'DrugBank', 0, 0, 0);
