INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13650, 'Alecensa 150 mg 224 caps.', 'اليسنزا 150مجم 224 كبسولة', '110,000', '80000', '.', 'Alectinib', 'F.hoffman la roche', 'Cap', 'كبسولة', '150 mg', '4', 'علاج للسرطان.', 'alecensa is indicated for the treatment of patients with anaplastic lymphoma kinase (alk)-positive metastatic non-small cell lung cancer (nsclc) as detected by an fda-approved test.', NULL, NULL, 2573, '2024-01-31', 0, 'Alectinib is indicated for the treatment of adult patients with anaplastic lymphoma kinase (ALK)-positive metastatic non-small cell lung cancer (NSCLC).[L51008] It is also indicated as an adjuvant treatment in adult patients following tumor resection of ALK-positive non-small cell lung cancer.[L51008]', 'Alectinib is a second generation oral drug that selectively inhibits the activity of anaplastic lymphoma kinase (ALK) tyrosine kinase. It is specifically used in the treatment of non-small cell lung cancer (NSCLC) expressing the ALK-EML4 (echinoderm microtubule-associated protein-like 4) fusion protein that causes proliferation of NSCLC cells. Inhibition of ALK prevents phosphorylation and subsequent downstream activation of STAT3 and AKT resulting in reduced tumour cell viability. Both alectinib and its major active metabolite M4 demonstrate similar in vivo and in vitro activity against multiple mutant forms of ALK. ', NULL, 'DrugBank', 0, 0, 0);
