INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13802, 'Almitat 0.05% cream 15 gm', 'الميتات 0.05% كريم 15 جم', '6', NULL, 'Glucocorticoid', 'Alclometasone', 'Uniswab > roxcel egypt', 'Cream', 'كريم', '0.05%', '1', NULL, NULL, NULL, NULL, 601, '2022-07-21', 0, 'For the relief of the inflammatory and pruritic manifestations of corticosteroid-responsive dermatoses.', 'The mechanism of the anti-inflammatory activity of the topical steroids, in general, is unclear. However, corticosteroids are thought to act by the induction of phospholipase A<sub>2</sub> inhibitory proteins, collectively called lipocortins. It is postulated that these proteins control the biosynthesis of potent mediators of inflammation such as prostaglandins and leukotrienes by inhibiting the release of their common precursor, arachidonic acid. Arachidonic acid is released from membrane phospholipids by phospholipase A<sub>2</sub>. Alclometasone initially binds the corticosteroid receptor. This complex migrates to the nucleus where it binds to different glucocorticoid response elements on the DNA. This in turn enhances and represses various genes, especially those involved in inflammatory pathways.', 'Alclometasone is a synthetic corticosteroid for topical dermatologic use. The corticosteroids constitute a class of primarily synthetic steroids used topically as anti-inflammatory and antipruritic agents. Alclometasone is a selective glucocorticoid receptor agonist.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13804, 'Almotrip forte 12.5mg 2 f.c. tabs.', 'الموتريب فورت 12.5 مجم 2 قرص', '22', NULL, 'Antimigraine', 'Almotriptan', 'European egyptian pharm. ind.', 'Tab', 'أقراص', '12.5mg', '1', NULL, 'indication for the treatment of acute migraine headache in adults pharmacodynamics almotriptan is a selective 5-hydroxytryptamine receptor subtype agonist indicated for the acute treatment of migraine attacks with or without aura in adults. almotriptan is', NULL, NULL, 951, '2022-07-09', 0, 'For the treatment of acute migraine headache in adults', 'Almotriptan binds with high affinity to human 5-HT<sub>1B</sub> and 5-HT<sub>1D</sub> receptors leading to cranial blood vessel constriction.', 'Almotriptan is a selective 5-hydroxytryptamine receptor subtype agonist indicated for the acute treatment of migraine attacks with or without aura in adults. Almotriptan is not intended for the prophylactic therapy of migraine or for use in the management of hemiplegic or basilar migraine. Almotriptan is an agonist for a vascular 5-hydroxytryptamine receptor subtype (probably a member of the 5-HT<sub>1D</sub> family) having only a weak affinity for 5-HT<sub>1A</sub>, 5-HT<sub>5A</sub>, and 5-HT<sub>7</sub> receptors and no significant affinity or pharmacological activity at 5-HT<sub>2</sub>, 5-HT<sub>3</sub> or 5-HT<sub>4</sub> receptor subtypes or at alpha1-, alpha2-, or beta-adrenergic, dopamine1,; dopamine2; muscarinic, or benzodiazepine receptors. This action in humans correlates with the relief of migraine headache. In addition to causing vasoconstriction, experimental data from animal studies show that Almotriptan also activates 5-HT<sub>1</sub> receptors on peripheral terminals of the trigeminal nerve innervating cranial blood vessels, which may also contribute to the antimigrainous effect of Almotriptan in humans.', 'DrugBank', 0, 0, 0);
