INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13860, 'Alumag 20 tab.', 'الوماج 20 قرص', '0', NULL, 'Antacid', 'Aluminium hydroxide+magnesium trisilicate+peppermint oil', 'Misr', 'Tab', 'أقراص', NULL, '2', NULL, NULL, NULL, NULL, 1018, '2022-07-20', 0, NULL, 'Aluminum Acetate is an astringent.  An astrignent is a chemical that tends to shrink or constrict body tissues, usually locally after topical medicinal application.  The shrinkage or constriction is through osmotic flow of water (or other fluids) away from the area where the astringent was applied.  Astringent medicines cause shrinkage of mucous membranes or exposed tissues and are often used internally to check discharge of blood serum or mucous secretions. This can happen with a sore throat, hemorrhages, diarrhea, or with peptic ulcers. Externally applied astringents, which cause mild coagulation of skin proteins, dry, harden, and protect the skin. Acne sufferers are often advised to use astringents if they have oily skin. Astringents also help heal stretch marks and other scars. Mild astringent solutions are used in the relief of such minor skin irritations as those resulting from superficial cuts, allergies, insect bites, or fungal infections such as athlete''s foot. ', NULL, 'DrugBank', 0, 0, 0);
