INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14162, 'Anginitra 5 mg 10 sublingual tab.', 'انجينيترا 5 مجم 10 اقراص', '1', NULL, 'Anti-ischemic', 'Isosorbide dinitrate', 'Chemipharm', 'Tab', 'أقراص', '5 mg', '1', NULL, 'about isosorbide dinitrate alkyl nitrate derivative a direct vasodilator antianginal. mechanism of action of isosorbide dinitrate it is a direct smooth muscle relaxant which have a prominent action vascular smooth muscles preferentially veins than arterie', NULL, NULL, 1103, '2022-07-15', 0, 'For the prevention of angina pectoris due to coronary artery disease.', 'Isosorbide dinitrate is converted to the active nitric oxide to activate guanylate cyclase. This activation increases levels of cyclic guanosine 3'',5''-monophosphate (cGMP). cGMP activates protein kinases and causes a series of phosphorylation reactions which leads to dephosphorylation of myosin light chains of smooth muscle fibres. Finally there is a release of calcium ions which causes smooth muscle relaxation and vasodilation.', 'Isosorbide Dinitrate is a moderate to long acting oral organic nitrate used for the relief and prophylactic management of angina pectoris. It relaxes the vascular smooth muscle and consequent dilatation of peripheral arteries and veins, especially the latter. Dilatation of the veins promotes peripheral pooling of blood and decreases venous return to the heart, thereby reducing left ventricular end- diastolic pressure and pulmonary capillary wedge pressure (preload). Arteriolar relaxation reduces systemic vascular resistance, systolic arterial pressure, and mean arterial pressure.', 'DrugBank', 0, 0, 0);
