INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14203, 'Anoxicam 20mg 20 tab.', 'انوكسيكام 20 مجم 20 قرص', '39', '28.8', 'Nsaid.oxicam', 'Tenoxicam', 'Marcyrl co.', 'Tab', 'أقراص', '20mg', '2', 'مسكن عام -- مضاد للالتهابات', 'mechanism of action of tenoxicam the anti-inflammatory effects of tenoxicam may result from the inhibition of the enzyme cyclooxygenase and the subsequent peripheral inhibition of prostaglandin synthesis (prostaglandins sensitize pain receptors their inhi', '6223003570637', NULL, 1697, '2023-09-12', 0, 'For the treatment of rheumatoid arthritis, osteoarthritis, backache, and pain.', 'The antiinflammatory effects of tenoxicam may result from the inhibition of the enzyme cycooxygenase and the subsequent peripheral inhibition of prostaglandin synthesis. As prostaglandins sensitize pain receptors, their inhibition accounts for the peripheral analgesic effects of tenoxicam. Antipyresis may occur by central action on the hypothalamus, resulting in peripheral dilation, increased cutaneous blood flow, and subsequent heat loss.', 'Tenoxicam, an antiinflammatory agent with analgesic and antipyretic properties, is used to treat osteoarthritis and control acute pain.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14204, 'Anschlarin syrup 120 ml', 'انشلارين شراب 120 مل', '11', NULL, 'Anemia.iron supplement', 'Iron', 'Averroes pharma-egypt', 'Syrup', 'شراب', '120 ml', '1', 'مكمل غذائي غني بالحديد لعلاج الأنيميا.', NULL, '6224008179146', NULL, 1554, '2022-12-02', 0, 'For treatment of patients with documented iron deficiency in whom oral administration is unsatisfactory or impossible. Also used to replenish body iron stores in Non-Dialysis Dependent-Chronic Kidney Disease (NDD-CKD) patients receiving or not receiving erythropoietin and in Hemodialysis Dependent (HDD-CKD) and Peritoneal Dialysis Dependent (PDD-CKD) - Chronic Kidney Disease patients receiving an erythropoietin.', 'After iron dextran is injected, the circulating iron dextran is removed from the plasma by cells of the reticuloendothelial system, which split the complex into its components of iron and dextran. The iron is immediately bound to the available protein moieties to form hemosiderin or ferritin, the physiological forms of iron, or to a lesser extent to transferrin. This iron which is subject to physiological control replenishes hemoglobin and depleted iron stores.', 'Iron dextran is a dark brown, slightly viscous sterile liquid complex of ferric hydroxide and dextran for intravenous or intramuscular use. It is for treatment of patients with documented iron deficiency in whom oral administration is unsatisfactory or impossible. Iron is essential to the formation of hemoglobin and to the function and formation of other heme and nonheme compounds. Untreated depletion of iron stores leads to iron-deficient erythropoiesis and, in turn, to iron deficiency anemia.', 'DrugBank', 0, 0, 0);
