INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (23384, 'Neomycin powder 12 gm', 'نيومايسين باودر 12جم', '5', NULL, 'Antibiotic topical', 'Neomycin', 'Green pharm', 'Powder', 'بودرة', '12 gm', '1', NULL, 'about neomycin an aminoglycoside antibiotic ammonium detoxicant. mechanism of action of neomycin this aminoglycoside cause protein synthesis inhibition by irreversibly bind to specific 30s-subunit proteins and 16s rrna. specifically neomycin binds to four', NULL, NULL, 961, '2023-04-08', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Neomycin is a broad-spectrum aminoglycoside antibiotic drug that is derived from the metabolic products of _Streptomyces fradiae_.[L11979] Neomycin is a complex comprised of three components, neomycin A, B, and C.[A191529] Neomycin B, also known as [framycetin], is the most active component of the complex and neomycin C is the isomer of neomycin B, making these two stereoisomers the active components of neomycin.[A175042,A191529] Neomycin A, or [neamine], is a moiety that conjoins two molecules of neomycin B and C together.[A175042] Neomycin is active against both gram-positive and gram-negative organisms and mediates its pharmacological action by binding to bacterial ribosomes and inhibiting protein synthesis, which is crucial for the survival of bacteria.[L11985] 

Neomycin sulfate is the most common form for pharmaceutical preparations; because the compound is a complex, the amount of neomycin in products is measured in units.[A191529] Neomycin sulfate as monotherapy is available in an oral solution for adjunct use in the treatment of hepatic coma.[L11979] It is also used in combination with [polymyxin B] sulfates and [hydrocortisone] in otic suspensions for use in the treatment of bacterial infections in the external auditory canal, including infections caused by medical procedures in the ear.[L10532] Neomycin is also used in combination with [polymyxin B] sulfates and [dexamethasone] in ophthalmic preparations for use in the treatment of inflammatory conditions and infections in the eye.[L10716] Neomycin is also available in over-the-counter topical products to prevent minor skin infections.', '["A07AA51", "R02AB01", "J01GB05", "D06AX04", "S03AA01", "A01AB08", "A07AA01", "S02AA07", "B05CA09", "S01AA03"]', '[]');
