INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14427, 'Gertadino syrup 120 ml', 'جيرتادينو شراب 120 مل', '15', NULL, 'Antihistamine.antiallergy', 'Desloratadine', 'Aig pharma', 'Syrup', 'شراب', '120 ml', '1', '- التهاب الانف التحسسى', NULL, NULL, NULL, 901, '2022-09-07', 0, 'For the relief of symptoms of seasonal allergic rhinitis, perennial (non-seasonal) allergic rhinitis. Desloratidine is also used for the sympomatic treatment of pruritus and urticaria (hives) associated with chronic idiopathic urticaria.', 'Like other H1-blockers, Desloratadine competes with free histamine for binding at H<sub>1</sub>-receptors in the GI tract, uterus, large blood vessels, and bronchial smooth muscle. This blocks the action of endogenous histamine, which subsequently leads to temporary relief of the negative symptoms (eg. nasal congestion, watery eyes) brought on by histamine.', 'Desloratadine is a long-acting second-generation H<sub>1</sub>-receptor antagonist which has a selective and peripheral H1-antagonist action. Histamine is a chemical that causes many of the signs that are part of allergic reactions, such as the swelling of tissues. Histamine is released from histamine-storing cells (mast cells) and attaches to other cells that have receptors for histamine. The attachment of the histamine to the receptors causes the cell to be "activated," releasing other chemicals which produce the effects that we associate with allergies. Desloratadine blocks one type of receptor for histamine (the H1 receptor) and thus prevents activation of cells by histamine. Unlike most other antihistamines, Desloratadine does not enter the brain from the blood and, therefore, does not cause drowsiness.', 'DrugBank', 0, 0, 0);
