INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Zadsilla gargle 60 ml', 29817, 'زادسيلا مضمضة 60مل', '4', NULL, 'Oral care', 'Oral care', NULL, 'Aluminium chloride+calcium hydroxide+menthol', 'Kahira > zad', 'Gargle', 'غرغرة', '60 ml', '1', NULL, NULL, NULL, NULL, 741, '2022-07-13', NULL, 0, NULL, 'Aluminum Acetate is an astringent.  An astrignent is a chemical that tends to shrink or constrict body tissues, usually locally after topical medicinal application.  The shrinkage or constriction is through osmotic flow of water (or other fluids) away from the area where the astringent was applied.  Astringent medicines cause shrinkage of mucous membranes or exposed tissues and are often used internally to check discharge of blood serum or mucous secretions. This can happen with a sore throat, hemorrhages, diarrhea, or with peptic ulcers. Externally applied astringents, which cause mild coagulation of skin proteins, dry, harden, and protect the skin. Acne sufferers are often advised to use astringents if they have oily skin. Astringents also help heal stretch marks and other scars. Mild astringent solutions are used in the relief of such minor skin irritations as those resulting from superficial cuts, allergies, insect bites, or fungal infections such as athlete''s foot. ', NULL, 'DrugBank');
