INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Luna emollient cream 20 gm', 1701, 'لونا كريم مرطب للبشرة 20 جم', '35', NULL, 'Emollinent', 'Emollinent', NULL, 'Glycerin', 'Luna', 'Cream', 'كريم', '20 gm', '1', '1- لونا كريم مرطب لعلاج جفاف وتشققات اليدين', NULL, NULL, '6221039302116', 1003, '2022-08-05', NULL, 0, 'It is used as a solvent, emollient, pharmaceutical agent, and sweetening agent.', 'When administered rectally, glycerin exerts a hygroscopic and/or local irritant action, drawing water from the tissues into the feces and reflexively stimulating evacuation. 

Glycerin decreases intraocular pressure by creating an osmotic gradient between the blood and intraocular fluid, causing fluid to move out of the aqueous and vitreous humors into the bloodstream.', 'Glycerin is commonly classified as an osmotic laxative but may act additionally or alternatively through its local irritant effects; it may also have lubricating and fecal softening actions. Glycerin suppositories usually work within 15 to 30 minutes.', 'DrugBank');
