INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Germovid powder 450 gm', 19754, 'جيرموفيد بودرة 450 جم', '60', NULL, 'Vitamin e', 'Vitamin e', NULL, 'Natural wheat germ oil', 'Nilco > lavida group', 'Powder', 'بودرة', '450 gm', '1', NULL, NULL, '-suitable for all family members -use: 1-2 tablespoonful to be mixed with milk youghurt juice green salad fruit salad and bakeries.', NULL, 900, '2022-07-24', NULL, 0, NULL, 'The predominant targets of NK cells are ‘stressed’ cells with downregulated expression of class I MHC (MHC-I), a common mechanism used by malignant or virus-infected cells to evade recognition by the body’s immune system. In response to encountering these cells, NK cells release two types of proteins, perforins and granzyme B, which attack the target cell’s membrane, damage its organelles, and trigger apoptosis. NK Cell immunotherapy is an emerging anti-cancer strategy, as these cells are able to exert effects without activating the graft-versus-host disease cascade. In addition, their broad mechanism allows for generally safe administration of NK cells that are not immunologically matched to a patient. ', NULL, 'DrugBank');
