INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Sustanon 100mg amp', 11072, 'سوستانون 100مجم امبول', '3', NULL, 'Androgen', 'Androgen', NULL, 'Testosterone', 'Organon', 'Amp', 'أمبول', '100mg', '1', NULL, NULL, NULL, NULL, 972, '2023-05-19', NULL, 0, 'Testosterone is indicated to treat primary hypogonadism and hypogonadotropic hypogonadism.[L8983,L8935,L8938,L8986,L8989,L8992,L8995]', 'The androgen receptor exists in the cytoplasm bound to the heat shock proteins HSP90, HSP70, and other chaperones.[A187114] After binding to an androgen, the androgen receptor dissociates from HSP90 and undergoes a conformational change to slow the rate of dissociation from the androgen receptor.[A187114] The androgen-receptor complex is transported into the nucleus where it binds to DNA and recruits other transcriptional regulators to form a pre-initiation complex and eventually induce expression of specific genes.[A187114]



Testosterone and its active metabolite dihydrotestosterone (DHT) antagonize the androgen receptor to develop masculine sex organs including the prostate, seminal vesicles, penis, and scrotum.[A187111,L8983,L8935,L8938,L8986,L8989,L8992,L8995]



Antagonism of the androgen receptor is also responsible for the development of secondary sexual characteristics including facial and body hair, enlargement of the larynx, thickening of the vocal cords, and changes in muscle and fat distribution.[A187111,L8983,L8935,L8938,L8986,L8989,L8992,L8995]', 'Testosterone antagonizes the androgen receptor to induce gene expression that causes the growth and development of masculine sex organs and secondary sexual characteristics.[A187114,L8983,L8935,L8938,L8986,L8989,L8992,L8995] The duration of action of testosterone is variable from patient to patient with a half life of 10-100 minutes.[L8983,L8935,L8938,L8986,L8989,L8992,L8995] The therapeutic index is wide considering the normal testosterone levels in an adult man range from 300-1000ng/dL.[L8983,L8938,L8986,L8989,L8992,L8995] Counsel patients regarding the risk of secondary exposure of testosterone topical products to children.[L8983,L8938,L8986,L8989,L8992]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Flibanorin 100 mg 5 f.c. tabs', 19135, 'فليبانورين 100مجم 5 قرص', '100', NULL, 'Women need', 'Women need', NULL, 'Flibanserin', 'Rameda', 'Tab', 'أقراص', '100 mg', '1', 'لزيادة الرغـــــــ...ـــبة للسيدات', NULL, NULL, NULL, 3978, '2023-02-16', NULL, 0, 'For the treatment of hypoactive sexual desire disorder (HSDD) in premenopausal women. ', 'Flibansetrin has high affinity for serotonin receptors in the brain: it acts as an agonist on 5-HT1A and an antagonist on 5-HT2A. In vivo, flibanserin binds equally to 5-HT1A and 5-HT2A receptors. However, under higher levels of brain 5-HT (i.e., under stress), flibanserin may occupy 5-HT2A receptors in higher proportion than 5-HT(1A) receptors. It may also moderately antagonize D4 (dopamine) receptors and 5-HT2B and 5-HTB2C. Its action on neurotransmitter receptors may contribute to reduction in serotonin levels and increase in dopamine and norepinephrine levels, all of which may play part in reward processing. ', NULL, 'DrugBank');
