INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Synapse co q10 60 tabs.', 9013, 'سينابس كو كيو 10 60 قرص', '1,550', NULL, 'Antioxidant', 'Antioxidant', NULL, 'Coenzyme q-10', 'Synapse pharm', 'Tab', 'أقراص', NULL, '1', NULL, NULL, NULL, '6224002641021', 2217, '2023-01-07', NULL, 0, 'Mesna is a uroprotective agent and is used prophylactically to reduce ifosfamide and cyclophosphamide induced hemorrhagic cystitis.[A18572]', 'A metabolite called acrolein is produced when ifosfamide and cyclophosphamide are metabolized. This metabolite concentrates in the bladder and causes cell death via upregulation of reactive oxygen species (ROS), and activates inducible nitric oxide synthase (iNOS) which leads to production of nitric oxide (NO). Both ROS and NO produce products which are detrimental to lipids, proteins and DNA strands. Furthermore, ROS stimulate gene expression of pro-inflammatory cytokines such as TNF-α AND IL-1β. Acrolein may also lead to ulceration of the bladder urothelium. Mesna protects against cyclophosphamide and ifosfamide induced hemorrhagic cystitis by binding to their toxic metabolites. Mesna is metabolized to dimesna and excreted by the kidneys. Glutathione dihydrogenase acts on the reabsorbed portion and produces free sulfhydryl groups. These free sulfhydryl  groups bind acrolein in the bladder, allowing effective excretion and prevention  of toxic effects.[A18570] In addition, Mesna binds to and detoxifies a urotoxic ifosfamide metabolite called 4-hydroxy-ifosfamide.', 'Mesna binds to and inactivates acrolein there by preventing or reducing bladder problems', 'DrugBank');
